INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL51','Pre-University English: Essentials of Composition','An introduction to the process of writing from drafting to revising to polishing. The course includes practical exercises and writing assignments to help students develop and support their ideas accurately, concisely, and clearly.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL100A','Fiction','An introduction to fiction through the detailed examination of a range of novels and/or short stories.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL100B','Poetry','An introduction to poetry through a detailed examination of a range of poetic texts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL100C','Drama','An introduction to dramatic literature through the detailed examination of a range of dramatic texts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL101A','Introduction to Literary Studies','An introduction to the study of literature, covering such areas of enquiry as literary history, genre, criticism, analysis, and theory.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL101B','Introduction to Rhetorical Studies','An introduction to the study and practice of persuasion, including the history and theory of rhetoric, the structures and strategies of arguments, and the analysis of texts and artifacts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL102A','The Major Forms of Literature: Short Stories and Drama','A study of short stories and drama to determine how the shape of a literary work contributes to its meaning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL102B','The Major Forms of Literature: Novels and Poetry','A study of novels and poetry to determine how the shape of a literary work contributes to its meaning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL103A','The Nature and Structure of the English Language','Introduction to the study of the English language. Topics to be discussed include the nature and origin of language, the structure of English and its development, and the relations between language and reality.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL103B','Varieties of English','Introduction to the study of varieties of the English language - regional, social, temporal, functional, and stylistic. The relations of languages and literature and of speech and writing will be discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL104','Rhetoric in Popular Culture','This course examines the role of persuasion in contemporary society by focusing on one or more topic areas: film, television, video games, comic books, music, fashion, etc. Students will explore the topic area(s) in depth using a variety of rhetorical theories and methods.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL105A','Literature in English, 1900 -1960','A close examination of a selection of works by major authors writing in English in this period.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL105B','Literature in English, 1960 to the present','A close examination of a selection of works by recent and contemporary authors writing in English.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL107','Issues in Canadian Literature','Canada''s literature in English is marked not only by its variety of forms, but also by certain ongoing concerns: language, region, politics, genre. This course introduces a range of writing that illuminates some of these issues and the reading strategies they invite.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108A','The Superhero','An examination of hero figures, ranging broadly from ancient characters such as Gilgamesh to the modern comic book superhero. Literary as well as non-literary materials (e.g., film, comics, games) will be considered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108B','Global English Literatures','An exploration of texts from a range of geographical locations, such as South Africa, Australia, the Caribbean, India, New Zealand, and Pakistan.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108C','Literature and the Environment','A literary and rhetorical examination of some of the main currents of environmental thought through the study of literature, film, advertising, and popular culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108D','Digital Lives','An examination of how digital communication technologies create and promote on-line identities and social spaces, as well as interpersonal and communal interactions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108E','Women in Literature','A study of the role and representation of women, gender, and sexuality in literature in English.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108F','The Rebel','A study of various works of literature in which the protagonist is a rebel against existing norms. The course will examine a number of rebel types and concepts, moral implications, and final outcomes either in successful realization or in tragic defeat.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108H','Isolation and Alienation','The study of a variety of works centering on the theme of individuals in crisis, the stress being on people at variance with their inner selves, other persons, or their world. The course will discuss the process in which wisdom and maturity are gained as the ultimate products of suffering.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL108M','Youth and Adolescence','Studies the portrayal of young protagonists as they respond to the mores of adult society; their own physical, mental, and psychological development; and the expectations placed upon them by themselves and by others.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL109','Introduction to Academic Writing','The course will explore a variety of issues in academic writing such as style, argument, and the presentation of information. Frequent written exercises will be required.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL119','Communications in Mathematics & Computer Science','This course aims to build students'' oral and written communication skills to prepare them for academic and workplace demands. Working independently and in collaboration with others, students will analyze and produce various written and spoken forms of communication. Projects and assignments will draw on materials for Mathematics and Computer Science students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL129R','Written Academic English','This writing skills course provides instruction in grammar, sentence and paragraph structure, elements of composition and academic essay writing, including a focus on theme, development of central ideas, exposition and argumentation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL140R','The Use of English 1','The use and abuse of spoken and written English. The study and evaluation of language as it is used for various purposes (e.g., colloquial, scientific, legal, political, commercial, journalistic, literary) in order to increase critical awareness and to help students to write clearly and effectively.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL141R','The Use of English 2','A continuation of ENGL 140R. The study of factual, emotive, scientific and imaginative writing; relevance, context, meaning, tone, feeling, and intention.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL190','Shakespeare','Designed for students in all faculties, the course examines some of Shakespeare''''s comedies, history plays, and tragedies. Shakespeare''''s variety and flexibility in developing characters and dramatic structures are stressed, as are significant themes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL200A','Survey of British Literature 1','An historical survey of major figures, types, and trends in British literature from the Middle Ages to the late 18th century.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL200B','Survey of British Literature 2','An historical survey of major figures, types, and trends in British literature from the late 18th century to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL201','The Short Story','This course deals with the history and techniques of the short story, with emphasis upon works by such British, American, and Canadian writers as Henry James, James Joyce, D.H. Lawrence, Ernest Hemingway, and Alice Munro.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL202A','The Bible and Literature 1','A study of the major stories, themes and literary characteristics of the Old Testament of the King James Bible (also known as the Hebrew Scripture); and of its influence on other English literature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL202B','The Bible and Literature 2','A study of the major stories, themes and literary characteristics of the New Testament of the King James Bible and of its influence on English literature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL203','Designing Digital Images and Hypertext','This course draws on multiple theoretical perspectives to introduce students to the fundamental principles of multi-modal communication design in its social context. Students will analyze, design, and produce images and hypertext for use in a variety of digital platforms, including e-learning and business applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL204','Designing Digital Video','This course introduces students to the principles of designing time-based multi-modal communication in a social context. Students will analyse, design, and produce video for use in a variety of digital platforms, including e-learning and business applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL205R','The Canadian Short Story','Exploration of the Canadian short story, from its beginnings - in the bush, in the north, on the land, in the small towns - through the struggles of an urbanizing society to the present. Students will be expected to work in some depth with individual authors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL206','Autobiography','This course studies the ways the self is constructed through text by examining a variety of autobiographical approaches, organized from youth to old age, along with theories of identity, memory, gender, narrative, cultural studies, and autobiography as a genre.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208A','Forms of Fantasy','A study of fantasy literature, including some subgenres such as romances, fairy tales, fables, and gothic and horror fiction.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208B','Science Fiction','Various examples drawn, for instance, from Utopian and anti-Utopian science fiction, social science fiction, """"gadget"""" science fiction, parapsychology, and alternate worlds and beings will be considered. Some attention will be given to the historical development of the genre.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208C','Studies in Children's Literature','A critical examination of works of children''s literature. Specific readings may range broadly, encompassing works as diverse as ancient folk tales and novels and poetry from the eighteenth century to the present day.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208E','Women Writing since 1900','This course explores work of women writers, their challenges to social and literary conventions, and their development of voice through major literary movements of the twentieth century and beyond.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208H','Arthurian Legend','The story of Arthur and his knights of the Round Table will be discussed as it is treated at various times in various works and genres. Such matters will be considered as the character of Arthur, the concept of Camelot, and the Fellowship of the Round Table.');
