INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208L','Race and English Literature','An introduction to representations of race in English writings, and the ways in which racial ideas are transmitted and resisted in literature, from the middle ages to the present. Topics may include the invention of the """"""""""""""""race"""""""""""""""", Eurocentrism and imaginative geography, racial beauty myths, internalized racism, and issues of gender, sexuality, and colonialism. Possible writers include """"""""""""""""Mandeville"""""""""""""""", Shakespeare, Behn, Wheatley, Hurston, Achebe, Kogawa, Mukherjee, Kureishi, and Highway.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208M','Travel Literature','The course examines the forms and functions of travel literature as a genre. Topics will include the representation of travel as adventure, discovery, pilgrimage, and escape; travel and tourism; travel and gender; travel and colonialism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL208N','Sex and Marriage in Literature','An examination of changing attitudes toward sex and marriage as those attitudes are expressed in literary works written in English during the various periods of literary production from the medieval period to the modern age.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL209','Writing Strategies','Students practise effective writing along with the study of established models. The goal is to develop language competence to meet a variety of academic, business, and professional situations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL210E','Genres of Technical Communication','This course explores writing, presentation, and design across various genres of technical communication, with a primary focus on printed and/or online computer documentation. Other assignments might include white papers, product specifications, help-desk communication, etc.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL210F','Genres of Business Communication','This courses explores the genres of communication in business and other organizations. Students will study and produce instances from several of the following: reports (of several kinds), letters, email messages, marketing materials, public relations materials, and any other types of organizational communication.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL210H','Arts Writing','A study of the various forms, processes, and modes of publication of professional writing in the arts. The course will consider both free-lance writing and writing within institutional contexts. Practice in research, writing, and editing will be emphasized.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL210I','Legal Writing','A study of the principles, processes, and various forms of writing used in the practice of law and drafting of legislation. The history and structure of legal writing, including current debates about plain language, will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL212','Convict Literature','This course examines the representation of the prison experience in literary works written by or about prisoners as well as the legal contexts of their imprisonment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL213','Litigious Literature','A study of literary works and related case law of fiction, poetry, and drama that has occasioned litigation on such grounds as treason, heresy, obscenity, libel, and plagiarism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL214','Themes in Canadian Literature','The course will survey a theme which is significant to the understanding of the Canadian literary mind. Topics will vary from section to section.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL215','Canadian Regional Literature','This course will provide a survey of literature set in a distinctive region of Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL216','Canadian Multicultural Literature','A study of writing by Canadian authors from a range of cultural and ethnic backgrounds. Works are studied in the context of the social, political and cultural forces that produced Canadian literature in general and Canadian minority literatures in particular.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL217','Canadian Children's Literature','A study of 19th- and 20th-century Canadian literature for children.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL218','Mennonite Literature','A study of poetry and fiction by authors of Canadian Mennonite heritage, since 1962. The course will include a close examination of selected texts considered in the context of the various historical and cultural conditions that affected their production.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL219','Contemporary Usage','An in-depth, applied study of the conventions governing contemporary English grammar, punctuation, syntax, diction, spelling, and sentence structure. In addition, the course will examine variations and changes in conventions; the question of the determiners of correct usage; and the impact of dictionaries, textbooks, journals, large publishing houses, and international wire services on accepted English usage in general and on Canadian usage in particular.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL220A','Languages and Society I','This course examines the role that languages play in multilingual societies from a linguistic perspective. It focuses on topics such as dialects, language contact and change, bilingualism, language choice, and language and identity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL220B','Languages and Society II','This course examines the role that languages play in multilingual societies from a social and cultural perspective. It focuses on topics such as plurilingualism and multilingualism, language maintenance and loss, language planning and politics, multilingual and heritage language education.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL220C','Comparative Literature: Theory and Practice','An introduction to comparative literature, its history as a discipline, and its continuing role in literary studies. Past and present theoretical approaches to literature are used to illustrate the interpretive possibilities achieved by overcoming national, linguistic, and other boundaries. Examples are drawn from a variety of literatures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL233A','Survey of Dramatic Literature and Theory 3','French Neo-Classicism, the Restoration Period and Sentimental Drama.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL233B','Survey of Dramatic Literature and Theory 4','The late 18th and 19th centuries; romanticism and naturalism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL233C','Survey of Dramatic Literature and Theory 5','The first part of the 20th century.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL233D','Survey of Dramatic Literature and Theory 6','The second part of the 20th century.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL235','Survey of Dramatic Literature and Theory 8','American Drama from the 1920s to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL240R','Form and Function 1','The uses of literacy and the functions of language as acquired in ENGL 140R/141R. These will be applied to the more advanced form of the literacy and critical assignment essay, involving comparison, evaluation and exposition.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL241R','Form and Function 2','A continuation of topics covered in ENGL 240R.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL247','American Literature and Popular Culture','An introduction to American literary and cultural studies through the examination of selected movements, moments, topics, or figures, drawing on both literature and other media.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL251A','Criticism 1','An introduction to strategies of reading, interpretation, and analysis of literary and non-literary texts, focusing on narrative, poetics, discourse, and rhetoric, and the acquisition of critical vocabulary.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL251B','Criticism 2','An introduction to the theorizing of literary and non-literary texts. Emphasizing contemporary theories, the course will focus on the text, the reader, and culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL260','Irish Literature and the "Troubles"','A study of Irish literature written during and about the """"Troubles"""" (1916-1923; 1968 - present), focussing on the relationship between literature and its social, historical, and cultural contexts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL290','Global Shakespeare','An analysis of the history and forms of international Shakespeare, which may include 20th- and 21st- century print, theatrical, film, and multimedia productions and adaptations from Africa, eastern Europe, southern and eastern Asia, and Central and South America.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL292','Contemporary Issues in Language, Writing, and Rhetoric','The course inductively defines the fields of Rhetoric and Professional Writing through an exploration of contemporary issues in language, writing, and rhetoric, as those issues are identified and dealt with, in the pertinent scholarly and professional journals, by current researchers and their work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL293','Introduction to Digital Media Studies','A study of theories of digital media, including critical, rhetorical, and semiotic approaches, and of the interpretation and creation of digital media artifacts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL301H','Honours Literary Studies','Through lectures, discussion, and presentations by visiting faculty, this course provides Honours students with an enriched survey of the discipline of literary studies. Topics of discussion will be drawn from bibliography and research methods, critical approaches to literature, literary history, genre studies, rhetoric, media perspectives, and other areas of scholarly interest.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL303','Special Topics in Digital Design','In this course students will learn advanced digital design theory. They will participate in workshops with professional designers, develop specialized digital materials and contribute signature work to their Digital Portfolio.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL304','Designing with Digital Sound','In this course, students will be introduced to sound analysis and production. Students will learn to record, edit, and implement sound in a variety of linear and non-linear media forms, with emphasis on film and video games.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL305A','Old English 1','An introduction to the English language in its earliest form and to English prose in pre-Conquest England, examining Old English prose style, its principal practitioners, and their world view.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL305B','Old English 2','An introduction to Old English poetry, noting in representative Old English poems those things about its purpose, style, and its audience which make it unique but which also provide the beginnings of the English poetic tradition.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306A','Introduction to Linguistics','Introduction to linguistics and the principles of linguistic analysis through an examination of English phonology, forms, syntax, and discourse.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306B','Modern English Grammar','Introduction to modern English grammar and structure - its meaningful forms and syntax. Several methods of analysis will be employed and evaluated, including the traditional, structural, transformational-generative, and functional.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306C','Historical Linguistics','Introduction to historical-reconstruction and comparative analysis. Basic phonological, morphological, syntactic changes as they manifest themselves in language will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306D','The History of English','Introduction to the linguistic history of English from earliest documents to the present, with some consideration of various modern dialects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306E','Linguistics and Literature','A study of linguistic and its applications in analyzing the style and language of literature. Topics include the relationship between the structure of language and literature, speech and writing, speech acts and genres, discourse and text.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306F','Introduction to Semiotics','A study of systems of signs, codes, and signification in language, culture, and literature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL306G','Approaches to Style','Theories of style and approaches to the stylistic analysis of both literary and non-literary texts. Students will consider contributions to the study of style from such areas as traditional stylistics, New Criticism, formalism, affective stylistics, speech act theory, discourse analysis, and sociolinguistics.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL309A','Classical Rhetoric','A study of rhetorical theories from the Classical period (Pre-Socratic to Augustine) with an emphasis on how these theories reflect changing attitudes towards language, reality, and the self.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL309B','Medieval to Pre-Modern Rhetoric','A study of rhetorical theories and practices from late antiquity and the medieval, Renaissance, and Enlightenment periods, with an emphasis on how those theories and practices reflect changing attitudes towards language, society, and the self.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL309C','Contemporary Rhetoric','An examination of contemporary rhetorical theory and its relationships to criticism, interdisciplinary studies, and digital applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL309E','Speech Writing','The analysis, writing, and editing of speeches. Analysis will focus on the reading and viewing of several famous 20th-century speeches using theories of communication. Writing and editing will focus on implementing oral/aural communication strategies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL309G','The Discourse of Dissent','A study of the social, historical, and rhetorical dimensions of collective action. Topics may include health and welfare movements, civil rights and anti-war protests, and environmentalism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL310A','Chaucer 1','An introduction to the poetry and the prose translations of Geoffrey Chaucer, including his dream allegories, """"Troilus and Criseyde,"""" and related compositions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL310B','Chaucer 2','A study of Geoffrey Chaucer''s """"Canterbury Tales"""".');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL310C','Non-Chaucerian Middle English Literature','Non-Chaucerian English writings during the later Middle Ages; the Middle English romance, including """"""""""""""""Sir Gawain and the Green Knight""""""""""""""""; alliterative literature, such as """"""""""""""""Piers Plowman""""""""""""""""; and representative examples of Middle English non-Chaucerian verse.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL313','Early Canadian Literatures','This course examines a selection of pre-1920 Canadian texts concerning first contact, imperialism, colonization, incipient nationhood, and early multi-racial immigration that participate in the ongoing invention of Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL315','Modern Canadian Literature','This course focuses on the varied ways in which 20th-century writers of poetry and prose participate in the shaping of Canadian literary culture, with emphasis on the literature of the middle decades.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL316','Canadian Drama','This course explores traditions and experiments in Canadian drama through an analysis of Canadian plays, especially those from 1960 to the present, in their historical and theatrical contexts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL318','Contemporary Canadian Literature','This course examines Canadian Literature written in the latter decades of the 20th century and into the 21st century. Literary works are studied in relation to relevant contemporary social, cultural and political topics, such as nationalism, aboriginality, multiculturalism and diaspora.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL319','History and Theory of Media 1','This course explores the development of media technologies such as writing and print (including the book) from their beginnings to the twentieth century. Emphasis will be placed on the social, political, and cultural contexts and consequences of these developing technologies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL320','History and Theory of Media 2','This course explores the social, political, and cultural contexts and consequences of contemporary technologies of representation such as print and visual media, photography and film, audio recordings, computer-mediated communications, and interactive digital media.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL322','Postcolonial Literature of the Americas','This course examines postcolonial literature in English from Canada, the U.S., and the Caribbean. Through study of both written and oral genres, we will discuss how language practices adapt to and are created in colonial and postcolonial contexts. Topics may include diaspora and migration, nationalism, gender, neo-colonialism, and multiculturalism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL325','Austen','A study of selected novels by Jane Austen, including Pride and Prejudice and Emma. Her letters and juvenilia may also be considered, as well as some of the films based on or inspired by her novels.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL330A','Sixteenth-Century Literature 1','A study of short poems by such writers as Wyatt, Gascoigne, Whitney, Ralegh, Spenser, the Sidneys, Shakespeare, and Donne.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL330B','Sixteenth-Century Literature 2','A study of selected genres, topics, and works from Tudor literature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL335','Creative Writing 1','Aimed at encouraging students to develop their creative and critical potentials, the course consists of supervised practice, tutorials, and seminar discussions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL336','Creative Writing 2','Designed to assist advanced creative writers to develop their skills in various genres by means of workshop processes, supervised practice, and critical discussion of one or more major projects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL342','American Literature to 1860','A study of developments in early American Literature. Texts may be drawn from Anglo-European movements such as gothicism and romanticism; captivity narratives and other colonial writings; Afro-American, Native American, and other minority traditions; sentimental and domestic fiction; and indigenous American forms such as the frontier romance, and other minority literatures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL343','American Literature 1860-1910','A survey of literary developments in America from the Civil War through the turn of the twentieth-century, including significant movements of the period such as realism, regionalism, and naturalism; the New Woman''''s writing and other developments in women''''s literatures; popular forms such as the Western; and minority literatures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL344','Modern American Literature','A study of American Literature from the early twentieth century through the second world war, emphasizing aesthetic innovation in the modernist movement, and its aftermath in the social writings of the 1930s.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL345','American Literature in a Global Context','A study of the ways in which movements of peoples and cultures have shaped American literature. Topics may include colonialism, immigration and migration, literary influence across borders and languages, nativism and internationalism, racial and ethnic styles and exchanges.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL346','American Fiction','A study of four to five writers. Topics may include the evolution of narrative style, realism and anti-realism, literature and story, fiction and history, the novel and film, gender and ethnicity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL347','American Literature Since 1945','A study of the movements of American Literature following the second world war. The course will consider the formal and cultural diversity of writing in this period, with attention to topics such as avant-garde experiment, the persistence of realism, counter-cultural politics, feminism and literature, postmodernism, and the emergence of minority writers in the mainstream.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL348','American Poetry Since 1850','A study of poems, poets, ideas, and movements, contributing to the growth of a distinctive American poetry from Whitman and Dickinson to the twenty-first century. Texts will be drawn from popular and avant-garde contexts, as well as the literary mainstream.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL350A','Seventeenth-Century Literature 1','A study of literature by such writers as Jonson, Donne, Wroth, Herbert, Bacon, Milton, Behn, and Dryden.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL350B','Seventeenth-Century Literature 2','An intensive study of Milton''s epic, Paradise Lost, in its historical and literary contexts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL361','English Drama to 1642','The Middle Ages, the Elizabethans and Jacobeans (excluding Shakespeare), and the Spanish Golden Age.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL362','Shakespeare 1','A study of the plays written before 1599-1600, excluding Julius Caesar.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL363','Shakespeare 2','A study of the plays written after 1599-1600, including Julius Caesar.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL364','Shakespeare in Performance at The Stratford Festival','An historical, theoretical, and analytical introduction to Shakespeare''s plays in performance, both on stage and screen, this course focuses on specific problems and decisive issues of past productions and of those in the current Stratford Festival season.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL365','Selected Studies','Designed to provide a study in-depth of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL366','Selected Studies','Designed to provide a study in-depth of problems and/or authors selected by the instructor. Students interested in initiating such courses are encouraged to do so by bringing their ideas to the attention of individual instructors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL371','Editing Literary Works','Investigating scholarly, educational, popular, and electronic editions, this course explores the theory and practice of editing literary texts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL376R','Applied English Grammar 1','In exploring different definitions and types of grammar (e.g. descriptive vs. prescriptive), students develop their own critical framework for explaining the structure of English. Of interest to intending teachers of English as the native or second language.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL377R','Applied English Grammar 2','A continuation of ENGL 376R. Practical applications of language theories to error analysis and correction.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL392A','Information Design','The theory and practice of design for print and digital media, including the study of design concepts such as space, colour, typography, interactivity, immersion, motion, and presence. Students produce designs using professional software tools.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL392B','Visual Rhetoric','This course introduces students to the study of images from a rhetorical perspective, including the interaction of texts and images in such professional writing fields as advertising, book illustration, technical documentation, journalism, and public relations. Issues may include visual and textual literacy, the semiotics and rhetoric of design, and the ideological basis of social communication.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL403','Digital Design Research Project','Students work in small groups under the supervision of a faculty researcher on an ongoing, large-scale, digital design project.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL406','Advanced Rhetorical Study','Topics may include communication, media, politics, science, and social movements. Students will explore the topic(s) in depth using a variety of rhetorical theories and methods.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL407','Language and Politics','This course explores how language shapes and is shaped by the unequal distribution of power in modern societies. The role of language will be considered in, for example, the maintenance of sexual difference, the establishment and maintenance of national identity, and the conflict between social classes. The reading will consist of literary and theoretical texts, the latter including such writers as Bourdieu, Bakhtin, Foucault, Cameron, Lakoff, Ngugi wa Thion''go, and Paulin.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL408A','Writing for the Media','This course examines the genres and strategies of both journalism and public relations. With a strong orientation towards rhetorical and linguistic theories, this course will cover audience concerns from both within and outside organizations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL408B','The Discourse of Advertising','This course introduces students to writing and editing advertising copy. Students will also be introduced to models of discourse and rhetorical analysis of advertising texts. Assignments include creating a portfolio of advertising copy and an extensive analysis of sample advertising discourse.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL408C','The Rhetoric of Digital Design: Theory and Practice','Students apply a variety of analytic perspectives - design discourse, multimodal discourse, rhetorical theory, social semiotics - to the design and production of a major digital project (or compilation of projects) using professional software and hardware tools.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL409A','Rhetoric of Argumentation','This course studies the discursive, social, and rhetorical principles of argumentation, including topics such as evidence, reasoning, and the organization and presentation of arguments. Scholars studied may include Richard Whatley, Jurgen Habermas, Stephen Toulmin, Chaim Perelman, Lucie Olbrecht-Tyteca, Kenneth Burke, and Pierre Bourdieu.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410A','Restoration Literature','A selection of literature, including drama, from the period 1660-1700 by such authors as Cavendish, Dryden, Behn, Etherege, Rochester, and Wycherley. Topics may include the poetry of the court wits, literary reflections of Restoration feminism and libertinism, and the rise of print culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410B','Eighteenth-Century Literature 1','A selection of early and mid eighteenth-century literature by such writers as Finch, Pope, Swift, Congreve, Manley, Montagu, Addison, and Steele. Topics may include satire, neo-classicism vs. literary modernism, the development of women''s publication, and generic experimentation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410C','Eighteenth-Century Literature 2','A selection of mid- and late eighteenth-century fiction by such writers as Thomson, Gray, Smart, Thrale, Johnson, Sheridan, and Wheatley. Topics may include the culture of sensibility, the emergence of (auto) biography and of (anti-) colonial writing, and the consolidation of women''s publication.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410D','Eighteenth-Century Fiction I','A selection of late-seventeenth and eighteenth-century fiction by such writers as Behn, Manley, Haywood, Defoe, Richardson, and Fielding. Topics may include the novel as an experimental form, romance and amatory fiction, and authorial engagement with issues of gender, class, and colonialism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410E','Eighteenth-Century Fiction II','A selection of mid- and late eighteenth-century fiction by such writers as Fielding, Haywood, Sterne, Burney, Radcliffe, and Austen. Topics may include sentiment and sociability, the gothic, and abolitionism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL410F','Eighteenth-Century Women Writers','A selection of writing by women such as Behn, Finch, Montagu, Fielding, Edgeworth, and Austen. Topics may include the culture of sensibility, romance and the gothic, and the interaction of women''s writing with discourses of race and colonialism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL430A','Literature of the Romantic Period 1','An examination of the first generation of Romantic writers, including such authors as Barbauld, Blake, Wollstonecraft, Wordsworth, and Coleridge.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL430B','Literature of the Romantic Period 2','An examination of the second generation of Romantic writers, including such authors as Byron, P. B. Shelley, Mary Shelley, Keats, and Hemans.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL451A','Literature of the Victorian Age 1','A critical study of selected poetry by authors such as Robert Browning, Tennyson, Elizabeth Barrett Browning, and Christina Rossetti. Some poetic theory and criticism will also be considered. Topics may include the Romantic inheritance, reactions to Darwin, and aestheticism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL451B','Literature of the Victorian Age 2','A critical study of novels by such authors as Dickens, the Bronts, George Eliot, and Hardy. Social and cultural criticism by authors such as Newman, Ruskin, or Mill will also be considered. Topics may include the """"""""""""""""Woman Question,"""""""""""""""" the crisis in religious faith, and the """"""""""""""""social problem"""""""""""""""" novel.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL460A','Early Literature of the Modernist Period in the United Kingdom and Ireland','A study of the literatures of the United Kingdom before and after World War I, including such writers as Conrad, Forster, Hopkins, Mansfield, Shaw, Synge, Wilde, and Yeats.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL460B','Literature of the Modernist Period in the United Kingdom and Ireland','A study of the literatures of the United Kingdom and Ireland from World War I to World War II, including such writers as Auden, Eliot, Isherwood, Joyce, Lawrence, Orwell, West, and Woolf.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL460C','Literature of the Postwar Period in the United Kingdom and Ireland','A study of the literatures of the United Kingdom and Ireland after World War II, including such writers as Beckett, Greene, Larkin, Murdoch, Osborne, Pinter, and Spark.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL460D','Contemporary Literature of the United Kingdom and Ireland','A study of the contemporary literatures of the United Kingdom and Ireland, including such writers as Byatt, Boland, Drabble, Heaney, Hughes, Rushdie, and Stoppard.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL463','Postcolonial Literatures','This course examines postcolonial literature (fiction, poetry, and drama) from Africa, Australia, Britain, India, New Zealand, and Pakistan. Topics may include the range of creative forms and language use in texts; indigeneity and migration; intersections of gender, sexuality and race; and resistance, nationalism, and history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL470A','Contemporary Critical Theory','Contemporary critical theory offers an array of competing constructions of text and culture. This course examines several topics in recent critical theory, such as gender, race, subjectivity, textuality, and popular culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL470B','History of Literary Criticism','An historical survey of major critical texts and movements from the Greek and Roman classics to the New Criticism of the mid-20th century, examining different critical theories and practices in a context of cultural changes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL470C','Literary Studies in Electronic Forms','A critical examination of literary publication, editing, research, and criticism in CD-ROM and on-line.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL471','Adapting Literary Works','Focusing on adaptation of classic works of literature in English, this course examines the problems, possibilities, and principles of representing such works in other literary forms and in other media.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL481','Topics in the History and Theory of Language','A special study of a selected topic in the history and theory of language. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL484','Topics in Literatures Medieval to Romantic','A special study of a selected topic, author, genre, or period in Medieval to Romantic literatures. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL485','Topics in Literatures Romantic to Modern','A special study of a selected topic, author, genre, or period in Romantic to Modern literatures. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL486','Topics in Literatures Modern to Contemporary','A special study of a selected topic, author, genre, or period in Modern to Contemporary literatures. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL487','Topics in British Literature and Commonwealth Literature Since 1800','A special study of a selected topic, author, genre, period, or national literature in British and Commonwealth Literature since 1800. Please see instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL490','Topics in North American Literature','A special study of a selected topic, author, genre, or period in North American Literature. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL492','Topics in the History and Theory of Rhetoric','A special study of a selected topic in the history and theory of rhetoric. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL493','Topics in Professional Writing and Communication Design','A special study of a selected topic in professional writing and communication design. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL494','Topics in Forms of Media and Critical Analysis','A special study of a selected topic in forms of media and critical analysis. Please see course instructor for details.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL495A','Supervision of Honours Essay','Senior Honours Essay will be completed under supervision.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','ENGL495B','Supervision of Honours Essay','Senior Honours Essay will be completed under supervision.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE100','Studio Fundamentals','This course introduces the basic principles of drawing as it relates to concepts of 2D, 3D, and time-based media, through a series of drawing exercises and projects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE101','Art History and Visual Culture','This course takes a case study approach to examining the role of the art object as a means of communication within the history of visual culture. Particular attention will also be paid to the unique properties of a range of diverse media, both traditional and new.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE102','World Cinema and Visual Culture','Introduction to film analysis via national cinemas and their worldwide reception, taking into account the role of new technologies. This course acknowledges not only the importance of multiple languages and cultures on the planet today but also the rapidly growing international contexts, complicated chains of production and distribution, and vastly different cultures of spectatorship.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE110','Introduction to Art History','This course is not intended to be a chronological survey of the history of art. Rather, it is an introduction to art and to art in history. The primary aims of the course are: to develop the visual skills and acquire the vocabulary needed to analyze a work of art; to examine works of art according to techniques and materials (e.g. how the ''''Mona Lisa'''' was executed); and to examine works of art within the context in which they were created. Modes of artistic education and the exhibition of art objects are also explored. Examples of art are drawn from various time periods and cultures. A field trip fee of $10-$15 may be required.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE112','Modern Art, 1874-1945','A study of the culture of Europe and North America from 1874 (Impressionism) to 1945 (Nazi propaganda) with particular focus on the visual arts. Topics such as ''''''''''''''''''''''''''''''''primitivism'''''''''''''''''''''''''''''''', Cubism, ''''''''''''''''''''''''''''''''abstraction'''''''''''''''''''''''''''''''', artists'''''''''''''''''''''''''''''''' reactions to calamitous world events such as World War I, and various methodologies, including semiotic and Marxist analyses, will be explored through reading and writing assignments.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE120','Fundamentals of Visual Art 1','An introduction to the fundamental principles and concepts of visual art through a series of exercises using a variety of materials.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE121','Fundamentals of Visual Art 2','A continuation of FINE 120 with further exploration of the fundamental principles and concepts of visual art.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE130','Introduction to Digital Imaging','This online digital course is designed to introduce students to current digital imaging software that students will use to create assignments. This course is taught from a Fine Arts perspective.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE150','Appreciation and Expression','The course develops the student''s appreciation of the visual arts through theory and practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE200','Appreciation and Expression','This course is an elective for upper year students who are not majoring or minoring in Fine Arts.The course will develop the student''s appreciation of the visual arts through theory and practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE202','Painting','This course explores issues of representation and abstraction, with a focus on painting and colour theory and practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE204','Topics in Studio Practice','This course consists of topics in studio practice. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE205','Topics in Art History','This course consists of topics in art history. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE206','Topics in Film Studies','This course consists of topics in film studies. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE209','Modern Art, 1940-1970','This course explores two dominant themes in art of the mid-twentieth century: Abstract Expressionism and Pop Art, and their variants in the United States, Canada, and Europe.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE210','Art, 1780-1875','A study of art and architecture in European and American culture between c. 1780 and c. 1875. Some of the pertinent themes discussed include: art and revolutions; art and the Industrial Revolution; the rise of landscape painting; the urban environment; the exotic; and the erotic. Cross-cultural influences and the complex relationship between the traditional and the ''''modern'''' are particularly emphasized.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE212','Renaissance Art, 1300-1500','An examination of the art of the fourteenth and fifteenth centuries in Europe. Amongst the many themes that will be explored in this course are: mediums (including exploitation of oil paint); scientific perspective; humanism; court structures; art and mercantile sectors; and prominent collectors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE213','Art of the 16th Century in Europe','Some of the finest works of art in the western world were produced and some of the most successful artists flourished in this tumultuous century that saw the split in the western Christian Church and the continued rise of extraordinarily powerful court families and monarchs. This course will examine these works of art and the artists against this backdrop. Arranged according to patrons, the course also examines particular stylistic trends (Mannerism, the persistence of the Gothic, etc.) as well as other pertinent issues such as collecting; the writing of art history; the teaching of art; and the role of the artist in society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE214','Medieval Art and Architecture','This course focuses primarily on the art and architecture of Medieval Europe with an emphasis on the Romanesque and Gothic periods. The objects that will be discussed include: church, collegial and castle architecture; sculpture; jewelry; vestments; stained glass windows; and manuscripts. Particular attention will be paid to how these objects functioned in medieval society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE215','Art of the 17th Century in Europe','Art of the 17th Century. This course focuses on art produced in Italy, Spain, France, England and the Netherlands in the 17th century. Various types of art (history painting, portraiture, still life, etc.) and architecture (churches, palaces, city homes, etc.) are discussed in relation to the primary political, religious and societal concerns that characterize the different geographical regions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE216','Topics in First Nations' Visual Culture','A study of historical and contemporary aspects of First Nations'' visual culture. The particular emphasis will vary according to the specialization and interests of the instructor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE220','Oil Painting','Various technical methodologies of oil painting and pictorial composition are developed in this course through a series of explorations in observation and abstraction. The plurality of approaches available to painters today is set within a contemporary critical context.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE221','Acrylic and Mixed Media','This course takes a materially specific approach to painting projects involving acrylic and mixed media. Emphasis is placed on creative problem solving in the context of contemporary art practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE222','Principles of Sculpture','This studio course is designed to introduce students to a variety of traditional and contemporary approaches, materials, processes, and conceptual concerns related to working in sculpture. Through demonstrations, presentations, critiques, and discussions, students gain expertise and develop a sculptural vocabulary.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE223','Methods and Materials of Sculpture','This studio course emphasizes practical applications of traditional and contemporary materials, processes, and conceptual concerns.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE223A','Clay Studies','Using a variety of clay bodies and firing techniques, students will explore figurative and abstract sculptural concepts, to develop a working knowledge of clay as a sculptural medium.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE224','Expressive Drawing','Expressive drawing techniques are emphasized through study with the life model. Conceptual drawing projects provide the student with the opportunity to produce bodies of work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE225','Observational Drawing','Observational techniques are used to develop an understanding of the principles and concepts of drawing practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE226','Experimental Drawing','Experimental drawing is a course for those who wish to challenge their ideas about drawing and image making. An emphasis will be placed on contemporary approaches to drawing and the development of individual forms of expression.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE226A','Introduction to Printmaking A','Introduction to a variety of printmaking processes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE226B','Intermediate Printmaking B','Exploration of printmaking as a vehicle for creative expression.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE226D','Special Topics in Printmaking','An investigation into a variety of experimental, non-traditional printmaking techniques.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE227','Photography','Students will explore black and white analogue (film) photography techniques and history, taught through a series of exercises, with an emphasis on fine arts practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE228','Digital Imaging','In this course students are introduced to digital tools through a series of exercises and assignments that cultivate both artistic expression and technical skill. Topics include photography, design, typography, and critical and cultural theory taught from a fine art perspective.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE228E','Photography for Artists','Introduction to photographic techniques for use as a tool for artists. Basic techniques will be taught through a series of exercises, with emphasis on applications for creative artistic expression and documentation. Supplies at student''s expense.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE229','Hybrid Digital Media','This course uses a variety of digital software to explore the place of digital technology in contemporary art practice. Group discussions and individual assignments are major components of this course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE230','Printmaking','Students are introduced to a variety of printmaking processes. Techniques explored include etching, engraving, monoprinting, collograph, and lithography. Training in traditional skills is combined with opportunities for individual expression.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE231','Mixed Multiples','The course examines the field of traditional printmaking which has expanded to include new and emerging photo-based technologies and ideas that focus on the concept of the multiple.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE232','Video and Sound','This course explores time-based media within a contemporary art context focusing on video and sound. Students learn how to record and edit material through a series of short self-directed projects. Approaches range from stop-motion animation, live-action, and performance to sound and installation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE241','Survey of Greek Art and Architecture','A survey of Greek art and architecture from the earliest times to the coming of the Romans. Material studied may include the art of the Bronze Age, the development of Greek sculpture, the evolution of the Acropolis at Athens and the change in art and architecture after Alexander the Great.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE242','Survey of Roman Art and Architecture','A survey of Roman art and architecture from the earliest times to the age of Constantine the Great. Material studied may include the art of the Etruscans, the evolution of Roman portraiture, innovations in architectural materials and forms, the use of art and architecture by the Emperors and the change to Late Antique art.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE243','Topics in Fine Arts Experiential Learning','This course enables Fine Arts students to gain valuable work experience within an art-related environment. Collaboration between students, professional artists, and art institutions creates a unique learning experience that complements a student''s program of study.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE244','History of Visual Media to 1910','This course provides a chronological and thematic overview of the history of visual media examining technologies such as the printing press, photography, early cinema, and the representation of perspective.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE245','History of Film and Visual Media from 1900 to Today','The course provides a chronological and thematic overview of the history of visual media in Modernity and Postmodernity. It starts at the beginning of the 20th century with the rise of cinema and ends with discussion of the digital age.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE248A','Art in Context','The study of art in context including visiting artists'' lectures, gallery and museum exhibitions and field trips. Specific course content term-by-term will be structured around the schedule of events in the department and the region.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE248B','Art in Context','See FINE 248A for course description.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE249A','Art in Context','The study of art in context including visiting artists'' lectures, gallery and museum exhibitions and field trips. Specific course content term-by-term will be structured around the schedule of events in the department and the region. Students will be required to prepare written reports and visual documents.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE249B','Art in Context','See FINE 249A for course description.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE250','History of Film 1 (1895-1940)','History of world cinema in its silent and early sound era, covering the work of outstanding directors, national productions and movements, and their contribution to the film medium''s development into a prominent art form of the 20th century. Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE251','History of Film 2 (after 1941)','A continuation of FINE 250/DRAMA 356. From the beginnings of the modern sound cinema (Welles) to the contemporary period. Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE252','Religion in Popular Film','This course examines how religion has been a central concern and inspiration for filmmakers and how popular films have informed and shaped our understanding of religion.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE253','Thematic Approaches to Religion in Film','Each semester, this course focuses on a specific genre or theme to explore the complex relationship between cinema and religion. Topics include science fiction and horror, comedy, Jesus in film, Hong Kong Cinema, and Bollywood.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE255R','Film as Social Criticism','Cinema as ''''prophetic voice'''', exploring the films of various directors as they pertain to selected themes which include technology and dehumanization, individual and collective goals, social realities and dreams, and the quest for individual and cultural identity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE256','Experimental Film','The course introduces students to the history, methodologies, theories, and aesthetics of non-narrative experimental film with a focus on intersections between film and visual and performing arts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE257','Video, New Media & the Digital Turn','This course analyzes the shift in visual practices of the 20th century transformed by the arrival of various digital technologies. An emphasis is placed on the increasing interdisciplinarity of new media, as well as their multi-sensory nature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE262','Global Queer Cinema','This course introduces the varied production of world-wide gay, lesbian, bisexual, and transgender cinema. Inquiry will focus on how queer cinematic production serves as a vehicle for documentation and education, aesthetic and sexual experimentation, as well as cultural export and self-inquiry.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE271','Ceramics: Studies in Material Practice','Students will explore ceramic material practice as a method for creative expression. The course will focus on visual and conceptual problem solving, while acquiring technical skills and knowledge of ceramic material. The assignments will incorporate hand building, mold making, surface treatments, and glazing techniques.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE272','Clay Studies','Students will explore the creative development of clay and examine cultural, historical, and personal modes of expression using a variety of construction techniques. Students will explore figurative and abstract sculptural concepts to develop a working knowledge of clay as a sculptural medium. The emphasis is on design, form and visual thinking.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE274','Figure and Anatomy','Students will explore the practice of drawing through the thorough study of the human figure and anatomy. Through a series of studio projects in diverse and mixed media, students will confront the dual challenges of careful observation and personal expression.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE281','Art and Gender','A study of selected themes dealing with gender in the history of art: e.g., the representation of the human body; portrayal of mythological, spiritual and allegorical figures; professional and amateur artists and their status within societies; etc.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE282','Canadian Art from the 17th Century to 1940','This course examines art in Canada from the beginning of the European settlement in the seventeenth century to the Group of Seven and the Canadian Group of Painters. Some of the issues that will be investigated include the perception of Canada from abroad, the """"new world"""", nation and nationhood, and the invention and appropriation of culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE290','Selected Subjects in Fine Arts','A variety of art history topics taught by visiting professors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE293','Fine Arts Abroad','This is a topics course that provides students with an introduction to and a familiarity with the art and culture of a particular destination. Working in the field with landscape, cityscape, and monuments of art, students will employ a variety of media to develop techniques for visual reportage, documentation, note-taking, and journal-keeping. Individual aesthetic responses to a wide range of subject matter will be encouraged.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE294','Fine Arts Abroad','Working in the field with landscape, cityscape, and monuments of art, students will employ a variety of media to develop techniques for visual reportage, documentation, note-taking, and journal-keeping. Individual aesthetic responses to a wide range of subject matter will be encouraged.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE300','Studio Practice','This course provides Fine Arts students with an opportunity to study a range of topics that will expand, explore, and refine personal areas of interest in studio practice. Students will be required to undertake a range of classroom assignments in addition to working independently on an area of focus.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE301','Advanced Studio Practice','This is an advanced fine arts studio course with an emphasis on development of the student''s individual and independent research interests. Students will be guided by faculty through one-on-one critiques as well as open forums and group critiques.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE302','Honours Studio/Art History Research','Students work with assigned faculty members to explore materials, techniques, readings, etc. that are relevant to each student''s areas of interest. Gallery visits are a component of this course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE303','Honours Art History Research','Students work with assigned faculty in a reading seminar environment to explore aspects of art history and theory that are relevant to each student''s areas of interest. Gallery visits are a component of this course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE304','Topics in Studio Practice','This course consists of topics in studio practice. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE305','Topics in Art History','This course consists of topics in art history. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE306','Topics in Film Studies','This course consists of topics in film studies. The topics can change from term to term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE313','Special Topics in 18th- and 19th-Century Art','A seminar course that examines the Neoclassic and Romantic currents of art between 1750 and 1850.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE316','First Nations' Art in Canada','A study of historical and contemporary First Nations'' art produced in Canada. The particular emphasis will vary according to the specialization and interests of the instructor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE319','Contemporary Art','A survey of contemporary international art movements with emphasis on work since 1970. Readings in contemporary criticism and gallery visits are an integral part of the course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE319A','Special Topics in 20th-Century Art','A study of the major innovations in late modern and contemporary art. Honours Art History majors interested in late modern and especially contemporary art are encouraged to use this course as preparation for the fourth year course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE320','Advanced Painting','An advanced painting course with an emphasis on the student''s individual development as a painter, through independent problems, along with class discussions and individual critiques.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE321','Advanced Painting Studio','This course is designed for students in the Honours Studio specialization. This course will prepare students for Fine 472 and Fine 473 and will involve intensive, experimental exploration and development of individual studio practice in painting, research into the work of related artists and critiques by faculty members, graduate students, visiting artists and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE322','Advanced Sculpture','An exploration of sculptural problems in a variety of mediums.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE323','Advanced Sculpture Studio','This course is designed for students in the Honours Studio specialization. This course will prepare students for FINE 472 and FINE 473 and will involve intensive, experimental exploration and development of individual studio practice in sculpture, research into the work of related artists and critiques by faculty members, graduate students, visiting artists and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE324','Advanced Drawing','An exploration of drawing problems in a variety of media. The emphasis is on students becoming familiar with contemporary approaches to drawing and developing their own individual expression.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE325','Advanced Drawing Studio','This course is designed for students in the Honours Studio specialization. This course will prepare students for Fine 472 and Fine 473 and will involve intensive, experimental exploration and development of individual studio practice in drawing, research into the work of related artists and critiques by faculty members, graduate students, visiting artists and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE326A','Advanced Image-Making Through Printmaking Processes','For students interested in pursuing printmaking as their area of concentration in FINE 472 & 473. Students must have demonstrated an ability to work independently on individual printmaking projects. Work will be assessed by the Fine Arts faculty as a whole.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE326B','Advanced Printmaking Studio','This course is designed for students in the Honours Studio specialization. This course will prepare students for FINE 472 and FINE 473 and will involve intensive, experimental exploration and development of individual studio practice in printmaking, research into the work of related artists and critiques by faculty members, graduate students, visiting artists and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE328','Advanced Electronic Imaging','Advanced Electronic Imaging builds on the technical and aesthetic ideas explored in FINE 229. Students are introduced to the principles of animation and audio-visual editing using 3-dimensional imaging and digital audio software. Through a series of structured assignments students will have the opportunity to develop a personal approach to the course content.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE329','Electronic Imaging Studio','This course is designed for students in the Honours Studio specialization. This course will prepare students for FINE 472 and FINE 473 and will involve intensive, experimental exploration and development of individual studio practice in electronic imaging, research into the work of related artists and critiques by faculty members, graduate students, visiting artists and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE330','Topics Course in Museums, Galleries, Curatorship','Often taught by the University of Waterloo Curator, this course explores a range of topics dealing with museums, galleries, and curatorial practices. Field visits may be a required component of this course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE331','Art of the 18th Century in Europe','This course is designed to introduce students to the culture of eighteenth century Europe. Particular attention is paid to France and Britain but these countries are discussed within the wider context of Europe and, at times, much of the world. By looking at the art, artists, patrons and collectors, some of the issues that will be discussed include cosmopolitanism, nationalism, empire-building, """"taste"""", consumerism and women in society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE332','History of Art Academies','This course examines the history of the institutionalization of the teaching of art. The course is organized as a series of ''''case studies'''' dealing with various formal academies of art and less formal gatherings of artists from the Renaissance to the twentieth century. The aims of the course are to analyze why these institutions were formed, how they were organized, what were their mandates, and what was their status in their contemporary worlds. Some of the themes that will be investigated throughout the course include: art in the service of power and politics, the status of the artist, the ideal human form, the classical and medieval traditions, the impact of the avant-garde, the conflict with authority, and the academy as community.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE333','Costume Design','This course examines the art form and practical craft of costume design for the theatre as it is practiced today. All aspects of the design and construction of stage costuming are addressed, with emphasis on text analysis, capturing a period look, fabric choice and methods of costume construction, and rendering approaches and techniques.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE334','Scenic Painting','Decorative painting has been part of worldwide culture since at least the Paleolithic Age. For the past four hundred years, scenic painting has been central to theatre production. This practical course examines the history, techniques and methods of this unique and ephemeral art, blending practical exercises with research work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE335','Design for the Theatre 1','An introduction to the problems of designing for the theatre. Work for the course will include the preparation of drawings and models as well as practical experience in the theatre.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE336','Design for the Theatre 2','An extension of the studies described in DRAMA 331, concentrating on the practicalities of set design.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE337','History of Costume','This course surveys the development of costume, focusing primarily on fashionable clothing in Western societies from the Renaissance to today. It examines the influence of art and design movements, social roles and trends, and manufacturing and marketing methods on the changing fashionable style image of men and women. It includes the role of the fashion designer as well as theatrical and film costume design.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE338','Philosophy of Art','What is art? What is beauty? What do the two have to do with each other? This course introduces students to some fundamental issues in the philosophy of art, and to a variety of philosophical views on these issues.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE341','Advanced Studies in Greek Art and Architecture','An advanced survey of the art and architecture from a selected time period of Greek history. Material studied may include the art and architecture of the Aegean Bronze Age, and the Archaic, Classical and Hellenistic periods. Archaeological, historical and cultural issues specific to each time period will be discussed through the important media of the day.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE342','Advanced Studies in Roman Art and Architecture','An advanced survey of the art and architecture from a selected time period of Roman History. Material studied may include the art and architecture of the Etruscans, the Roman Republic and the Roman Empire. Archaeological, historical and cultural issues specific to each time period will be discussed through the important media of the day.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE343','Topics in Fine Arts Experiential Learning','This course enables Fine Arts students to gain valuable work experience within an art-related environment. Collaboration between students, professional artists, and art institutions creates a unique learning experience that complements a student''s program of study.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE344','Fine Arts Internship','This experiential learning course is intended to enable Fine Arts students to gain valuable work experience within an art related environment. The course is designed to complement the student''s academic program by providing an opportunity for the practical application of skills and concepts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE348A','Art in Context','The study of art in context including visiting artists'' lectures, gallery and museum exhibitions and field trips. Specific course content term-by-term will be structured around the schedule of events in the department and the region. Students will be required to prepare written reports and visual documents.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE348B','Art in Context','See FINE 348A for course description.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE349A','Art in Context','The study of art in context including visiting artists'' lectures, gallery and museum exhibitions and field trips. Specific course content term-by-term will be structured around the schedule of events in the department and the region. Students will be required to prepare written reports and visual documents.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE349B','Art in Context','See FINE 349A for course description.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE350','French Film After 1945','A study of major achievements of the French cinema after World War II. Discussion and comparison of the two main creative impulses of the period: the Academic tradition of the 40s and 50s, and the rebellious nouvelle vague of the 60s. (Bresson, Carne, Ophuls, Renoir, Chabrol, Godard, Malle, Truffaut, Resnais, and others.) Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE351','Central and East European Film','Examination of the development of the motion picture art in Central and Eastern Europe after World War II. Selected work of prominent directors of the Czech Republic, Hungary, Poland, the former USSR, and former Yugoslavia will be discussed (Chytilova, Forman, Jancso, Makavejev, Tarkovsky, Wajda, and others). Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE352','The Cinema of Science Fiction','A chronological survey of one of the most intriguing of film genres. Discussion of its aesthetic, philosophical and cinematic aspects. Film screenings will present major international works in this genre (Godard, Kubrick, Lang, Marker, Siegel, Tarkovsky, Truffaut and other directors). Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE353','Contemporary Italian Film','A study of major achievements of the Italian cinema in its post- Neo-Realist period. Discussion of the works of major directors since the late 1950s (Antonioni, Bertolucci, Fellini, Olmi, Taviani, Rosi, Visconti and others). Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE354','New Cinemas of East Asia (from 1985)','This course examines the role of the post-1985 East-Asian film in the development of motion picture art and the East-West cultural exchange, focusing on Chinese (Mainland China, Hong Kong, Taiwan) and Korean cinemas. It will assist students in interpreting non-Western modes of cinematic expression. Screenings and seminar discussions will include a selection from the fifth and sixth generations of Chinese filmmakers: Hong Kong''''s auteur Wong Kar Wai; Taiwanese directors Hou Hsiao-hsien and Tsai Ming-liang; the achievements of Korea''''s master filmmaker Im Kwon-taek, and the newcomer Kim Ki-duk.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE355','History of Animated Film','This course will examine the historical development of the animated film and the diversity of its stylistic expression. It will focus on some of the most significant achievements of the animated form in an international context, including: Early film animation; Disney and Hollywood cartoon; two and three dimensional and live action animation in Western Europe; Czech animation; the Zagreb animation school, and the Russian animation; National Film Board of Canada and the independent US animation; Japanese tradition; recent advances in computer and experimental animation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE356R','Special Topics in Film','Special topics will be announced from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE357R','Special Topics in Film','Special topics will be announced from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE359','Topics in German Film','Selected topics in German film.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE360','Film and Television 1','Examination of principles of the audiovisual language and the main structural elements of the cinematic work. Discussion of the relationship between film, television and other arts/media. Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE361','Film and Television 2','Development of critical judgment and expression in the area of film and television. Investigation of the role of motion pictures and TV in society. Review of major theories (Eisenstein, Bazin, Metz, Kracauer, Esslin). Film screenings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE362','German Film Classics','Masterpieces of German film from the silent era to present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE363','German Filmmakers in Hollywood','An examination of the work of filmmakers who have made a mark in both European and American cinema.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE364','German and Russian Film Pioneers','An examination of early German and Russian cinema, and the way these two film industries influenced each other. Influential theories of film from the period will also be studied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE365','Film Noir','The principal focus will be on the American """"noir"""" films between 1940-55, the period during which the genre itself was defined and developed. Beyond the style and the techniques of this unique world of film, the parallels between cinema noir and America''''''''''''''''s social and political pressures will be examined. The course will include the neo-Noir school, the filmmakers who ''''''''''''''''borrowed'''''''''''''''' from the originators by re-applying the basics to the changing times in the 1970''''''''''''''''s and beyond.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE366','Musical Theatre and Musical Film','The course explores the elements that are unique to the musical, and the translation of this essentially artificial art form into theatrical and cinematic versions. It will examine in particular the distinctions between musicals based on stage productions and musicals devised exclusively for film.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE367','Plays on Film','The course examines the relationship between stage and film. A number of play scripts and their film adaptations are examined, concentrating on how a filmmaker manipulates stage text to create a film text.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE368','International Comics and Animation Film','This course will examine the interconnections of comics (graphic novels, manga, etc.) and animated film, forms of visual communication particular to the 20th and 21st centuries, in the historical and social contexts of various national traditions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE371','Advanced Ceramic Studio','This is an advanced studio course with an emphasis on the exploration and development of individual practice in the area of ceramics.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE375','Modern British Film','The course examines British film as a political expression of the changing British class system, from pre-war and interwar expressions of the social classes in films such as CAVALCADE and BRIEF ENCOUNTER, through the swift changes of the 1950s and early 1960s in films by Reisz, Richardson and Anderson, and the swinging London of the mid-to-late 1960s, up to the present day. The course focuses on the way the films parallel British social and political change.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE376','American Film','American Film will examine the relationship between film and the social/political movements of each decade since 1930. In this way, the course will address the medium as both chronicler of history and agent for change and/or conformity. At the same time, attention will be paid to the nature of film, its technical development and the changing approaches to acting in American films that is a direct result of the development in theatre of a specific and distinctive American acting style.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE377','The New Hollywood','The course examines the impact of European New Wave films of the late 1950s and early 1960s on American filmmaking, focusing on the revolutionary changes evident during the later 1960s and the 1970s. The course considers the work of filmmakers such as Bogdanovich, Cimino, Coppola, Peckinpah, Penn, Scorsese and others.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE378','Women and Film','The study of selected film texts is informed by contemporary critical readings in feminist and film theory. Subjects addressed may include representation, fetishism and the gaze, female spectatorship, women''''s genres (e.g., melodrama, romance), female stereotypes (e.g., the femme fatale) and women''''s documentary film.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE380','Film Studies Seminar','An introduction to key aspects of motion picture and TV production, film preservation and restoration with visits to studios, film archives, and museums. Screening of selected films and discussions focusing on material unavailable in Canada. Meetings with scholars/students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE381','Film Studies Seminar','An introduction to key aspects of motion picture and TV production, film preservation and restoration with visits to studios, film archives, and museums. Screening of selected films and discussions focusing on material unavailable in Canada. Meetings with scholars/students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE390','Selected Subjects in Fine Arts','Research and reading courses under the direction of individual instructors in either art history or film studies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE391','Selected Subjects in Fine Arts','Research and reading courses under the direction of individual instructors in either art history or film studies.');
