INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE460B','Senior Honours Seminar','This course is intended for students enrolled in the Fine Arts Honours degree in Art History and Studio. Each student will work under the direction of a Fine Arts faculty member on an advanced research project in Art History.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE461','Senior Honours Seminar - Joint Honours and Arts and Business','Each student will work on individual and assigned projects critiqued by visiting artists and supervising faculty and graded by the entire faculty.');
