INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE472','Honours Studio Thesis 1','This course is for students who wish to be practicing artists and who are enrolled in an Honours Fine Arts Studio plan. The course involves an intensive exploration and development of individual studio research by practice resulting in the production of a significant body of work. Students will be guided by the work of relevant artists, critiques by faculty members, graduate students, visiting artists, and fellow students.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE473','Honours Studio Thesis 2','This course is for students who wish to be practicing artists and who are enrolled in an Honours Fine Arts Studio plan. Students will develop strategies for independent studio research and complete a body of work complemented by appropriate documentation, all of which will then be submitted for evaluation by the Fine Arts faculty.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE474','Honours Studio Practicum 1','This course is for students enrolled in an Honours Fine Arts Studio Practicum plan. The course is an exploration of the development of specific technical studio skills and/or the production of a body of works produced through individual research interests.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE475','Honours Studio Practicum 2','This course is for students enrolled in an Honours Fine Arts Studio Practicum plan. Students will research and execute a series of self-directed projects, addressing historical precedents in the context of the concerns of contemporary art practice. Complemented by appropriate documentation, their work will be submitted to the Fine Arts faculty for evaluation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE490','Honours Film Studies Thesis 1','Each student will work under the direction of a Fine Arts faculty member on a research thesis in film studies. The work in this course will be evaluated by a committee of Fine Arts faculty members.');
