INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE492','Senior General Film Studies Project','Each student will work under the direction of a Fine Arts faculty member on an advanced research project in film studies. The course is evaluated by a committee of Fine Arts faculty members.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE493','Senior General Art History Project','Each student will work under the direction of a Fine Arts faculty member on an advanced project in art history. A committee of Fine Arts faculty members participates in the evaluation process.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE496','Honours Art History Thesis 1','Each student will work under the direction of a Fine Arts faculty member on an advanced research project in art history, subject to the approval of the Fine Arts Department. The student is expected to present his/her research findings to members of the faculty at the end of the term. The course is evaluated by a committee of Fine Arts faculty members.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE497','Honours Art History Thesis 2','Each student will work under the direction of a Fine Arts faculty member on an advanced research project in art history. This project may be related to the project completed for FINE 496. The course is evaluated by a committee of Fine Arts faculty members.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FINE499','Senior Studies in Art History','Each student will work under the direction of a Fine Arts faculty member on an advanced research project in art history. The course is evaluated by a committee of Fine Arts faculty members.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','VCULT100','World Cinema and Visual Culture','Introduction to film analysis via national cinemas and their worldwide reception, taking into account the role of new technologies. This course acknowledges not only the importance of multiple languages and cultures on the planet today but also the rapidly growing international contexts, complicated chains of production and distribution, and vastly different cultures of spectatorship.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','VCULT101','Art History and Visual Culture','This course takes a case study approach to examining the role of the art object as a means of communication within the history of visual culture. Particular attention will also be paid to the unique properties of a range of diverse media, both traditional and new.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','VCULT200','Visual Studies Across the Discipline','Weekly lectures by visiting faculty introduce students to how visual studies are conducted across and between the disciplines. Recognizing the vastness of the global study of visual culture, this course reinforces how, despite the diversity of media they analyze, various disciplines share related approaches and theories.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','VCULT300','Visual Culture in Theory','An advanced introduction to theorizing in art and media, highlighting the distinctive role of visual representation in cultural experience and the varied modes of thinking about images that visual representation has engendered. Readings will be drawn from several disciplines including art history, film and literary studies, media studies, and philosophy.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FR101','Beginner French','An intensive beginner course for students having no prior knowledge of French. Emphasizes listening, reading, writing, and oral communication skills.');
