INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FR291','French Civilization 1','This course traces the cultural development of France from its origin to the French Revolution. Emphasis is given to the study of music, art, architecture, literature, ideas, and daily life in their historical context.');
