INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','FR303','Introduction to Linguistics','An introduction to the basic principles of linguistic analysis (as opposed to traditional grammar) applied to the sounds, vocabulary and sentence structure of contemporary standard French and certain features of current Canadian French. Students'' language learning needs will be an important feature of the course.');
