INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER101','Elementary German I','For students with little or no knowledge of German. The basic elements of German grammar with emphasis on group and individual oral practice. Development of skills in listening/comprehension, speaking, reading, and writing. Introduction to aspects of German culture and everyday life. Tapes and computer exercises accompany each chapter of the textbook. Students are encouraged to use them in the language laboratory and at home.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER102','Elementary German II','A continuation of GER 101.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER180','German and Russian Literary Masterpieces','An examination of major Russian and German literary works that have influenced world literature.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER201','Intermediate German I','This course continues the work of GER 101/102. It offers practice in speaking, reading, and writing, with vocabulary building, grammar, and exercises in comprehension.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER202','Intermediate German II','Strengthening of communicative skills, grammar review, vocabulary building, written practice, conversation on issues of contemporary life in German-speaking countries.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER203','Written Communication','The most important elements of German grammar are examined, and students develop the skills necessary for various types of written communication.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER204','Integrative Language Seminar','Develops competence in reading, writing, and oral skills. This course also prepares students for the international ZD (Certificate for German as a Foreign Language) exam.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER211','Integrative Language Seminar I','This course develops reading, writing, and oral skills, and intercultural competence.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER212','Integrative Language Seminar II','This course builds on GER 211 to develop competence in reading, writing, oral skills, and intercultural competence.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER220','Once Upon a Fairy Tale: Fairy Tales, Then and Now','This course studies fairy tales in the broader context of the history of childhood and practices of education and socialization. The course will concentrate on the German and Russian context and in particular on the work of the Brothers Grimm and Alexander Afanasyev, and will also consider fairy tales drawn from a number of different national traditions and historical periods.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER250','Performance German I','This course focuses on improving the student''s oral skills through the preparation and performance of a German play. Students also learn about the theoretical and technical aspects of theatre production.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER261','Languages and Society I','This course examines the role that languages play in multilingual societies from a linguistic perspective. It focuses on topics such as dialects, language contact and change, bilingualism, language choice, and language and identity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER262','Languages and Society II','This course examines the role that languages play in multilingual societies from a social and cultural perspective. It focuses on topics such as plurilingualism and multilingualism, language maintenance and loss, language planning and politics, multilingual and heritage language education.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER271','German Thought and Culture','A survey of cultural currents to the 18th century. Lectures will focus on major developments in literature, philosophy, religion, art, architecture, and music as seen against the historical background of the German-speaking peoples.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER272','German Thought and Culture','A survey of cultural events from the 18th century to the present. Lectures will focus on major developments in literature, philosophy, religion, art, architecture, and music as seen against the historical background of the German-speaking peoples.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER280','Comparative Literature: Theory and Practice','An introduction to comparative literature, its history as a discipline, and its continuing role in literary studies. Past and present theoretical approaches to literature are used to illustrate the interpretive possibilities achieved by overcoming national, linguistic, and other boundaries. Examples are drawn from a variety of literatures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER291','Survey of German Literature and Culture','Introduction to the major periods of German literature and culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER292','Survey of German Literature and Culture','A continuation of GER 291.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER298','Topics in Cultural Studies','Selected topics in cultural studies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER299','German Abroad','This immersion course provides students with an introduction to and a familiarity with selected topics in German-speaking environments. The topics range from history and culture, to language and linguistic varieties in the German-speaking world, as well as arts and the German-speaking media landscape. Course offerings include classes abroad as well as field trips.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER303','German Through Media','Listening comprehension and oral communication are developed through exposure to German media. Students also develop a more sophisticated understanding of the contemporary German society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER304','Reading and Translating','Students learn strategies for understanding a variety of texts such as newspaper reports, manuals, and fiction. Translation exercises are used to improve language skills.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER305','German for Professional Purposes I','A third-year language course concentrating on the language requirements for the modern workplace. Praxis-oriented language and inter-cultural training combined with discussion of the German business world, in particular business organization and the marketplace.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER306','German for Professional Purposes II','A third-year language course concentrating on the language requirements for the modern workplace. Praxis-oriented language and inter-cultural training combined with discussion of the German business world, in particular industrial production and international trade.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER307','German for Professional Purposes','This language course teaches students the skills needed to function in German business and professional environments. Praxis-oriented language and intercultural training are combined with a discussion of the German business world.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER308','German through Comics','German-language comic strips and graphic novels enable students to explore modern language use. Colloquial German language skills are sharpened in this course, and the place of the comic strip and graphic novel in society is discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER309','The Structure of German','Students in this course investigate grammar phenomena in electronic corpora (large principled collections of texts) to develop their language awareness and the accuracy and appropriateness of their written German.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER331','Exploring the German Language','An introduction to the study of linguistics with a focus on developing the German language skills necessary for linguistic analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER332','Studies in Genre (Prose and Poetry)','An introduction to the study of prose and poetry with a special focus on developing the German language skills necessary for analysis of these genres.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER333','Studies in Genre (Theatre and Film)','An introduction to the study of theatre and film with a special focus on developing the German language skills necessary for analysis of these genres.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER334','Exploring German Literature','An introduction to the study of prose, poetry, and theatre. Periods in German literary history, forms of literary expression, and the methodologies used to analyze these forms are examined. Special attention is also paid to developing the German language skills necessary for literary interpretation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER350','Performance German II','A course for more advanced learners of German who wish to improve their oral skills through the preparation and performance of a German play. Students also learn about the theoretical and technical aspects of theatre production.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER353','Intermediate Conversation and Composition on Topics in German 'Landeskunde'','Conversation and composition on topics in German ''''''''''''''''Landeskunde'''''''''''''''' with grammar review and study of German vocabulary and idiomatic expressions. This course is taught in Mannheim in conjunction with the ''''''''''''''''Waterloo in Germany'''''''''''''''' program.');
