INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER364','German and Russian Film Pioneers','An examination of early German and Russian cinema, and the way these two film industries influenced each other. Influential theories of film from the period will also be studied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER383','Culture in the Third Reich: Racism, Resistance, Legacy','An examination of German culture during the Nazi period (1933-1945). The course will analyze representations of Nazi ideology in the arts (film, art, architecture, and propaganda), the literature of exile and """"inner emigration,"""" and the impact of the Nazi legacy on post-war German culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER385','Culture Behind the Iron Curtain','An examination of the culture of the Soviet Union, the German Democratic Republic, and other states that experimented with socialism in the 20th century. Representations of Marxist-Leninist and Stalinist ideologies in literature, cinema, and the arts; the role of the state in directing cultural expression; and resistance to totalitarian control of culture are examined. Western representations of the Cold War are also studied.');
