INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER431','Senior Seminar','Topics in German literary and cultural studies, and linguistics chosen by the instructor in consultation with the department. These courses reflect research interests of the faculty and form part of a well-rounded undergraduate education in ''''Germanistik''''.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','GER490','Senior Honours Project','German Honours students in their third or fourth year may complete, with departmental permission, a major project, the results of which must be presented in a substantial essay. The topic should reflect the student''s field of interest.');
