INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES100','Legendary Past: Russian Myths and Heroes','An introduction to myths in Ancient Rus'''' (Russia). This course traces the evolution of major themes, including heroism, and sets Russian myths in the context of the world''''s mythology.');
