INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES230','The Devil','An examination of the Devil and demonic figures in the Russian and Slavic cultural traditions based on examples drawn from literature, music, the visual arts, and film. Connections to a larger Western cultural context are also explored.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES260','Special Topics','Selected topics in Russian and East European Studies chosen by the instructor in consultation with the department. The topics chosen introduce students to important issues in eastern European culture.');
