INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES271','Russian Thought and Culture','An introduction to Russian culture from the tenth to the nineteenth century through the close study of selected works of literature and art.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES272','Russian Thought and Culture','An introduction to Russian culture from the nineteenth century to the present day through the close study of selected works of literature, art, film, and music.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES273','Croatian Culture and Literature','This course presents the evolution of Croatian culture from the beginnings to 1835. Particular emphasis is placed on developments in literature; however, other significant manifestations of Croatian civilization (art, architecture, music) are also examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES274','Croatian Culture and Literature','This course presents the evolution of Croatian culture from 1835 to the present. Particular emphasis is placed on developments in literature; however, other significant manifestations of Croatian civilization (art, architecture, music) are also examined. Integral to this course are the cultural aspects of Croatian settlements in Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES280','Comparative Literature: Theory and Practice','An introduction to comparative literature, its history as a discipline, and its continuing role in literary studies. Past and present theoretical approaches to literature are used to illustrate the interpretive possibilities achieved by overcoming national, linguistic, and other boundaries. Examples are drawn from a variety of literatures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES281','Women in Russia: The Conscience of a Nation','This course is a survey of the cultural, artistic, and literary impact of women on Russian society and intellectual development. It examines the changing images of Russian women from early tribal matriarchy to the present post-Communist period. Special attention is paid to the """"women''s question"""" in Russia, including nineteenth-century emancipation, the Revolutionary movement, and specific problems and achievements of women in Soviet and post-Soviet Russia.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES310','Russian Folklore','This course examines the cultural background of East Slavic mythology. Elements of the Russian fairy tale and Bylina (Epic Song) - such as a hero''''s or a heroine''''s journeys to the Other World, goddess worship, and the fearsome beings (for example the Baba-Yaga, the Zmei and Koshchei) - are analyzed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES320','The Slavic Short Story','A detailed examination of Russian and eastern European short stories by major representative writers.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES330','Russian Politics through Literature','Russian literature and politics often seem inseparable, and this course illustrates this phenomenon by employing twentieth-century Russian literature to examine Soviet society. Representative works by writers such as Zalygin, Rasputin, Grekova, and Aitmatov will shed light on Soviet agricultural, educational, religious, and environmental policies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES341','Russian Drama before 1905','A study of the origins and development of Russian drama up to 1905. Reading and critical analysis of major works in various genres with emphasis on authors of the 19th century.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','REES342','Russian Drama after 1905','A study of the origins and development of Russian drama after 1905. Reading and critical analysis of major works in various genres with emphasis on authors of the 20th century.');
