INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS201','Intermediate Russian I','This Russian language course is open to students with either basic level of proficiency or a cultural/familial tie to the language. It includes a review of more advanced grammatical structures, practice in reading, composition, and conversation. Students will enhance their language skills, speech etiquette, and sociocultural awareness of the Russian-speaking world through authentic text and multimedia resources.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS202','Intermediate Russian II','A continuation of RUSS 201. This specialized Russian language course is designed for students with an interest in the fields of science, technology, and business. The focus is on specialized terminology and its usage in professional contexts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS203','Integrative Language Studies I','This course develops competence in Russian through the use of authentic materials, with an emphasis on vocabulary building and consolidation of core grammar.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS204','Russian for Heritage Speakers','This course is designed for students of Russian speaking background (native or near-native) who have not received formal language training. The course focuses on reading, writing, and a comprehensive study of Russian grammar. It also develops stylistically appropriate language skills (e.g. academic language).');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS301','Advanced Russian I','A continuation of RUSS 201/202. Vocabulary building, especially the comprehension of literary language, is stressed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS302','Advanced Russian II','A continuation of RUSS 301.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','RUSS303','Integrative Language Studies II','This course advances competence in Russian through the use of authentic materials, with an emphasis on vocabulary building and advanced-level writing skills.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST102','War and Society in Europe, 1914-1945','This course explores the impact of World Wars I and II on European society, with a special emphasis on the experiences of the ordinary person.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST103','Canadian History Through Biography','An examination through lectures and film of the lives of Canadian men and women who have played formative roles in developing the Canadian nation. Examples will be drawn from such areas as politics, religion, business and labour, social reform, arts and entertainment and sports.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST104','An Introduction to Western Intellectual History Since the Renaissance','An exploration of some of the questions and answers posed by thinkers on the human predicament from Renaissance and Reformation times to the modern period. Readings range from Luther to J.P. Sartre, Shakespeare to Marx and Freud.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST105','Rock 'n' Roll and US History','This course explores the politics, culture, media, race relations, and gender relations of the United States after 1945 through the lens of rock and roll.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST106','Canada and War in the Twentieth Century','This course will introduce students to the ways in which historians have examined Canada''s military experience in this century. Beginning with the Boer War, and continuing through the two World Wars and the post-war era, students will examine the political, social, as well as military effects of war on Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST108','Family Ties in History','This course will examine some of the methods of genealogy within the context of the economic, social, religious and political forces that have shaped families and their histories in Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST109','Ten Days That Shook the World','Focusing on ten different days that forever transformed the world''s history, this course introduces first-year students to moments of dramatic change in diverse time periods and places. Topics may include the Fall of Ancient and Modern Regimes; the Age of Discovery; Religious Cataclysm; Revolutionary Wars; Technological, Intellectual, and Scientific Inventions. Students are introduced to the differing methods that historians use to understand the past.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST110','A History of the Western World I','This course will survey the emergence and development of the western world, from prehistory to 1715. Complementing the chronological and narrative overview of western culture and civilization will be thematic surveys of developments in the arts and humanities, science and socio-political structures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST111','A History of the Western World II','This course will survey the emergence and development of the western world from the 17th century to the present. Complementing the chronological and narrative overview of western culture and civilization will be thematic surveys of developments in the arts and humanities, science and socio-political structures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST113','Canadian Business History: Innovators and Entrepreneurs','This course examines the role of individuals in the growth of business in Canada. While there will be general examination of Canadian economic development, the principal focus will fall upon leading Canadian business persons and their interests and innovations. The relationship to the state of business, the place of education, and the impact of immigration are other topics that the course will consider.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST114','A Comparative History of Empires','This course examines the role of empires in modern history. It will examine how empires were formed, how they functioned, how they were resisted, and how they collapsed. While the focus will be on the European empires, we will also assess other examples, including the empires of the Ottomans, the Japanese, the Chinese, and the United States.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST115','Crusading in the Middle Ages','This course examines the historical events and cultural assumptions that led to the European phenomenon of crusading, or holy war, between 1095 and 1453.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST120','The United States at War, 1861-1945','This course will explore the social, cultural, and military impact of the Civil War and World Wars I and II on American society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST130','The Modern World in Historical Perspective','This course will introduce students to the history of the twentieth-century world, through an exploration of the changing nature of relationships between different parts of the globe.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST191','Special Topics in History','One or more topics courses will be offered from time-to-time as announced by the History Department and geared to first-year students. Topics will be dependent upon special interest and/or instructional interests by non-regular or visiting faculty.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST200','History and Film','An introduction to issues in modern cultural history through the study of selected narratives and documentary films with supplementary reading, lectures and discussions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST201','Columbus and After: New Worlds in the Americas, 1492-1680','Beginning with Columbus, this course introduces the history of early America as it was shaped by the encounters between colonizers and colonized. Particular attention is paid to the varied nature of these encounters and their contested interpretation by historians and others.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST205','History of Western Sport','This course considers the historical impact of Western sport. It traces the history from individual play through amateurism to professionalism, big business, and media. It examines sport''s social role within local, national, and international communities, and its relationship to class, gender, leisure, race, and politics.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST206','The Victorian Age','During the reign of Queen Victoria (1837-1901), Britain experienced profound change with the expansion of its population, industry and empire. Poverty, gender and racial discrimination persisted in spite of a marked expansion in political rights. This course will address both progressive and regressive forces during this era, focusing on issues of culture, politics, imperialism, and society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST207','Canadian Labour History','This course deals with the history of organized labour in Canada with an emphasis on prominent labour leaders, major industrial disputes and labour''s role in politics. It will also evaluate the development of the Canadian industrial relations systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST208','Foreign Relations of the United States since 1900','This course examines the history of foreign relations of the United States from the """"""""""""""""Age of Imperialism"""""""""""""""" through the """"""""""""""""War on Terror."""""""""""""""" Topics will include the Great War, Wilsonianism, World War II, the Cold War, human rights, and post-9/11 U.S. foreign policies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST209','Health, Disease and Medicine in Canadian History, 1500 to the Present','Starting with Amerindian medicine, the course will examine topics such as the rise of the medical and nursing professions, changing public attitudes to health and disease, and the evolution of the Canadian health insurance system.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST210','History of Ancient Law','An historical introduction to law in the Ancient world. Babylonian, Assyrian, Hittite and Roman law, legal practices and concepts will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST211','British History to 1485','A survey of the main stages in the transition of Britain from a remote province of the Roman Empire to a prominent state in post-Reformation Europe. Within the chronological framework, political and constitutional as well as ecclesiastical and social developments will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST213','A History of Popular Culture','This course introduces students to the history of Western popular culture and may include the study of popular literature, spectacle and performance, witchcraft, crime, sexual attitudes, consumption, sports, advertising and the media.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST214','History of Women in the Modern United States','This course will examine women''''''''''''''''s social, political, cultural, and economic position in the United States from 1920 to the present. We will study the evolving understandings of women''''''''''''''''s """"proper place"""" in society, which has varied based upon race, class, ethnicity, and region. We will consider women''''''''''''''''s daily lives and the forces that brought women into the public sphere. Topics covered will include women''''''''''''''''s political activism, legal position, sexuality, and paid and unpaid labour.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST215','Canadian Women in Historical Perspective','This course will focus on the interrelationship of women and Canadian society through an examination of women''s private and public lives.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST220','The Vietnam War and American Society','The Vietnam War, the longest war in U.S. history, was fought on two fronts, by American G.I.s abroad and anti-war protesters at home. Those two subjects, as well as a history of the war from Vietnamese perspectives, will be the focus of this course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST221','Race Relations in Canada: An Historical Perspective','The """"race problem"""" has appeared on the Canadian public agenda, but the issue is not of recent origin. This course will examine Euro-Canadian attitudes and practices toward non-European minorities from pioneer times to the present and will set racial policies in the context of the evolution of a Canadian national identity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST223','The Holocaust in History','An examination of the Holocaust in the context of the history of modern racism. Study topics will include historic anti-Judaism, scientific racism and the development of modern antisemitism, Nazi ''''race'''' ideology, wartime policies from ghetto to genocide, resistance movements, Nuremberg trials, Holocaust denial, universal lessons from the Holocaust.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST224','Food, Culture, and History','This course will examine the role of foodstuffs and foodways in world history, with an emphasis on Canada in the 20th century. Themes such as colonialism, immigration, ethnic identity, religion, gender, famine, and political policy will be examined to explore how food, and its associated habits and customs, has been central to the evolution of cultural patterns of the past.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST225','History of Education in Canada','This course considers the development of education as an institution within Canadian society and provides an understanding of significant educational issues and policies from a historical perspective.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST226','Canada in World War II','The Canadian experience in World War II is still a subject of considerable debate. This course will employ lectures, films and discussion groups to examine the war''s impact on the social, economic, political and military life of the country from 1939 to 1945.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST227','The French Revolution and Napoleonic Europe','This course will examine the causes and consequences of the French Revolution and the impact of Napoleon''s reign in France and Europe.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST230','Introduction to the Modern Middle East','This course examines the modern political history of the Middle East, with an emphasis on international affairs. It examines the colonisation of the Middle East, the rise of national self-determination and nation-states, enduring Arab-Israeli conflicts, the Cold War, and the impact of US foreign policy in shaping the modern Middle East.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST231R','The History of East Asian Communities in Canada','This course examines the evolution of the Chinese, Japanese, and Korean communities in Canada as well as their significance for Canadian economic, social, and political life in the nineteenth and twentieth centuries.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST232','A History of Peace Movements','A survey of individuals and groups that have created popular movements for peace globally and locally throughout history. The scope will be international, with a particular focus on the nineteenth and twentieth century movements. The choice of peace movements will allow for a contrast in comparison of ideology, strategy and impact.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST234','The Catholic Church in Canada','An examination of the role played by the Church in the social, political, and economic life of Canada from 1867 to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST235','History of Christianity','The development of Christianity in its Roman Catholic, Eastern Orthodox, and Protestant traditions from the time of Christ to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST236','Law and Society in the Middle Ages','A study of the laws and legal procedures of the Middle Ages. The course examines the relationship between legal procedures and institutions and the medieval societies that produced them.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST237','The Ancient Near East and Egypt','A study of the civilizations of the Ancient Near East focusing on Mesopotamia (Sumer and Akkad, the Babylonian Dynasty and the Third Dynasty of Ur), Hatti, Assyria, Egypt and Persia.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST239','History of Modern China, 1911 to the Present','Some of the topics studied in this course include: the three stages of warlordism, the May Fourth Movement and the structure of society in the People''s Republic of China.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST242','Greek History','A survey of ancient Greek history, from the Bronze Age to Alexander the Great, emphasizing particularly its political and military aspects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST243','A History of the Workplace: Europe 1750 to the Present','This course will examine the changing nature of work and the workplace in Europe and the impact of those changes on European society. The objective of this course is to develop a better understanding of today''s workplace and its challenges by exploring its historical roots and the forces that gave it shape.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST244','The Medium and the Message: Canadian Media, a History','An examination through lecture and film of print journalism, broadcasting, the Canadian Broadcasting Corporation, the National Film Board, Wartime propaganda, the Canadian music industry, and the other diverse forms of media.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST245','War, Ethnicity and Religion in East Central Europe, 1453-1739','This historical survey of a region encompassing the contemporary Czech Republic, Slovakia, Hungary, Slovenia, Croatia, Austria, and parts of Serbia, Romania and Germany is crucial for understanding important contemporary developments. The focus will be on how East Central Europe was shaped by an orientation toward Western Christianity, confrontations with the Islamic empire of the Ottoman Turks, the experience of multiethnicity, and Habsburg subjugation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST247','Mennonite History: A Survey','This course covers Mennonite origins, teachings, migrations, settlement patterns, divisions, leaders, institutions, and religious and social practices, indeed all facets of Mennonite history in various national settings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST249','The American Impact on Canada','This course will examine the social, economic, cultural, and diplomatic aspects of Canada''s relationship with the United States, from the time of the American Revolution to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST250','The Art and Craft of History','This course will provide a collegial learning setting within which students will be introduced to techniques of historical writing and research, and some examples of the best of recent historical scholarship.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST252','Roman History','A survey of ancient Roman history, from the Republic to the Empire, emphasizing particularly its political and military aspects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST253','Canadian History: The Colonial Period','This course examines the major themes in pre-Confederation Canadian history including the rise and fall of New France, the creation of British North American societies in the Maritimes and Upper Canada and economic and political development.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST254','Canadian History: The National Period','This course examines Confederation, the rise of political parties, Canadian external relations, western discontent, the impact of both world wars and political and economic changes in Canada since 1867.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST255','History of Childhood and Youth in Canada','This course offers a Canadian history of childhood and youth. While childhood may seem like a timeless, eternal concept, it is actually a modern one. This course considers age as a category of analysis, complementing other key concepts such as gender, class, and ethnicity/race.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST257','America Until 1877','This historical survey focuses on the emergence of the United States as a nation. The topics explored may include indigenous peoples, slavery, race, gender, labour, immigration, urbanization, culture, sectionalism, politics, and ideologies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST258','United States Since 1877','This course begins in the aftermath of the Civil War and ends at the present day. Topics may include major social movements, the place of the United States in world politics, immigration and imperialism, and the economy.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST260','Europe: 410-1303','The political, cultural, economic and ecclesiastical development of Europe from the fall of the Roman Empire to the end of the High Middle Ages.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST262','Early Modern Europe 1450-1700','This course examines European life in the Early Modern Period (1450-1700) and investigates the social, political, religious, and intellectual changes during the Renaissance, the Reformations, and the era of explorations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST263','Europe in the Nineteenth and Twentieth Centuries','This period marked the emergence of modern-day Europe. The course will focus on the way in which European society, politics and culture changed and why. It will also examine the continent''s descent into dictatorship and two world wars.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST264','Western Europe Since 1945','Western Europe since the end of World War II. Focus will be on the Cold War, political and social movements.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST265','Eastern Europe Since 1945','An introduction to the social, economic and political history of the countries of Eastern Europe since 1945. Topics may include the implementation of Communism, daily life, the secret police, women, opposition, and revolution.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST266','The British Empire 1857-1956','This course assesses the transformation of the British Empire from its position of comparative strength in the mid-nineteenth century to de-colonization and the emergence of the Commonwealth after theINSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST268','A Comparative History of Empires','This course examines the role of empires in modern history. It will examine how empires were formed, how they functioned, how they were resisted, and how they collapsed. While the focus will be on the European empires, we will also assess other examples, including the empires of the Ottomans, the Japanese, the Chinese, and the United States.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST269','Aboriginal History of Canada','This course examines the history of aboriginal peoples within Canada from before contact to the present. It emphasizes the relationship between aboriginal peoples (First Nations, Inuit, Mtis) and settler society, forms of indigenous resistance and assertions of rights, the diverse nature of communities and cultures, and the relationship between local and national histories.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST271','The Histories of Indigenous Peoples in the Local and Global World','This course examines the histories of indigenous peoples from around the world, covering areas such as educational assimilation, the internationalization of indigenous rights, lands and resource development conflicts, indigenous-state relations, language and cultural revitalization, and international politic activism. We will examine local Indigenous histories and place these experiences into the larger global context.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST275','The Modern World in Historical Perspective','This survey of the twentieth century explores the non-Western world''s response to a series of selected global themes: the rise of the West; post-colonialism; war and peace; human migration; culture; international organizations; climate change; human rights; disease and globalization.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST277','Canadian Legal History','This course examines the Canadian Legal system from colonial time to the present with particular emphasis on such themes as law and the economy, courts and judiciary, the legal profession, family and criminal law, women and the law and civil liberties.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST278','The USSR and World War II: The Great Patriotic War','This course examines the Soviet experience during World War II. It will consider such themes as the impact of totalitarian state systems and radical ideologies on the war; racial genocide; the wartime economy; resistance and collaboration; relations with the Western Allies; and the specific problems of a multiethnic state at war.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST282','History of Modern South Asia 1750-2000','This course provides an overview of the political developments and conflicts that have shaped modern South Asia from 1750 to 2000 through an examination of the regions'' cultures, political systems, encounters with Western imperialism, nationalist movements, and the impact of the Cold War.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST291','Special Topics in History','One or more term courses will be offered from time to time as announced by the History Department. Topics will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST300','History and the Human Sciences','This introduction to historiography traces the relationship between history and other human sciences (anthropology, economics, literature, philosophy, and sociology) since the nineteenth century. In addition to strengthening critical skills, it offers interdisciplinary perspectives on problems of objectivity, documentary evidence, forms of story-telling, and causal explanations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST302','Public History: Remembering and Forgetting in Canada and Beyond','This course critically examines the many sites of historical production in the Waterloo region, across Canada, and in international contexts. Students will become familiar with the growing scholarly literature on public history and social memory and will engage in the production of their own public history project.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST303','Digital History','Digital history, the application of new and emerging technologies to the study of history, is an exciting new field. This course explores the literature on digital history and then puts theory into practice by exploring the digital collection, evaluation, and production of historical knowledge.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST304','Heresy and Religious Crises in Late Medieval Europe','An exploration of the impact of social crises on late medieval religious modes of expression. Topics will include the Great Famine, the Black Death, the Avignon Papacy and Western Schism, the development of heretical movements, and the eventual disintegration of European religious unity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST305','Historical Memory and National Identity','What factors create a national identity: historical events or socially-constructed historical memory? By analyzing key themes such as elite and popular culture, historical sites and commemorations, sports, and iconic public events like the Centennial and Expo 67, this course will demonstrate how Canadians have created a series of evolving national identities.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST309','The Discourse of Dissent','A study of the social, historical, and rhetorical dimensions of collective action. Topics may include health and welfare movements, civil rights and anti-war protests, and environmentalism.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST310','The American West: Legend and Reality','An exploration of westward expansion in the United States in the 18th and 19th centuries and its impact on American popular imagination. Themes will include explorations, indigenous peoples, labor, women, violence, and frontier culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST311','International Relations, 1890-1951','This course examines the international relations of the great powers from the rise of Wilhelmine Germany in the 1890s to the first steps of European integration in the early 1950s. Attention will be paid to the formation of foreign policy, alliances, leadership, war-making, and peace-making.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST312','National Identity in the Twentieth-Century United States','This course examines how people living in the United States during the twentieth century conceived of American identity. Examples will be drawn from politics, culture, race and gender relations, and U.S. foreign policy.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST313','History of the Family in North America','This course will consider the history of private interactions between family members in North America, as well as the family''s relationship to public forces such as politics, the law, social movements, and the economy. Other topics covered in this course include changing conventions of courtship and dating, marriage, divorce, parenthood, and childhood.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST314','The American Civil Rights Movement','This course will explore the Civil Rights movement in the United States from the 1950s to the 1970s. Topics will include the origins and evolution of the movement, tactics, key figures, and the role of the federal government.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST315','Foreign Relations of the United States since 1900','This course examines the history of foreign relations of the United States from the """"""""""""""""Age of Imperialism"""""""""""""""" through the """"""""""""""""War on Terror"""""""""""""""". Topics will include the Great War, Wilsonianism, World War II, the Cold War, human rights, and post 9/11 U.S. foreign policies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST316','The Russian Revolution','This course will trace the history of the Russian Revolution from 1861 to 1924. It will examine the root causes of the Revolution, the rise of the Bolshevik Party and its seizure of power, and the ensuing civil war, culminating in the establishment of a communist dictatorship.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST317','History of Sexuality: The Pre-Modern Period','This course introduces students to the history of Western sexuality, beginning with the ancient world and focusing primarily on the Middle Ages and the transition to modernity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST318','History of Sexuality: The Modern Period','This seminar introduces students to the history of Western sexuality. The course focuses on the 19th and 20th centuries.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST321','Human Rights in Historical Perspective','A detailed analysis of selected topics in the history of human rights. Special attention will be paid to revolutionary developments since World War II, and to the emergence of modern human rights policies. Topics will be examined through assigned readings, lectures, and films.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST329','Origins of the Common Law','A study of the common law of England from its introduction in the 11th century to the 15th century. Original documents and court cases will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST339','The History of France in the 19th Century','A study of French society and the four revolutions that influenced it with particular attention to social and institutional forces.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST340','A Social History of Europe: 1789-1914','European society amidst the dramatic changes of the 19th century. Emphasis is given to the impact of the French and industrial revolutions on class, the family, religion, and living conditions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST341','Occupied Europe, 1938-1945','This course will examine the nature and impact of Nazi occupation on Western and Eastern Europe preceding and during World War II and the responses of the people occupied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST347','Witches, Wives, and Whores','This course explores the ways in which Early Modern European women experienced, participated in, shaped, and responded to the world they inhabited. It investigates the ways in which women negotiated the Early Modern world as it unfolded in the Renaissance, the Reformation, European encounters around the world, and the challenges of everyday life.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST348','The Radical Reformation','A study of 16th century Anabaptism -- a religious Reformation movement dissenting from both Protestantism and Roman Catholicism -- its origins, its social, political, and theological content; and its relationship to such independent dissenters as Sebastian Franck.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST350','Canada and the Americas','This course will examine the economic, cultural, and diplomatic aspects of Canada''s relationship with the United States, Latin America, and the Commonwealth Caribbean from the time of the American Revolution to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST351','Canada: The Immigrant Experience','Immigrants and immigration have always been central to Canadians'''' perceptions of themselves as a country and as a society. This course will examine the immigrant experience and Canada''''s changing policies and attitudes toward immigration and immigrants from New France to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST356','20th Century Russia and History of the Soviet Union','This course examines the history of Russia and the Soviet Union from the beginning of the 20th century to the present. Topics include: origins and nature of the Russian Revolution, communist society, Stalinism, the Cold War and impact of the communist experience on contemporary Russia.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST358','Nazi Germany','An examination of the social, economic and political history of Nazi Germany. Topics may include the rise of the Nazis, the secret police, war, population policies and mass murder, culture, and women.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST363W','Jews in Modern Europe 1750-1938 (WLU)','');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST369','The Politics of Decolonization','This course examines the break-up of empires in the mid/late 20th century and assesses how the colonial experience has shaped relations between and among former imperial polities to the present. Focus is given to questions of political identity in colonial and post-colonial societies and how these identities shape current political challenges.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST371','Ireland Before the Famine','A focus on social and economic determinants of Irish History from the Penal Era to the 19th century struggle for Catholic emancipation and the Great Famine.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST372','Ireland After the Famine','An exploration of the political, social and cultural history of Ireland from the Famine to the end of the 20th century including the formation of the Irish State, the Republic and the """"Troubles"""".');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST374','Canada's Social History','A topical consideration of key themes, approaches, and chronologies in the history of society in Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST379','Reformation History','A study of the major 16th-century reformers and their intellectual background in humanism and late medieval scholasticism. Special attention will be given to the Lutheran and Reformed traditions and their ideological, social, and political expressions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST380','History of the Canadian North: From Pre-contact to the Creation of Nunavut','The idea of """"""""""""""""northerness"""""""""""""""" is central to our national identity, yet few """"""""""""""""southern"""""""""""""""" Canadians have an appreciation of the historical development of Northern Canada. This course will focus on political, social, cultural, and environmental histories and will introduce students to major themes in Canadian Northern history, from pre-contact to the creation of the territory of Nunavut in 1999.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST385','From Macdonald to Laurier: Canada, 1841-1921','A topical examination of major political and social developments over this eighty year period. These include Irish immigration, Confederation, the Riel rebellions, social reform, the development of labour and business, and the Boer and First World Wars.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST387','Ontario History since Confederation','The course will examine the emergence of Ontario as an industrial giant and the development of its hegemony in Canada. An emphasis will also be placed on the sources and methods of local historical research.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST388','Modern Canada','Lectures, tutorials and independent research will provide a decade-by-decade examination of the central social, political and economic themes that have helped characterize ''''modern Canada''''.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST389','Canada in World Affairs','An analytical and historical examination of Canadian foreign policy in the international system. Domestic sources of Canadian foreign policy and international sources of Canadian foreign policy are examined in detail.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST390','The Canadian City Since 1880','The course focuses on the history of environmental issues such as pollution and water management and social problems in health, education, welfare and culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST391','Special Topics in History','One or more term courses will be offered from time to time as announced by the History Department. Topics will be dependent upon special research and/or instructional interests of faculty.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST397','Directed Studies in Special Topics','Study in a limited field under tutorial guidance. A high standard of written work will be expected.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST398','Directed Studies in Special Topics','Study in a limited field under tutorial guidance. A high standard of written work will be expected.');
