INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST400B','Early Modern Europe','Research seminar in particular topics related to the study of Early Modern European history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST401A','European','Selected themes in the historiography of European history.');
