INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST402A','Medieval Europe','Selected themes in the historiography and methodology of medieval European history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST402B','Medieval Europe','Research seminar in particular topics related to the study of European medieval history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST403A','Canadian','Selected themes in the historiography of Canadian history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST403B','Canadian','Research seminar in particular topics related to the study of Canadian history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST407A','Race in Modern History','Selected topics in the historiography of the study of ''''race'''' in modern history.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST407B','Race in Modern History','Research seminar in particular topics related to the study of ''''race'''' in modern history.');
