INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','HIST491','Independent Study in Special Subjects','May substitute for either a 4th year ''''''''''''''''A'''''''''''''''' or ''''''''''''''''B'''''''''''''''' History seminar, and can be either a readings or research seminar. The topic is determined by the individual faculty member supervising the seminar, in consultation with the student.');
