INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL100J','Introduction to Philosophy','This course seeks to introduce students to the nature of philosophy. This is done through the examination of core texts and figures in the history of philosophy as well as in the discussion of perennial philosophical questions.');
