INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL418J','Ethics and Society','This course examines the nature and purpose of community living as well as such traditionally controversial issues as private and public morality, the individual good and the common good, personal freedom and group responsibility.');
