INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL440A','Logical Theory','A rigorous and general development of the propositional and predicate calculus within which alternative calculi are examined. Study of such concepts as completeness, consistency, extensionality, and modality from both formal and philosophical points of view. Intended primarily for those interested in philosophical issues connected with logic.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL440B','Logical Theory','The second part of PHIL 440.');
