INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL442','Studies in Logic','Special topics in logic, as announced by the Department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL447','Seminar in Cognitive Science','An interdisciplinary discussion of central issues concerned with mind and intelligence, such as representation, meaning, inference and consciousness.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL450J','Being and Existence','A discussion of metaphysics as the kind of examination initiated by the Greeks (e.g., Parmenides and Aristotle) and renewed subsequently by many other philosophers (e.g., Aquinas and Heidegger), understood as ontology or the rational study of being as such. Notions such as reality, essence, existence, and analogy will be discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL451J','Thomas Aquinas','An examination of the work of Thomas Aquinas and some of the main themes addressed by him, although a particular emphasis will be placed on ethics and metaphysics. His relation to ancient, Arabic, and thirteenth-century thought will also be considered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL452','Studies in Epistemology','Special topics in epistemology, as announced by the Department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL455','Studies in Metaphysics','Special topics in metaphysics, as announced by the department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL456','Problems in Metaphysics','Various problems in metaphysics, as announced by the Department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL458','Studies in the Philosophy of Science','Special topics in the philosophy of science, as announced by the department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL459','Studies in the Philosophy of Physics','An investigation of philosophical issues raised by physics. Topics could include the interpretation of quantum theory, the arrow of time, or the nature of space and time. This course is suitable for students with a background in either philosophy, physics, or mathematics.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL463','Studies in the Philosophy of Language','Special topics in the philosophy of language, as announced by the department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL465','Existential Philosophy','An in depth study of the thoughts of a major figure such as Kierkegaard, Unamuno, Nietzsche, Heidegger, Sartre, Camus, Marcel, Jaspers, Ortega y Gasset.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ART','Arts','PHIL470','Phenomenology','A critical examination of the issues and methods of phenomenology, including the attempts to understand the uses and ramifications of phenomenological methods through the working out of particular analyses. The basic writings of phenomenologists such as Husserl and Merleau-Ponty will be used.');
