INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC222','Conducting 1','A study of conducting techniques appropriate for song leading, choral rehearsal and public performance. The course will include score analysis and rehearsal procedures for music from a wide variety of historical styles.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC226','Music Studio','Individual instruction in Voice, Piano, Organ, Classical Guitar and orchestral instruments.');
