INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC231','Psychology of Music','The study of music from a behavioural science perspective. Topics include auditory and musical perception, music cognition, musical aptitudes and abilities, learning and pedagogy, creativity and aesthetic experience, emotive human responses and the social psychology of music activities.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC240','Introduction to Jazz','A survey of the development of jazz schools and individual styles as well as a study of melodic, harmonic and rhythmic improvisation. Styles will be demonstrated through recordings and live performance.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC245','World Music','A survey of traditional music outside of North America, including Africa, South America, the Far East, the South Pacific, the mid-East and Europe, with special emphasis on the role of music within the culture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC246','Soundtracks: Music in Film','An examination of the techniques, styles, and functions of music in film, and how music interacts with image and narrative to create meanings. Weekly screenings of films selected from the 1930''s to the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC253','Cathedral and Court: Music to 1600','The study of music that flourished under courtly and church patronage from the early Christian Church to 1600. Gregorian chant, liturgical drama, mass, motet, secular songs and instrumental music are studied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC254','Monteverdi to Mozart: Music from 1600-1800','The response of 17th- and 18th-century composers to cultural, political, and artistic movements. Topics include performance practice, women composers and performers, the development of opera, choral music, symphony, and sonata, and the music of Monteverdi, Bach, Handel, Mozart, Beethoven, and others.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC255','The Romantic Century: Beethoven and Beyond','The music of the 19th century in all its principal forms by its great composers: Beethoven, Schubert, Schumann, Liszt, Chopin, Wagner, Verdi, Brahms, Bruckner and others.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC256','Music Since 1900','A study of the contemporary composer''s challenge to traditional musical ideals and musical reactions to artistic, political, social, and technological movements since 1900. Topics include the changing relationship between composer, performer, and audience; recent developments in Canadian music; and the contributions of women composers.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC260','The Symphony','A survey of the great symphonies from Haydn to Stravinsky, through lectures and listening. A portion of the course will be devoted to works being performed by the Kitchener-Waterloo Symphony Orchestra during the term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC261','Opera','An introduction to the themes, sources, forms and nature of opera through selected works from Mozart to the present. Special topics include opera as a reflection of society''s politics and ideals and opera behind the scenes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC270','Music Theory 1','The study of basic melodic, harmonic and voice leading concepts including an introduction to figured bass and functional harmony. Ear-training, sight-singing and keyboard lab sessions will be integrated with written and analytical work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC271','Music Theory 2','The study of harmony, counterpoint and form of 18th- and early 19th-century music. Sight-singing, ear-training and keyboard lab sessions will be integrated with written and analytical work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC275','Music and Technology','A comprehensive study of music and technology, including electroacoustic music, computer and MIDI applications, soundscape and multimedia composition, music and the web. Digital studio work is part of the course.');
