INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC332','Aesthetics of Music','This course employs various methods to explore questions of musical meaning. Through study and discussion of selected sources from antiquity to the present, such themes as imitation, abstraction, beauty and other debated issues will be examined in historical context and in relation to the other arts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC334','Women, Music and Gender','A study of gender issues relating to the role of women in music from antiquity to the present, emphasizing both classical and popular music within social, cultural settings of Western and non-Western societies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC355','Music and Culture Travel Course','A Spring term seminar, approximately three weeks long, taught on location. Daily lectures and attendance at music performances, as well as tours of places of cultural importance. Past and possible future locations include Vienna, London, New York and South Africa.');
