INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC370','Music Theory 3 (19th-Century)','The study of chromatic harmony as well as melodic and formal aspects of 19th-century music. Ear-training, sight-singing and keyboard lab sessions will be integrated with written and analytical work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC371','Theory 4 (20th-Century)','The study of the compositional aspects of 20th-century music, including extended tonality, atonality, 12-tone writing, neo-classical idioms and contemporary compositional procedures. Lab sessions will cover non-tonal melodic reading and complex chord structures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC376','Composition Seminar','Creative and critical potential is developed through supervised practice, tutorials and seminars. Free composition, style emulation, arranging and orchestration will be dealt with.');
