INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC491','Honours Research Seminar','A research seminar for Honours Music students designed to give students an opportunity to develop research techniques through individual research projects and regular meetings with other students and faculty.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','MUSIC492','Senior Honours Thesis','An independent research project for Honours Music students. The topic is determined through consultation between the student and a faculty advisor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS101','Peace is Everybody's Business','This course explores ways in which individuals and groups from multiple sectors of society have contributed to peace by engaging conflict constructively and advancing social justice. Attention will be given to the diversity of peacemaking approaches, and to interpersonal and intergroup as well as international applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS201','Roots of Conflict, Violence, and Peace','An examination of influential theories about the sources and nature of conflict, violence, and peace. Contributions from the social sciences as well as the humanities will be explored, with attention to connections between interpersonal, intergroup, and international levels of analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS202','Conflict Resolution','An examination of the resolution of conflicts, ranging from interpersonal to broader social and international conflicts. Students are introduced to negotiation, mediation, and nonviolent resistance, and are encouraged to develop their own theoretical understandings that aid in addressing conflict.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS203','A History of Peace Movements','A survey of individuals and groups that have created popular movements for peace globally and locally throughout history. The scope will be international, with a particular focus on the nineteenth and twentieth century movements. The choice of peace movements will allow for a contrast in comparison of ideology, strategy and impact.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS301','Special Topics in Peace and Conflict Studies 1','A seminar course investigating special issues related to peace and conflict. Content may vary from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS302','Special Topics in Peace and Conflict Studies 2','A seminar course investigating special issues related to peace and conflict. Content may vary from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS311','Doing Development: Issues of Justice and Peace','This course introduces a variety of theoretical and practical perspectives on international development. It examines current and alternative development programs in terms of their contribution to justice and/or peace at local, national, and global levels.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS312','Quest for Peace in Literature and Film','A study of works of literature and film which express a resistance to war. The course examines how the desire to articulate an anti-war position has engaged the artistic sensibilities and shaped the visions and modes of expression of selected writers and filmmakers.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS313','Community Conflict Resolution','An analysis of the growing use of mediation and other conflict resolution strategies in community conflicts, race relations, church disputes, and alternatives to the legal system. The course focuses on case studies with attention to both practical and theoretical issues.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS314','Conflict Resolution in the Schools','This course examines the potential for utilizing the principles of mediation and conflict resolution in school administration, curricula, teaching, classroom management, and school counselling.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS316','Violence, Non-violence, and War','An exploration of the traditional debates concerning the legitimacy of violence and war as instruments in the pursuit of personal and political goals. The course critically examines a continuum of views from religious doctrines of non-resistance, to various forms of pacifism and non-violent resistance, """"just-war theory"""", and political realism. The strategic arguments for political non-violent action are also considered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS318','Peace-building, Human Rights, and Civil Society','This course will examine the close causal connection between violations of human rights and violent conflict/war. It will also analyse the role of human rights and civil society principles in forging or consolidating peace. Course work will include case studies, assigned readings, class participation, and simulation exercises.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS320','Christian Approaches to Peacemaking','Current Christian approaches to peacemaking in areas of conflict: war and militarism, crime, poverty, racism, and gender relations. Attention will be given to various biblical, theological, and historical bases for these approaches.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS321','Gender in War and Peace','An examination of various themes in the history of peace and war using gender as a central category of analysis. Theoretical literature and international case studies will be used to explore how the discourse and enactment of war and peace are influenced by societal constructions of gender, both historically and in the present.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS323','Negotiation: Theories and Strategies','This course explores different ways of negotiating between people and groups with conflicting interests. You will learn the theory behind the strategies and develop practical negotiation skills you can put to use in your daily life at home, at work, and in the community.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS324','Human Rights, Peace, and Business','This course will explore the tension between the values of human security/human rights and traditional economic policy. The impact of this dynamic relationship on the well-being of individuals as well as on corporations and international economic institutions to promote peace and just development will also be analyzed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS325','Conflict Management for Technical Professions','Like all professionals, persons engaged in technical professions are frequently confronted by conflict or by strong opposing interests that the professional must manage in order to accomplish his/her objective. This course will explore the types of conflicts that can arise in the technical professions and provide tools that will enable effective responses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS326','Religion and Peace-Building','This course investigates the roles of religion and spirituality in peacemaking, exploring both obstacles and opportunities facing religious peace-builders. Cases involving representatives of major world religions (Christianity, Islam, Judaism, Hinduism, and Buddhism) will provide a basis for comparing practices of faith-based advocacy for social justice, reconciliation, and coexistence.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS327','Cultural Approaches to Conflict Resolution','Cultural differences enrich our world while also creating bases for disagreement and misunderstanding among individuals, social groups, and nations. This course explores the cultural dimensions of conflict and conflict resolution, shedding light on major patterns of human difference and their implications for contemporary peacebuilding practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS328','Fair Trade','This course examines the history, theory, and practice of fair trade and how it differs from traditional principles of international trade. It will specifically explore the impact that the fair trade movement has had on small scale producers, consumers, and more broadly on the global economic system.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS329','Restorative Justice','This course investigates the history, theory, principles, practices, and people of restorative justice. Content will centre particularly on restorative justice as a way of dealing with crime and interpersonal violence in the Canadian context.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS330','War and Peace in Christian Theology','Christian teachings on war and peace from the early church to the present, including crusade, just war, and pacifist traditions, focusing especially on the 20th century discussion around realism, just revolution, nuclear pacifism, and non-violent resistance.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS331','Trauma, Healing and Conflict Resolution','This course examines how the conflict resolution process can be impacted by trauma. With case examples from armed conflict to family violence, participants explore the emotional, physical, and relational aspects of conflict to better understand the potential for interventions that promote peace and justice. Studies include causes, types, and impacts of trauma; cycles of interpersonal and societal violence; frameworks for healing; and exploration of apology, reconciliation, revenge, and forgiveness.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS390','Field Studies in Peace and Conflict','An independent study course requiring reading, research, and a paper on issues related to the application of peace and conflict studies theory within a field setting, either in Canada or abroad.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS391','Conflict Resolution Skills','This course provides a framework for students to pursue conflict resolution skills training by completing intensive skills-building workshops in the Certificate Program in Conflict Management with additional academic work supervised by Peace and Conflict Studies faculty. Offered on a credit/no credit basis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS395','Peace and Conflict Studies Travel Course','A travel seminar of approximately three weeks in length, taught on location, which will involve travel to a region that has experienced conflict. Student preparation, in the form of readings and/or written assignments, will be required prior to departure. Regular lectures and interaction with persons who have worked for peace will be scheduled. Hands-on examples merging theory with practice will be emphasized. Possible locations include the Middle East, Central/South America, Africa, Asia.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS398','Directed Readings in Peace and Conflict Studies','Students may arrange independent studies in the area of peace and conflict studies on problems of special interest.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS399','Directed Readings in Peace and Conflict Studies','Students may arrange independent studies in the area of peace and conflict studies on problems of special interest.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS401','Senior Research Seminar','Each student will work on an integrative research project and will meet regularly with other students and faculty for discussion.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('CGC','Conrad Grebel','PACS402','Senior Research Seminar','A continuation of PACS 401. Each student will work on an integrative research project or projects and will meet regularly with other students and faculty for discussion.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH100','An Introduction to Architecture','An introduction which explores in broad terms the nature of architecture and how it has evolved. The main streams in the development of Western architecture are traced up to the present and lead to a discussion of current work, ideas and concerns.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH110','Visual Communication 1','Introduction to the use of graphic media in architecture. Students will engage in exercises in drawing using various media, and traditional forms of architectural presentation. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH113','Visual Communication 2','Introduction to computing techniques in architecture. Students will engage in the use of the desktop computer in architectural practice. They will be instructed in the conceptual foundations for computer use in architecture, graphic applications for the computer and basic skills for two-dimensional drawing and three-dimensional modelling. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH114','Visual Communication 3','This course will elaborate upon the graphic and communication conventions established in the fundamental architecture curriculum. Intermediate investigations in two dimensional representation and photographic manipulation are developed to enhance presentation skills within a professional and academic context. Building upon the fundamentals of CAD, the course further examines digital media as a design tool at high and low fidelity levels. The topics covered in the course culminate in the generation of an architecture portfolio. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH125','Principles of Environmental Design','An introduction to the environmental aspects of architectural design and to an analysis of the form that landscapes take and the processes and ideals leading to those forms. Topics of discussion include environmental concepts and influences on design, site planning, landscape, sustainability, embodied energy, climatic influences and microclimates.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH142','Cultural History 1: Iconography','Selected schemes of order, such as fate, providence, natural law, the human will, as expressed in plays, poems and fiction from various ages; selected conventions in literature, cinema, and the visual arts; the development of one or two archetypal symbols in literature and the visual arts; directed to lead into more detailed studies of symbolic patterns in Iconography 2. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH143','Cultural History 2: The Ancient World','A study centred on ancient life to initiate the student into the stream of cultural history and the complex problems of what the artist is, the quality of human existence, culture, and environment, as well as the working of the icon from a raw state of perceived image to its function as an expressive symbol in poetry, music, dance, architecture and other works of art; a study of modern work in comparison to ancient achievement. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH172','Building Construction 1','A focus on the construction of small scale buildings will introduce the fundamentals of building construction demonstrating relationships between design development and: building techniques, materials, building science and construction practices, factors of environment, climate and geology. Case studies and projects for small-scale buildings to investigate: climate, solar geometry, soils, foundations, wood frame, stone and masonry construction. The detailed technical design of a small building as the major term project will be undertaken.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH173','Building Construction 2','An emphasis on the construction of medium to large scale building will examine relationships between design development and the building science and construction practices of structural systems and enclosures. Case studies and projects will be used to investigate: reinforced, precast and prestressed concrete construction; steel framing systems; building envelopes (curtain wall, window walls, glazing and roofing systems); fire protection; interior finish selection. The detailed technical design of a small building as the major term project will be undertaken.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH174','Experimental Courses','These courses offer a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH175','Experimental Courses','These courses offer a vehicle for introducing additional electives to the program on a short term basis, and for developing future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH192','Design Studio','Development of the means to appreciate the art and science of building; introduction to the study of theories of architecture; development of skills in graphic communication; introduction to a study of building elements; promotion of the application of theory in the practice of design.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH193','Design Studio','Further development of basic skills, and the application of theory and design in small scale architectural design projects. Introduction to issues of inhabitation, program and context. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH215','Digital Tools for Industrial and Graphic Design','This course will elaborate upon the graphic and communication conventions established in the fundamental architecture curriculum through a series of assignments ranging from conventional architectural techniques to introductory principles of graphic and industrial design. The intention is to: cultivate an understanding of fundamental concepts and techniques in leading vector and raster-based graphic tools for design development and presentation; to develop an understanding of the paradigm shift from digital media as a representation tool to that of design development; to expand the depth and breath of skills necessary for modern design industry; and to apply design techniques and technologies to and from industrial and graphic design. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH226','Environmental Building Design','This study of building construction and design examines relationships between design development and environmental building practices. Case studies, testing exercises, and projects will be used to investigate: solar geometry, influences of climate, regional circumstances, sustainability, vernacular building practice, daylighting, and passive design. Energy related issues will be addressed and energy based software design programs will be introduced. The detailed design of an energy efficient/passive solar building as the final term project will be undertaken.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH246','Cultural History 3: Foundations of Europe','Recognition of patterns of life and concepts of order and conduct, models of the universe and other, moving metaphors and myths by means of study of the thoughts, acts, art, architecture, technology, literature, music and town design of the West from the break-up of the Roman Empire until the Renaissance. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH247','Cultural History 4: Renaissance to Revolution','Analysis of the various styles emerging out of provincial and international Gothic, especially Italian use of classical models, the spread of this Renaissance mode, leading to consideration of the Mannerist, the Baroque, the Rococo, the Neoclassical; investigation of the course of attitudes from humanism, nationalism, and Reformation through the Enlightenment until the French Revolution and Hume''s dethronement of Reason. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH252','Creative Problem Solving','Development of creative skills through group behaviour in problem solving sessions by: developing a clear understanding of each participant''''s own creative thought processes; increasing the ability to consciously and deliberately make use of one''''s own creative potential; engendering an awareness of the capacity to use active collaboration between self and colleagues to produce better solutions to the problems identified by the group.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH256','Introduction to Photography','Introduction to the main concepts in creating and using photographic images. This will be accomplished in the context of various academic applications including site and model documentation, portfolio, and thesis presentation. The course will include both analog and digital procedures but with a heavy emphasis on monochrome silver images. A limited number of analog and digital cameras are available on a loan basis. Materials at student''s expense.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH260','Principles of Structures','Fundamental concepts of mechanics and structures, as related to architectural design, study of loading conditions, forces, moments, systems of forces, conditions of equilibrium for two and three dimensional structures, centre of gravity of loads and areas, bar forces in trusses, simple frame analysis, moment of inertia. Concepts of simple stress and strain; shear and bending moments in simple beams; shear and moment diagrams, qualitative deflected shapes, flexural and shearing stresses, deflection calculations; compression members; Euler''s formula. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH272','Interior Environments: Acoustics and Lighting','A detailed study of the interior environments of buildings focusing on acoustics, artificial source lighting and day lighting. The course also addresses building services such as electrical distribution, vertical transportation and exterior site services. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH273','Environmental Systems','A focus on the air and water systems of buildings with an aim to developing knowledge and skills appropriate to architectural practice. Subjects covered include environmental parameters, heating and cooling loads, energy conservative design, the selection of heating, ventilating and air conditioning systems, plumbing systems, and fire protection criteria and systems, with reference to building codes and standards. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH274','Experimental Course','These courses offer a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH275','Experimental Courses','These courses offer a vehicle for introducing additional electives to the program on a short-term basis, and for developing future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH276','Timber: Design, Structure and Construction','Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural timber systems. Topics such as flexural, compression and truss members, connections, and plywood construction are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH277','Timber: Design, Structure and Construction for Engineers','Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural timber systems. Topics such as flexural, compression and truss members, connections, and plywood construction are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH284','Architectural Research','This offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum, guided exploration of specific architectural problem areas, of appropriate complexity to the particular term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH285','Architectural Research','This offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum, guided exploration of specific architectural problem areas, of appropriate complexity to the particular term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH292','Design Studio','The exploration of design as a thinking process through the medium of small scale design projects. The development and analysis of architectural propositions concerning personal space within the context of a larger community.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH293','Design Studio','Design involving problems of human perception and dimension in more complex environments, and dealing with issues of public and private space. Development of skills in analysis and programming, and further exploration of questions of siting and context.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH314','Digital Design','This intermediate level course provides a more in-depth theoretical foundation in architectural design by computer, including three-dimensional modelling, light and colour, rendering, image processing and animation. Practice is provided by weekly tutorial exercises and a small term design project.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH327','Architecture of the Urban Environment','An introduction to the structure and form of urban environments as understood through the urban architecture. The forces that determine the creation and development of urban places will be examined. Topics include: the plan as a generative form, urban building types, urban morphology and the shape of the public realm, infrastructure as both system and architectural object, nature and the park, and real estate and development controls. Of special interest will be analyses of the suburb and urban master plans. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH328','Approaches to Architecture and Urbanism','The course develops a trans-disciplinary overview to design approaches in architecture and urbanism using image- and symbol-making as key activities in designing environments. The course evaluates the quality of results and the quality of process of design of environments. The students address topics within a wide range of subject matter groupings, from sustainable architecture to engineering, landscape and infrastructure, to branding and aesthetic practices, to design within the municipal political forum. Students present seminars on diverse categories of design as a means to develop a personal design ethos. The final submission includes a set of drawings, diagrams and images that represent a set of design principles allowing students to consciously take a position in the world as a designer. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH331','Working with Wood','This course focuses on understanding the premise that hand skills can facilitate and help inform conceptual and academic skills. The content provides a broad based introduction to woodworking equipment, its fabrication methods and methodology. A combination of lectures and hands on experiences are used to equip students with the basic skills and knowledge required to conceptualize, fabricate and finish a designed object from wood. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH332','Design/Build Workshop','A design/build workshop which offers opportunities for hands-on experience in three-dimensional design. Advancements of technical and design skills provide the underpinnings for the projects. Students are encouraged to explore a variety of media and techniques such as woodworking and metalworking, allowing for both individual and small team investigations. A logbook will be kept to record creative intentions and the design process from conception to completion. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH342','Modern Architecture','An overview of the individuals and movements associated with the birth of modern architecture in the nineteenth and early twentieth century. The social, technological and aesthetic ambitions of the modern project are critically reviewed and its development and dissemination in Europe, the Americas and Asia is traced. Selected works are examined in depth as examples of the canonic and variant forms of modernism. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH343','Enlightenment, Romanticism and the 19th Century','Depiction of modern culture as one in which the notion of environmental order as the fulfilling of natural law is replaced by a notion of order as the historical creation of autonomous wills. Selected works in philosophy, literature, art and architecture will be studied. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH345','Architectural Theory 1850-1990','Beginning with the introduction of important theories of architecture in vogue prior to 1850, this course examines texts, movements, buildings, projects, and urban proposals of the period in order to understand the structure of contemporary architectural theory.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH346','Competitions in Architecture','This course provides an opportunity for the student to independently engage in the respected tradition of the Architectural Competition. The competition entry and accompanying research paper must focus on the use of architectural precedents as the basis for the creation of typologically based propositions. Submission to the external competition is mandatory, the timing and detailed requirements of which will determine the personalized academic requirements for this course. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH347','Philosophy in Architecture','The course introduces the study of philosophical issues as they pertain to architecture, urbanism, image-making and symbol manipulation. The course focuses on the work of a selection of living and recent philosophers whose issues are relevant to questions of everyday 21st century life, with reference to their basis in the history of philosophy. Work by philosophers such as Giorgio Agamben, Kwame Anthony Appiah and Judith Butler on ethics, Gilles Deleuze on meaning, Friedrich Kittler on media and technology, Jean-Luc Nancy on community and Slavoj Zizek on psychoanalysis and political economy, as well as Canadian scholars such as Marshall McLuhan and Harold Innis is addressed. Students participate in a seminar format with a series of presentations linking their design projects with philosophical issues, and submit a final project making use of text and diagrams. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH362','Steel and Concrete: Design, Structure and Construction','Architectural case studies are used to examine conceptual development, structural design, building process and the selection of structural steel and concrete systems. Topics such as tension, flexural and compression members; and connections are studied using calculations, design aids, rules of thumb and the latest CSA design standards. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH364','Building Science','The physio-technical factors that influence building design for performance: durability, efficiency, health and sustainability will be explored. Common building design construction problems, their causes and solutions, will be examined with the aid of case studies. Using the principles of building science, good details of masonry, wood, steel and glass will be developed. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH365','Structural Design Build Workshop','This course builds on the principles learned in the previous structural design courses to the field of application. Students will design, construct, and test full size prototypes of chairs and other structures of limited scale. Performance will be predicted and analyzed in the wake of testing. A full structural report is required. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH374','Experimental Courses','These courses allow for additional electives to the program on a short term basis, and for developing future permanent courses.');
