INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ARCH484','Architectural Research','This offers a student an opportunity for independent research into architectural problems not offered in the regular curriculum. It allows guided exploration of a specific architectural problem area, of appropriate complexity to the particular term.');
