INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE420','Introduction to Process Control','Laplace transform techniques. Proportional-integral-derivative control. Frequency response methods. Stability analysis. Controller tuning. Process control simulation and computer control systems. Process identification. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE480','Process Analysis and Design','Development and analysis of process flowsheets and chemical product design. Design and selection of common process equipment such as heat exchangers, pumps, piping, staged separations. Incorporation of pollution prevention and inherently safer design principles. Equipment and project cost estimation. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE482','Chemical Engineering Design Workshop','In this course, students study the design process including: problem definition and needs analysis; process synthesis, process debottlenecking and troubleshooting; safety and environmental protection in design; written and oral communication for design reports. A significant portion of the term work will be devoted to a group design project, culminating in a design proposal that will be presented to the department. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE483','Group Design Project','Student design teams of two to four members work on design projects of industrial scope and importance under the supervision of a faculty member. The projects are a continuation of those initiated in CHE 482. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE490','Chemical Engineering Lab 5','A selection of computer and laboratory exercises refreshing and reinforcing material covered in the previous term. Topics may include: reaction kinetics and reactor engineering, heat and mass transfer unit operations, numerical methods, principles of design and safety. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE498','Directed Research Project','Directed research project under the supervision of faculty members. Participation will give students experience in advanced research techniques, with valuable training for those potentially interested in graduate school or industrial research careers. Taken over and above normal course load. Good standing and permission of department required for registration.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE499','Elective Research Project','A major undergraduate research project carried out as a technical elective (TE) under the supervision of a faculty member. An oral presentation of results and a written report are the minimum requirements. Other requirements may be set by the faculty supervisor or department. [Offered: F,W,S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE500','Special Topics in Chemical Engineering','Special courses on advanced topics may be offered from time to time, when resources are available. For current offerings, inquire at the CHE undergraduate office.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE514','Fundamentals of Petroleum Production','Background for understanding the physical principles involved, and the terminology used, in petroleum production. Fundamentals of surface chemistry; capillarity. Characterization of, and fluid flow through, porous media. Principles of oil production performance, water flooding and enhanced oil recovery techniques. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE516','Energy Systems Engineering','Energy systems in society. Review of fossil fuel reserves, production and consumption trends, including unconventional sources such as shale gas, and oil sands. Transportation fuels and alternative fuels; the design of hybrid power trains, fuel cells and batteries. Design considerations of carbon-free energy generation and carbon-neutral technologies, including nuclear, wind, solar and bio-energy. Design of energy storage on a ''''utility scale'''' and portable power sources. Applications of thermodynamics in the design of energy systems, including the Rankin and Brayton cycles. Other energy system topics as appropriate. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE522','Advanced Process Dynamics and Control','State space methods. Sampled-data systems. Discrete systems. Transform methods. Multivariable control. Computer control. Closed-loop analysis. Design of controllers. Control of complex chemical systems. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE524','Process Control Laboratory','Experiments on process dynamics, control and simulation of processes. Time constant; step and frequency response; controller tuning; multivariable control strategies. Implementation using simulation systems, mainframe computer control, microcomputers. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE541','Introduction to Polymer Science and Properties','An introduction to principles governing polymerization reactions and the resultant physical properties of polymers. Molecular weight distribution. Crystallinity. Step-growth and chain-growth polymerization and copolymerization. Selected additional topics in polymer characterization/ properties. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE543','Polymer Production: Polymer Reaction Engineering','Mathematical modelling and polymer reactor design. Physical properties and rheological behaviour of the polymeric, glassy and rubbery states. Polymer solution properties. Selected additional topics in specialty polymerization techniques for branched systems and nano-materials. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE562','Advanced Bioprocess Engineering','Application of process engineering principles to the design and operation of fermentation reactors which are widely used in the pharmaceutical, food, brewing and waste treatment industries. Aspects of mass transfer, heat transfer, mixing and rheology with biochemical and biological constraints. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE564','Food Process Engineering','Applications of unsteady and steady state heat and/or mass transfer operations to processing natural and texturized foods. Design and analysis of sterilization, low temperature preservation, concentration, separation and purification processes. Effects of formulation, additives and processing on organoleptic and nutritional quality. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE571','Industrial Ecology','Industrial Ecology is a rapidly growing field that systematically examines local, regional, and global uses and flows of materials and energy in products, processes, industrial sectors, and economies. It focuses on the potential role of industry in reducing environmental burdens throughout the product life cycle from the extraction of raw materials to the production of goods, to the use of those goods and to the management of the resulting wastes. This course will review the environmental issues associated with chemical industries and the roles of engineers to manage these issues. The principles and philosophy of green chemistry will be addressed including pollution prevention in unit operations. The concepts and practices of environmental life cycle analysis and accounting will be addressed in detail, together with the basics of risk assessment, management and communication. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE572','Air Pollution Control','Nature and sources of air pollutants. Transport of pollutants and dispersion modeling for regulatory purposes. Design of industrial particulate capture systems using cyclones, electrostatic precipitators, filters, scrubbers. Design of organic compound emissions control using incineration, biofiltration, adsorption and absorption. Overview of NOx and SOx control. Indoor air quality assessment techniques. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CHE574','Industrial Wastewater Pollution Control','Primary focus is on the control and treatment of inorganic aqueous waste from chemical process industries. Waste minimization methods with specific examples such as rinsewater circuit design. Principles and design of treatment methods: chemical treatment, precipitation, coagulation and flocculation, ion exchange and membrane separation. Treatment of organic aqueous waste. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE121','Digital Computation','Introduction to electronic digital computers, hardware and software organization, examples of efficient numerical algorithms for basic scientific computations. Programming and problem solving concepts introduced in the course will be incorporated into group projects involving Civil, Environmental, or Geological Engineering applications. The language of instruction will be Visual Basic and Visual Basic for Applications. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE125','Civil Engineering Concepts 1','An introduction to some of the basic methods and principles in Civil Engineering. The fundamentals of engineering calculations: units and dimensions. Surveying, data collection, measurement and error analysis. Laboratory on visual communication: engineering graphics including projections, computer software including spread sheets, computer aided design. Introduction to engineering design. Technical communication: word processing software, elements of technical report writing. Aspects of the engineering profession including ethics, safety, and intellectual property. Professional development including rsum skills, interview skills, and preparation for co-op terms. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE127','Statics & Solid Mechanics 1','Two-dimensional force systems, moments, couples, and resultants. Two-dimensional equilibrium problems including trusses and frames. Distributed forces, centroids and moment of inertia. Stress-strain-temperature relationships. Behaviour of prismatic members in tension, compression, shear, bending and torsion. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE153','Earth Engineering','This course studies earth materials and processes from an engineering point of view through case histories and problem sets. The course develops a geological knowledge for applications to any physical environment and provides an appreciation of the impact of engineering work on the environment. Topics include: mineral and rock identification, the rock cycle, structural geology and tectonics, geology of Canada, effects of water, ice and wind. Students are also introduced to the concept of geologic time, topographic and geologic maps, and the basic principles and tools used to determine geologic history. [Offered: S; Offered as: CIVE 153 (W), ENVE 153 (S), GEOE 153 (S)]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE199','Seminar','The engineer society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE204','Statics and Solid Mechanics 2','Three-dimensional force systems, moments, couples, and resultants. Three-dimensional equilibrium problems. Friction. Thin-walled pressure vessels. Torsion of shafts and thin-walled closed sections. Shear, bending moment, and deflection diagrams for beams. Compound stress and stress transformations. Design concepts. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE205','Mechanics of Solids 2','Frames, arches and suspended structures. Strain energy. Energy methods. Virtual work. Maxwell-Betti theorem. Influence lines. Force and displacement methods for single members. Buckling of columns. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE221','Advanced Calculus','A review of Year One Calculus. Hyperbolic Functions. Partial derivatives. Multiple integration with applications. Vector analysis, theorems of Green and Gauss, line integrals. Elements of Fourier series. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE222','Differential Equations','An introduction to linear and partial differential equations. Standard methods of solution, applications to physical and engineering problems, linear equations with constant coefficients, systems of differential equations, solution by series, numerical methods, partial differential equations. Applications from Dynamics and Vibrating Systems. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE224','Probability and Statistics','Role of Probability in engineering and decision-making under uncertainty. Data analysis. Basic probability concepts. Probability distributions. Functions of random variables. Estimation theory. Empirical determination of distribution models. Regression analysis. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE240','Engineering and Sustainable Development','This course explores the concepts of sustainability, namely the balancing of economic, environmental, social, cultural, health and political needs, as it pertains to Civil Engineering decisions. The course examines aspects of urban transportation and infrastructure planning, land-use, and issues related to water, air, and noise pollution. Methods of quantifying costs associated with health risks and consumption of non-renewable resources are presented. Case studies from a range of Civil Engineering application areas are used to examine the effect of engineering decisions on sustainability. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE265','Structure and Properties of Materials','A basic course in structure, behaviour and uses of engineering materials. Topics include monotonic and cyclic stress-strain behaviour of metals. Phase diagrams. Diffusion, nucleation and growth of grains. Metallurgy and mechanical properties of irons and steels. Structure and mechanical properties of wood, cements and concrete. Fracture, fatigue and corrosion. Three lab sessions. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE280','Fluid Mechanics and Thermal Sciences','An introduction to fluid mechanics and thermal sciences. Fluid properties. Fluid statics. Thermodynamic principles. Bernoulli equation. The momentum equation and applications. Laminar and turbulent flow. Dimensionless numbers. Closed conduit flow. Pipe network analysis. Steady flow in pipes. Heat transfer. Four lab sessions. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE291','Survey Camp','Introduction to surveying, length measurements, levelling, transit surveys.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE292','Engineering Economics','An introductory course on the principles of engineering economy. Basic concepts. Capital. Interest formulas and derivations. Annual worth comparisons. Present worth. Return on investment. Benefit-cost ratio depreciation. Effect of taxes. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE298','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE299','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE303','Structural Analysis 1','Analysis of statically indeterminate structures using force and displacement methods. Maxwell-Mohr, slope-deflection, and moment distribution methods. Influence lines for indeterminate structures. Matrix formulations. Computer applications. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE306','Mechanics of Solids 3','Membrane stresses in shells. Buckling. Beams on elastic foundations. Plane elasticity. Torsion of non-circular sections. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE313','Structural Concrete Design 1','Reinforced concrete members. Concrete and reinforcing steel materials. Safety, loads, design criteria. Flexure, shear, combined bending and axial force. Serviceability. One-way slabs, beams, columns, foundations and retaining walls. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE331','Advanced Mathematics for Civil Engineers','This course is an integration of CIVE 121, CIVE 221, and CIVE 222 in which both classical calculus theory and basic computational algorithms were discussed. Partial differential equations (PDEs) with application in the modelling of civil engineering processes (e.g., wave, diffusion, Laplace and Poisson equations). Boundary and initial conditions. Numerical integration. Numerical interpolation schemes for irregularly spaced spatial data (e.g., splines, Lagrange polynomials, etc). Solution methods for linear and non-linear systems of algebraic equations. Numerical solution of PDEs using the finite difference method. Aspects of the finite element method. An emphasis will be placed on algorithm development and implementation. Maple and Visual Basic will be integral tools in this course. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE332','Civil Engineering Systems','Introduction to conceptual planning, optimization and life-cycle performance assessment of civil engineering systems. Fundamentals of decision analysis and concepts of risk, uncertainty, utility and probability theory. Tools for supporting decision making process, namely, linear programming, network models, variational methods and optimization, dynamic programming, Monte Carlo simulation, and first-order reliability theory. Risk-based models for condition assessment, inspection, rehabilitation and replacement of infrastructure systems. Risk- and cost-benefit analysis of public projects and their impact on sustainability and quality of life. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE342','Transport Principles and Applications','Introduction to basic principles and procedures of transport planning and engineering applied to Canadian intercity transport problems. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE343','Traffic Engineering','A comprehensive introductory course to traffic engineering and control. Topics include: vehicle - driver - roadway environment; theories of traffic flow; application of queuing theory, capacity and delay analysis of unsignalised and signalised intersections; design optimisation of isolated and co-ordinated traffic signal timing plans; traffic simulation model calibration and application; and field data collection and analysis. State-of-practice analysis and design methods are examined and applied. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE353','Geotechnical Engineering 1','An introduction to geologic processes. Subsurface exploration. Classification systems. Weight-Volume relationships. Soil mechanics principles including state of stress, ground water flow, consolidation and shear strength. Six lab sessions. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE354','Geotechnical Engineering 2','Foundation engineering. Earth pressure theories. Retaining walls. Anchors. Shallow and deep foundations. Braced trenches and excavations. Slope stability. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE375','Water Quality Engineering','Water sources and use. Characteristics of water: physical, chemical, and bacteriological parameters. Water quality management. Solid and hazardous waste management. Biodegradable waste disposal in streams. Water and waste treatment systems: sedimentation, biological treatment theory, design principles. Six lab sessions. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE381','Hydraulics','Energy, momentum and continuity equations for open channel flow. Dimensional analysis and modelling. Design of lined and unlined open channels. Water profile computations. Bridge and culvert hydraulics. Hydraulic structures and energy dissipators. Pumping stations. Water hammer. Four lab sessions. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE398','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE399','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: F, W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE400','Civil Engineering Project 1','Students must undertake an independent Civil Engineering design project during the last two terms of their program. The purpose of the project is to demonstrate students'' abilities to practise in a Civil Engineering capacity in their chosen area of expertise, using knowledge gained from their academic and employment experiences. The first part of the project (CIVE 400) will include problem identification, generation and selection of solutions and time management. Incorporation of technical and economic issues in the solution for the project will be required. If applicable, ecological, social and political issues must also be considered. A basic requirement of the proposed solution is that it must be compatible with the principles of sustainability. Requirements include: proposal, progress report, oral presentation and a final report containing recommendations for part two of the project, CIVE 401. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE401','Civil Engineering Project 2','A continuation of CIVE 400. The final design of the major Civil Engineering project proposed in CIVE 400 will be undertaken. The purpose of this phase of the project is to carry out a detailed technical design of the solution proposed in CIVE 400. Requirements of this part of the two-term project include an oral presentation and a final report. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE403','Structural Analysis 2','Advanced structural analysis; linear and nonlinear behaviour. Computer applications. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE405','Structural Dynamics','Dynamics of continuous and discretized structures. Free and forced vibrations of single and multidegree of freedom systems. Impact, earthquake loads, wind loads. Vibration of beams, frames, structural systems. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE413','Structural Steel Design','Structural steel members. Limit states design, loads, materials. Design of tension and compression members, beams and beam-columns. Plate girders. Connections. Fatigue. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE414','Structural Concrete Design 2','Reinforced concrete members and structures. Torsion. Slender columns, walls, continuous beams, floor systems. Prestressed concrete. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE415','Structural Systems','Geometries, loads, safety and serviceability, structural idealizations. Building design and bridge design. Proportioning of components and structures in concrete, steel, masonry and wood. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE422','Finite Element Analysis','This course focuses on the development of the basic fundamentals of the finite element method with applications in fluid flow, mass transport, solid mechanics and structures. Topics include: discrete problems, matrix methods, variational principle, method of weighted residuals, element shapes, and interpolation functions. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE440','Transit Planning and Operations','The historical evolution of transit in cities; the technological innovations which made transit possible; and transit mode definitions. Models of transit vehicle motion are presented; transit travel times under different travel regimes are derived. Transit scheduling methods are shown. System operational characteristics are defined and quantitative measures of effectiveness are introduced. Transit network planning objectives are identified; actual geometries are qualitatively and quantitatively analyzed. Transit ownership structures and economics are discussed; contemporary ITS applications are presented. Methods for selecting appropriate transit modes are covered. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE444','Urban Transport Planning','The course develops a number of standard methods for predicting travel in urban areas. General characteristics of urban travel and urban transport systems are presented along with a discussion of typical issues pertaining to urban areas. Methods used to evaluate alternatives and resolve issues are presented. These include trip generation, trip distribution and mode split. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE460','Engineering Biomechanics','Introduction to engineering technologies applicable to the field of biomechanics. Specific topics covered may include biological growth, form and function; biomaterials; kinematics and neurology of gait; biotribology; joint anatomy, function and repair; occupational biomechanics; trauma prevention. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE486','Hydrology','Basic components of the hydrologic cycle. Introduction to frequency analysis and time series analysis. Rainfall-runoff relationships. Unit hydrograph theory. Hydrologic and hydraulic routing. Introduction to hydrologic design: design storms and storm water management. Rural and urban simulation models. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE491','Engineering Law and Ethics','Background (Charter of Rights and Freedoms), Contracts, Torts (Negligent Malpractice), Forms of Carrying on Business, Professional Practice (Professional Engineers Act, Joint Practice Rules, Professional Misconduct and Sexual Harassment), Alternate Dispute Resolution, Construction Liens, Intellectual Property (Patents, Trade Marks, Copyrights and Industrial Designs), Labour Relations and Employment Law, Environmental Law. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE497','Special Topics in Civil Engineering','A special course on advanced topics in Civil Engineering is offered from time to time, when resources are available. For the current offering, inquire at the Department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE498','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: F, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE499','Seminar','The engineer in society. Principles, methods and practice of Civil Engineering. Informal lectures. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE507','Building Science and Technology','The building process. Loadings: gravity, wind, thermal, moisture, fire. Enclosure design: walls, windows, roof. Subgrade construction. Energy related considerations. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE512','Rehabilitation of Structures','This course deals with the assessment, rehabilitation and/or strengthening of building and bridge infrastructures. Topics include damage mechanisms, instrumentation and non-destructive test methods, conventional repair techniques, innovative repair and strengthening techniques with composites. Case studies provide students with the opportunity to learn from field applications. The laboratory portion involves test methods used to evaluate deterioration. Student teams are required to examine infrastructure renewal projects and to develop recommendations for rehabilitation strategies. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE542','Pavement Structural Design','Pavement design, soil identification, subgrade design, base courses, flexible pavement design, design and testing of asphaltic concrete mixes, surface treatments. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE554','Geotechnical Engineering 3','Simulation of geotechnical consulting practice. Students are required to complete several projects, based on actual case studies, which require problem identification, evaluation of geotechnical data, analysis, design and report preparations. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE572','Wastewater Treatment','Wastewater quantity and characteristics. Primary treatment and secondary treatment. Reverse osmosis, ultra filtration, adsorption, air stripping, air flotation, chemical precipitation. Sludge treatment and disposal. Groundwater and leachate treatment. Industrial wastewater management. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE583','Design of Urban Water Systems','Design of water supply and distribution systems. Design of waste and storm water collection systems. Storm water management. The course consists of 24 hours of lectures and a subdivision design project. The emphasis is on computer aided design and sustainability, using commonly used software packages. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','CIVE596','Construction Engineering','Topics in construction management and engineering including non-deterministic computing methods for construction modelling and analysis, network methods, optimization, risk management and resource allocation. Construction methods and trenchless technology. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE153','Earth Engineering','This course studies earth materials and processes from an engineering point of view through case histories and problem sets. The course develops a geological knowledge for applications to any physical environment and provides an appreciation of the impact of engineering work on the environment. Topics include: mineral and rock identification, the rock cycle, structural geology and tectonics, geology of Canada, effects of water, ice and wind. Students are also introduced to the concept of geologic time, topographic and geologic maps, and the basic principles and tools used to determine geologic history. [Offered: S; Offered as: CIVE 153 (W), ENVE 153 (S), GEOE 153 (S)]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE298','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE299','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE398','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE399','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE400','Geological Engineering Design Project 1','Students are expected to carry out a design project to demonstrate their capability to engage in the practice of engineering as a profession. A topic must be identified and resolved by the student in consultation with the supervising professor and course coordinator. The topic may be analytical, numerical, experimental, or field-oriented, utilizing knowledge gained from academic and employment experiences. A written proposal, literature search and an oral presentation are required. Professional engineering standards and a design approach are required for the GEOE 400-401 Design Project. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE401','Geological Engineering Design Project 2','A continuation of GEOE 400 Geological Engineering Design Project 1, and the same standards and requirements apply. A progress report, a final report in thesis format and an oral presentation are required. All other courses in the Geological Engineering Program are listed under the course descriptions in Earth Sciences or Civil Engineering. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE498','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','GEOE499','Seminar','General seminar course covering current topics in Geological Engineering. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE100A','Electrical and Computer Engineering Practice','Areas of research and professional practice in Electrical and Computer Engineering, including the engineer''s role in protecting public safety. Exposure to concepts from other Engineering disciplines. Support material for the academic term, co-operative education, and professional or career development. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE100B','Electrical and Computer Engineering Practice','Areas of research and professional practice in Electrical and Computer Engineering. Exposure to concepts from other Engineering disciplines. Support material for the academic term, co-operative education, and professional or career development.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE103','Discrete Mathematics','Propositional logic, predicate logic, set theory, finite automata, temporal logic. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE105','Physics of Electrical Engineering 1','Forces in nature and Newton''s laws, Dynamics and circular motion, Work, Energy and conservation of energy. Linear Momentum and linear Impulse, Rotational Dynamics. Oscillations; Simple Harmonic Motion. Wave motion; Traveling waves and standing waves. Thermal Physics; Temperature, Thermal energy and Specific heat, Ideal gas heat engines and Refrigerators. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE106','Physics of Electrical Engineering 2','Electrostatics; electric field, flux, Gauss''''''''''''''''s Law, potential and potential energy. Capacitors; Dielectric, capacitance, electric energy storage, charging/discharging. Resistors; charge flow, current, resistance, Kirchhoff''''''''''''''''s voltage and current laws. Magnetostatic; magnetic force, magnetic fields, Ampere''''''''''''''''s Law. Inductors; magnetic flux, inductance, magnetic materials, magnetic energy storage. Time-Varying Fields; Faraday''''''''''''''''s Law, mutual inductance, simple motors and generators. [Offered: W, S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','ECE124','Digital Circuits and Systems','Number systems and Boolean arithmetic. Boolean algebra and simplification of Boolean functions. Combinational circuits. Sequential circuits; design and implementation. Hardware description languages. Timing analysis. Implementation technologies. [Offered: W, S]');
