INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE311','Advanced Engineering Math 2','Series solutions of ordinary differential equations: Bessel functions, orthogonal functions, Fourier expansions and integral, Legendre polynomials, Sturm-Liouville systems. Partial differential equations: parabolic, hyperbolic and elliptic equations, numerical methods. Numerical integration: Gaussian quadrature, Runge-Kutta methods. Nonlinear systems: qualitative analysis, phase plane, Newton-Raphson method. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE312','Applied Linear Algebra','Vector spaces, linear independence, linear maps and matrix representations. Inner product spaces and orthogonality. Gramm-Schmidt algorithm and orthogonal projection. Interpolation and curve-fitting. Eigenvalues and eigenvectors, diagonalization, singular value decomposition. Applications and numerical methods. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE322','Software Design','Software requirements specification; software architecture; design patterns; software testing and quality assurance; software maintenance; design of efficient algorithms and methods for their analysis, mathematical algorithms, string processing algorithms, geometrical algorithms, exhaustive search and traversal techniques, introduction to lower bound theory and NP-completeness. Case studies and engineering examples. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE332','Societal and Environmental Systems','The interactions of human society with its physical environment are complex and multifaceted. The course surveys the mathematics needed to understand such interactions, including dynamic systems, complex and nonlinear systems, self-organizing systems, remote sensing, and statistical inference. The mathematical methods will be motivated by a set of case studies, a systems engineering perspective to investigate pressing large-scale interconnected problems such as global warming, energy shortages, water dead zones, desertification, food crises, overpopulation, poverty and economic instability. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE334','Applied Statistics','Review of basic Normal theory, t, chi-squared, and F distributions. Simple linear regression. Lack of fit. Analysis of variance. Multiple linear regression, variable selection techniques, indicator variables, diagnostics. Brief introduction to non-linear regression, factorial experimentation. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE348','User Centred Design Methods','This course approaches the design tasks, tools, products and systems from a user-centered design perspective. Emphasis is on the human factors and usability methods and techniques that can and should be applied throughout the iterative design process. While design issues pertaining to human-computer interaction are discussed, the methods presented can be applied to the design of almost any user interface. Major topics include: function and task analysis, usability analysis, prototyping and evaluation, user interaction styles, interface design, user designing to guidelines and standards. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE351','Systems Models 1','Introduction to systems modelling and analysis. Graph theoretic models and formulation of system equations. State space formulation and solution. Time and frequency domain solutions. Application to engineering systems. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE352','Introduction to Control Systems','Classical and state space representations of control systems. Stability, controllability, observability and sensitivity. Routh-Hurwitz and root-locus methods. Frequency domain behaviour, Bode plots, Nyquist stability criteria. Pole placement, PID, phase-lead and phase-lag controllers.(labs alt. weeks) [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE352L','Control Systems Laboratory','Laboratory experiments for students taking SYDE 352. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE361','Engineering Design','The methodology of design: defects, needs and the problem definition; criteria and generation of alternative solutions; feasibility analysis; optimization; selection, implementation and solution. The lecture material is supplemented by a term long design project done in small groups. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE362','Systems Design Workshop 1','Engineering design project course where students work in small groups applying the principles of engineering problem solving, systems analysis, simulation, optimization and design to a problem of their own choosing. Students have individual project supervisors as well as an overall coordinator who provides the framework for the term projects. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE372','Introduction to Pattern Recognition','Pattern recognition as a process of data analysis. Pattern features as components in a random vector representation. Classification techniques: distance measures in feature space, probabilistic (Bayesian) decision theory, linear discriminants. Clustering and feature extraction. Applications: optical character recognition, speech recognition, industrial robot vision, medical diagnosis, remote sensing and satellite image analysis, fault detection and diagnosis in complex systems such as nuclear reactors. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE381','Thermodynamics','An introductory course in engineering thermodynamics structured for students in Systems Design. Classical thermodynamics is presented as the systematic study of energy; its use, degradation, and waste. Introduction to heat transfer by conduction, convection, and radiation. Applications focus on problems of energy and environment. The concepts of statistical thermodynamics are introduced. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE383','Fluid Mechanics','Fundamental concepts in systems involving fluid flow. Basic treatment of statics, kinematics and dynamics of fluids. Conservation of mass, momentum and energy for a control volume. Dimensional analysis and similarity. Flow in pipes and channels. Brief introduction to boundary layers, lift and drag, ideal and compressible flow. [Offered: S]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE384','Biological and Human Systems','In this course, students will become familiar with the physiology and anatomical structures of the human body. The structure, functions and properties of the major biological systems (musculoskeletal, nervous, and cardiovascular) will be presented in relation to modeling biological systems and the design of biomedical devices (imaging, assistive and diagnostic). Various aspects of pathology and how they influence measurements will also be introduced. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE401','Seminar','Systems Design fourth-year students will meet with a faculty member designated as their class professor. Conceptual difficulties, the interrelation of course work and engineering practice will be discussed. Non-credit course. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE402','Seminar','Systems Design fourth-year students will meet with a faculty member designated as their class professor. Conceptual difficulties, the interrelation of course work and engineering practice will be discussed. Non-credit course. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE411','Optimization and Numerical Methods','Root-finding methods. Linear programming, simplex and interior-point methods. Local and global optimization methods. Constrained optimization. Multiobjective and multidisciplinary design optimization.[Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE422','Machine Intelligence','The objective of this course is to introduce the students to current intelligent system concepts. An overview of different learning schemes will be provided, including: Decision Tree, Bayesian, Inductive, Analytical and Rule-based Learning. The main focus of the course will be on Neural Nets, Genetic Algorithms and Reinforcement Learning. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE431','Design Optimization Under Probabilistic Uncertainty','Optimization methods for real world problems have to deal with probabilistic uncertainty either due to data uncertainty or manufacturing uncertainty or both. Maximizing the expected value of the objective function subject to reliability (or risk) constraints is commonly used in such design or decision-making problems. Common methods used are stochastic programming, stochastic dynamic programming, chance-constraints, yield optimization and tolerance design. Example applications are selected from water management, energy systems, financial engineering, and manufacturing. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE433','Conflict Resolution','Formal methods for studying engineering decision making problems involving multiple participants and multiple objectives. Topics include the graph model for conflict resolution, normal game form, metagame analysis, games with misperceptions, preference elicitation, human behaviour under conflict, evolution of a conflict, decision making under uncertainty, sensitivity analyses, multiple criteria decision analysis, group decision and negotiation, coalition analysis, decision support systems, and real-world applications of the foregoing concepts. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE444','Biomedical Measurement and Signal Processing','This course develops an understanding of biomedical measurements through the examination of electromyographic (EMG), and electroencephalographic (EEG), electrocardiographic (ECG) signals. Measurement of human-body position, force, and pressure, and related instrumentation will also be presented. Signal processing techniques will be discussed in the context of extraction and application of useful biomedical signals. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE461','Systems Design Workshop 2','The first half of a two term engineering design project continuing the systems design workshop sequence. An interim progress report is presented at the end of the first term. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE462','Systems Design Workshop 3','The concluding half of the fourth year Systems Design Workshop. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE475','Image Processing','Beginning with a discussion of quantitative models of imaging systems, this course moves on to apply methods of linear systems theory and signal processing to image processing. Simple spatial domain techniques as well as spatial frequency domain methods and digital filter design for image enhancement and restoration are discussed. The key methods and problems are surveyed: edge detection, image denoising, image segmentation, image enhancement, image compression, image registration, and feature detection. Applications to machine vision, remote sensing, and medical imaging will be emphasized. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE482','Dynamic Modelling of Biomechanical Systems','This course combines techniques of kinematic and dynamics analysis of mechanical systems and understanding of biological and human systems to provide advanced skills in the modelling of musculoskeletal and biomechanical systems. Topics include kinematics, dynamics, inverse and forward dynamics, modelling and analysis of biomechanical and musculoskeletal systems, mathematical models, physical models, model assumptions, model validation, biomechanical system simulation, and clinical relevance of studies. Resolution of redundant forces by indeterminate methods using optimization approaches will be introduced. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE522','Machine Intelligence','The objective of this course is to introduce the students to current intelligent system concepts. An overview of different learning schemes will be provided, including: Decision Tree, Bayesian, Inductive, Analytical and Rule-based Learning. The main focus of the course will be on Neural Nets, Genetic Algorithms and Reinforcement Learning. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE531','Design Optimization Under Probabilistic Uncertainty','Optimization methods for real world problems have to deal with probabilistic uncertainty either due to data uncertainty or manufacturing uncertainty or both. Maximizing the expected value of the objective function subject to reliability (or risk) constraints is commonly used in such design or decision-making problems. Common methods used are stochastic programming, stochastic dynamic programming, chance-constraints, yield optimization and tolerance design. Example applications are selected from water management, energy systems, financial engineering, and manufacturing. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE533','Conflict Resolution','Formal methods for studying engineering decision making problems involving multiple participants and multiple objectives. Topics include the graph model for conflict resolution, normal game form, metagame analysis, games with misperceptions, preference elicitation, human behaviour under conflict, evolution of a conflict, decision making under uncertainty, sensitivity analyses, multiple criteria decision analysis, group decision and negotiation, coalition analysis, decision support systems, and real-world applications of the foregoing concepts. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE542','Interface Design','This course focuses on the design of computer interfaces for simple to complex systems. Examples of applications will be used to illustrate theoretical approaches. Main topics include: forms of visual display; auditory display and soft controls; context, integration, and navigation; techniques for interface design; ecological interface design. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE543','Cognitive Ergonomics','This course focuses on the role engineering psychology research plays in design of the information displays and devices associated with simple and complex cognitive tasks. Main topics include: signal detection and target location tasks, navigation tasks, training tasks, communication tasks, human error, stress and mental workload, supervisory control, and situational awareness. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE544','Biomedical Measurement and Signal Processing','This course develops an understanding of biomedical measurements through the examination of electromyographic (EMG), and electroencephalographic (EEG), electrocardiographic (ECG) signals. Measurement of human-body position, force, and pressure, and related instrumentation will also be presented. Signal processing techniques will be discussed in the context of extraction and application of useful biomedical signals. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE552','Computational Neuroscience','Introduction to quantitative principles in the analysis of neurophysiological systems. Biophysics of excitable membranes. Encoding of sensory information in neural spiking activity. Bayesian models in perception and motor control. Models of synaptic plasticity, learning, and memory. [Note for Systems Design Engineering students: It is recommended that one of BIOL 273, 376, 377 or SYDE 384 be taken before or concurrently with SYDE 552. Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE553','Advanced Dynamics','Newtonian and Eulerian formulation of particle and rigid body kinematics and dynamics. Energy (Lagrangian and Hamiltonian) formulations of particle and rigid body dynamics; generalized coordinates, generalized forces, holonomic constraints, Lagrange multipliers. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE556','Simulating Neurobiological Systems','This course develops and applies a general framework for understanding neural computation in the context of recent advances in theoretical and experimental neuroscience. Particular emphasis is placed on understanding representation, nonlinear computation, and dynamics in real neurobiological systems. Students will apply signal processing, control theory, linear algebra, probability theory, and similar quantitative tools for the purpose of modelling sensory, motor, and cognitive systems. Topics covered include single neuron function, neural coding, neural dynamics, attractor networks, learning, statistical inference, locomotion, working memory, etc. Familiarity with neural systems is helpful but not essential. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE558','Fuzzy Logic and Neural Networks','Fuzzy systems and neural networks have recently become widely applied to various areas including consumer products, mechatronics systems, industrial process control, information systems, pattern and speech recognition, and prediction of future stock prices to name a few. Fuzzy logic and neural networks share the common ability to improve the decision making process for systems characterized by ill-defined dynamics and working in an imprecise environment. For fuzzy systems this is done through linguistic description of the system by combining fuzzy sets with fuzzy rules following a well-structured numerical estimation procedure. For neural networks, this is done through detecting patterns and relationships from a set of training input-output data gathered from the system, while learning from relationships and adapting to change. The course is mainly intended as introductory material for fuzzy logic and neural networks and outlines the most recent developments in these areas and their applications for intelligent systems design. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENG','Engineering','SYDE575','Image Processing','Beginning with a discussion of quantitative models of imaging systems, this course moves on to apply methods of linear systems theory and signal processing to image processing. Simple spatial domain techniques as well as spatial frequency domain methods and digital filter design for image enhancement and restoration are discussed. The key methods and problems are surveyed: edge detection, image denoising, image segmentation, image enhancement, image compression, image registration, and feature detection. Applications to machine vision, remote sensing, and medical imaging will be emphasized. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS105','Environmental Stewardship and Ethics','This course examines and evaluates the reasons why the compatibility of commercial activity, ethics and environmental stewardship has been challenged or defended by stakeholders in government, business and civil society organizations. Concepts and activities by which stakeholders describe and debate this compatibility will be discussed. Different cultural perspectives on the compatibility debate will be considered, compared and evaluated. Case studies will test whether and to what extent compatibility may be defended adequately. Private and non-profit enterprises will be considered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS131','Communications for Environmental Professions','This course provides an introduction to strategies and tools that enhance the effectiveness and impact of communications for environmental professionals. The course focuses on topics such as effective presentation methods in small or large group settings, digital presentation techniques, media relations, and corporate communications strategies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS178','Introduction to Environmental Research Methods','Introduction to methods of developing, evaluating and using evidence in Environmental Studies. Methods for summarizing and critical appreciation of data describing environmental systems. Skill development in applying statistical techniques and in using microcomputers as a research tool. While not a prerequisite for this course, CS 100 or a high school computing course is helpful.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS195','Introduction to Environmental Studies','Provides an overview of human ecological aspects of environmental studies from an intercultural and global perspective.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS200','Field Ecology','Introduces the main concepts and principles of ecology; the cycling of elements; energetics and structural organization of major ecological systems; population dynamics; impact of natural resource management practices and urban and industrial development on the environment; incorporating environmental quality considerations into development activities. The lab sessions include field trips to study natural and disturbed ecosystems, urban and applied ecology.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS201','Introduction to Canadian Environmental Law','Introduction to the basic legal framework that regulates the natural environment in Canada. The course provides students with an understanding of the foundational legal institutions in Canada and their respective roles in environmental regulation. The course includes both public and private law approaches to environmental regulation, and covers the principal regulatory tools used to protect the environment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS220','Ecological Economics','Evaluation of various economic approaches to the environment. The links between economics, systems and the natural environment will be explored and future directions examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS278','Advanced Environmental Research Methods','Advanced methods for developing, evaluating and using primary and secondary data in Environmental Studies. Builds upon ENV S 178 by introducing probability and inferential statistics, statistical sampling procedures and hypothesis testing. Standard parametric and nonparametric statistical tests up to the linear regression model and extensions. Modelling of environmental phenomena in space and time using the microcomputer for data entry, storage and analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS295W','Ecotourism and the Environment (WLU)`','');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS334','Introduction to Park Management','Introduction to the categories, administrative arrangements and functions of parks, including planning and management of parks at all government levels, with emphasis on law, policy system planning, management planning, site planning and management. The course will emphasize international aspects of park management.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS395','Study Abroad','Study abroad for academic transfer credit under a Faculty of Environment Exchange Program.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS401','Environmental Law','Detailed consideration of recent developments in Canadian environmental and resources regulatory regimes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS433','Ecotourism and Park Tourism','Planning and management of ecotourism and park tourism. Emphasis is placed on public involvement, market segmentation, policy and law, visitor management strategies and international trends. The role of national parks and other categories of protected areas is highlighted.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS444','Ecosystem and Resource Management in Parks/Natural Areas','This course examines how ecosystem principles and techniques are used in planning and operations in regulated lands, parks, and protected areas. Conservation of biological diversity, ecological integrity, and sustainable resource use are major themes. Students learn population and community ecological modelling and related univariate and multivariate analytical techniques pertaining to ecosystem management. Comparisons of management frameworks illustrate modelling and analytical techniques for the forestry, agricultural, mining, fishing and energy sectors. A field assignment gives experience in assessment and analysis. Guest lectures by professionals in the conservation, parks and resource sectors augment student experience.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS469','Landscape Ecology, Restoration and Rehabilitation','Survey of the major ideas and techniques of landscape ecology. Application of these concepts to a case study in restoration and/or rehabilitation. Interaction with professionals from government, NGOs and private industry on ecological issues will also be part of the course. The course includes a practical project on ecological restoration or rehabilitation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENVS474','Special Topics in Environmental Studies','This course allows for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG10','Knowledge Integration Seminar','General seminar on inter-disciplinary topics and integrative practices of special interest to INTEG students, faculty, and alumni. INTEG students must register every term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG120','Introduction to the Academy: Disciplines and Integrative Practices','Introduction to the disciplines, objectives and transferable skills embedded in a university curriculum. Practical introduction to creativity, communication, time management, problem analysis, and group work. Students will begin to place discipline-specific landmarks into historical and socio-cultural context with one another, and with the breadth of human accomplishment and experience.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG121','Introduction to the Academy: Design and Problem-Solving','A project course in design and problem-solving across various disciplines. We introduce problem-solving methods and issues in design, including ethics, sustainability, and intellectual property. Applied design projects require group work and reflect the skills introduced in the first term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG220','Nature of Scientific Knowledge','This course examines how knowledge is produced in scientific disciplines, including the nature of inquiry, as well as types of evidence and expertise across different fields. The course features significant input from scientific practitioners in the form of guest lectures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG221','The Social Nature of Knowledge','This course examines social aspects of knowledge, including ways that characteristics of individual knowers and their communities affect the acquisition, interpretation, production, and transfer of knowledge. In addition, we analyze the consequences this has for the objectivity of knowledge. The course features significant input from researchers in the humanities and social sciences in the form of guest lectures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG230','The Museum Course: Preparation and Field Trip','Introduction to the issues and intent of the Museum Course. Planning and research for the destination. Field trip to study museums, exhibits, audiences, and curatorial practices on-site in a major museum city. For additional information on itinerary and travel costs, contact the Department.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG231','The Museum Course: Field Trip Project','Seminar and projects based on field trip study of museums, exhibits, audiences, and curatorial practices.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG251','Creative Thinking','An introductory, integrative course in the history, psychology, value and practice of creativity across a variety of domains, from scientific research to the fine and performing arts. Students will learn to identify and explain key ideas and historical figures in the study and practice of creative thinking. They''ll also learn to reliably generate novel ideas of value, both solo and collaboratively.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG275','Special Topics in Knowledge Integration','From time to time courses of special study may be added to the program at the second-year level. Students wishing to take such courses should consult the Department''s Undergraduate Officer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG320','The Museum Course: Research & Design','An introduction to the museum, broadly interpreted as the public face of scholarship. Students work in small groups to research an inter-disciplinary topic of personal interest, in-depth, and design a museum exhibit suitable for a particular audience.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG321','The Museum Course: Practicum and Presentation','An introduction to the museum, broadly interpreted as the public face of scholarship. Students construct and exhibit their designs from INTEG 320, and present public talks on the scholarship and curatorial decisions behind their work.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG375','Special Topics in Knowledge Integration','From time to time courses of special study may be added to the program at the third-year level. Students wishing to take such courses should consult the Department''s Undergraduate Officer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG420','Senior Research Project A','Each student will work on a short research project under the dual direction of a member of the Department and an advisor from a discipline related to the topic. The results of this project will be presented in thesis form, and will be critically examined by members of this and, where pertinent, other departments.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG421','Senior Research Project B','A continuation of INTEG 420.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INTEG475','Special Topics in Knowledge Integration','From time to time, courses of special study may be added to the program at the fourth-year level. Students wishing to take such courses should consult the Department''s Undergraduate Officer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS110','Environmental Analysis and Solutions I: Foundations','Introduces analytical approaches for problem definition and problem solving that are appropriate for a wide range of environment and resource issues. Considers the limitations of approaches that perceive and attempt to manage issues as isolated phenomena. Also examines alternative approaches that recognize the broader context and underlying roots in ethical positions and ecological, economic and institutional systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS111','Environmental Analysis and Solutions II: Experiential Approaches','This course builds on the analytical approaches from ERS 110 and advances these by examining a case study or series of case studies in a transdisciplinary approach to problem solving. During the course and depending on the current environment or resource issue(s) of relevance, the instructor may choose to use field studies, laboratory studies, dispute resolution hearings, charettes, or role-playing simulations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS203','Environment and Development in a Global Perspective','Examines the interface between human development and the environment in a global context. Various perspectives are explored to link environmental issues to wealth, poverty, consumption, population, and economic globalization. Case studies, with an emphasis on developing countries, are used to illustrate linkages.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS210','Environmental Analysis and Solutions III: Greening Communities','The course considers how ''''green'''' communities might be fostered in a contemporary urban setting. It includes concepts and theories related to transformational learning, community resilience, socio-ecological systems thinking, communications, and public engagement. Students learn some basic qualitative methods which are then applied to a term project. Field sites are located in the Region of Waterloo (and the University of Waterloo). Students also learn how to undertake research in an ethical manner and how to effectively communicate their ideas and findings.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS211','Environmental Analysis and Solutions IV: Restoration Ecology','Application of conceptual, political and biophysical foundations of restoration in ecosystems, siting strategies, succession management, community assembly, intermediate statistical analysis, and ecological modelling. The course will take a transdisciplinary approach commensurate with the latest developments in restoration ecology. Field work will be a part of the course; subject to availability, students will participate in research and implementation of local projects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS215','Environmental and Sustainability Assessment I','An introduction to processes and techniques for incorporating environmental considerations in planning and evaluating proposals for future undertakings that may have significant social and biophysical effects. The course provides an overview of methodologies for, and controversies surrounding, the design and conduct of biophysical and socioeconomic impact studies, and the testing of reported findings. The main focus is on the purposes and design of environmental assessment processes, with particular reference to the Canadian federal and Ontario provincial legal mandates, and the evolution of assessment into a sustainability framework.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS234','Forest Ecosystems','This course examines the fundamental concepts of forest ecology and the role of forests in terrestrial ecosystems. The course will be divided into four sections, and will integrate case studies to introduce the student to current research problems in the study of forest ecology. Topics include: impacts/dependence of humans on forests; transfer and cycling of energy and nutrients; biophysical interactions; and forest management practices.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS253','The Politics of Sustainable Communities','Considers a variety of contemporary issues within the context of local politics and governance. The course explores the nature of a healthy community by examining issues related to environmental concerns, land use, economic development, community health, transportation, and public participation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS265','Water: Environmental History and Change','This course explores issues of water management from ancient to recent history. Tensions related to water supply and demand, agriculture and urbanization, health and sanitation, gender and household access to water resources, urban water and wastewater infrastructure, trans-boundary politics and water privatization debates are considered. Readings and detailed assessments of national and international cases from Europe, Western Asia, and North America are used as a basis for in-class discussion and research projects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS266','Water: Environmental History and Change','This course explores issues of water management from ancient to recent history. Tensions related to water supply and demand, agriculture and urbanization, health and sanitation, gender and household access to water resources, urban water and wastewater infrastructure, trans-boundary politics and water privatization debates are considered. We use readings and detailed assessments of national and international cases from Europe, Western Asia, and North America as a basis for in-class discussion and research projects. In comparison to the similarly titled ERS 265, this course requires an extensive field component.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS270','Introduction to Sustainable Agriculture','Provides both survey and detailed examinations of the ethics, science, and techniques involved in sustainable agriculture. Topics normally include management of crops, soil, water, nutrients, wastes and pesticides, integrated pest management, organic farming, permaculture, ecological farm planning, use of genetically modified organisms, urban agriculture in developing nations, and innovations such as computer modelling and precision farming. Course may be offered in any term; when offered in the spring term, it will usually involve at least one field trip.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS275','Special Readings/Seminar on Select Topics','Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering in that topic is available.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS280','Applied Field Studies','Analysis of selected environmental issues or programs with particular emphasis on applied problem-solving/management perspectives. Field trips to chosen sites will be conducted to gather information for analysis. Key organizations and people will be involved in field trips and discussions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS283','Ontario Natural History: Species and Patterns','An introduction to natural history, the art and science of identifying organisms, and observing their behaviour and ecological interactions. The students will reside for approximately nine days in a location in Ontario that has exceptional biodiversity. They will learn about local species (with an emphasis on insects, plants, and terrestrial vertebrates), human history and conservation initiatives. Each student must complete a project on a group of organisms or an ecological """"pattern"""" in consultation with the professor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS294','The Sacred Earth: Religion and Ecology','An examination of the past and present effects of Christianity and other world religions on human treatment of the natural world. Historical background, recent debates, and contemporary approaches to the ethical issues will be investigated.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS310','Environmental Analysis and Solutions V: Environmental Thought','Examination of conflicting positions on how we do and should view the natural world and ourselves, beginning with review of the history of attitudes to the environment and our place in it. Emphasis on evolution of attitudes to human nature and the environment in industrial society, critiques of these attitudes and implications for approaches to modern environmental issues.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS311','Environmental Research Project I: Systems Thinking for Interdisciplinary Research','This course provides an opportunity to learn and begin to apply systems-based tools in the context of an interdisciplinary research problem defined by the student in cooperation with the teaching-team. Students are provided with an introduction to the conceptual tools of systems thinking and resilience that help understand the dynamics of social change and social innovation. These conceptual tools will then be applied by students to provide a framework for interdisciplinary research and to develop a richer understanding of a case study of fostering social change and building adaptive capacity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS315','Environmental and Sustainability Assessment II','Continuing from concepts developed in ERS 215, this course places more emphasis on case studies and projects by students. The course provides a synthesis of ecological, physical, economic, socio-cultural and institutional concerns, as well as experience in the use of impact assessment methodologies and approaches, as a key element in achieving more informed and responsible decision making.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS316','Integrated Urban Water Systems Planning and Management','Urban water management cases drawn from North America, Australia, Western Asia and the Global South within the context of climate change and extreme events (e.g., flooding and drought). Focus is on the development and maintenance of urban water and wastewater systems, including demand and supply management planning, efficiency mechanisms, privatization, development and equity concerns. When possible, field trips to local water supply and treatment plants and demonstration of applied water efficiency and reuse projects will occur.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS317','Waste Management','This course will deal with the solid waste system, landfilling, incineration, energy from waste, recycling, composting, reduction and reuse. The context will be primarily Ontario and municipal waste management.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS330','Environmental Journalism 1','Introduction to writing (and preparing graphics) for print media on environmental issues, through practical experience working on the environmental journal Alternatives: Perspectives on Society, Technology and Environment. Each participant covers an environmental news beat in a selected regional (e.g. Atlantic Canada) or sectoral (e.g. law, technology, waste) topic area.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS340','Ecosystem Assessment','A term long """"blended"""" course with 4 of the 12 weeks typically spent at the Waterloo Summit Centre for the Environment at Huntsville ON. The course focuses on ecosystem assessment skills and practice. The primary vehicles will be acquiring and improving field identification skills of organisms and formal use of ecosystem assessment. As part of this course, students receive certification and accreditation of the provincially-recognized Certificate of Ecological Land Classification.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS341','Conservation and Restoration Practice','A term long """"blended"""" course with 4 of the 12 weeks typically spent at the Waterloo Summit Centre for the Environment at Huntsville ON. Students will acquire and improve practical site inventory and assessment skills for restoration and conservation goals while considering old growth and high integrity sites, and site limitations, and potential for bioengineering, bioremediation, vegetation installation and erosion-control measures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS360','Nature: Art, Myth and Folklore','This course explores the symbolic representation of nature in art, architecture, myth and literature from a multi-cultural perspective. The ideas about sacred spaces and environments will also be discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS365','Water Governance','Water governance refers to the processes and institutions through which societies make decisions and take actions that affect water. A profound, world-wide shift in the nature of water governance is occurring because governments can no longer be the primary source of decision making authority regarding water. Instead, through mechanisms that range from markets to co-management arrangements, citizens, non-government organizations and corporations are now playing key roles in water governance. The course explores major water governance challenges in Canada, and assesses different ways of addressing or resolving them.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS370','Corporate Sustainability: Issues and Prospects','A course that examines the ways in which sustainability issues and business operations have interacted, considering progressively ''''greener'''' corporate responses and broader sustainability challenges.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS371','An Ecosystem Approach to Environment and Health','This course will take an ecosystem approach to the issues of environment and health. The environment as defined in this course includes the natural (biological), built, social and political settings. Case studies will be used to illustrate environmental health issues using an interdisciplinary approach.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS372','First Nations and the Environment','First Nation environmental issues are often complex and require a holistic approach where the lines between different disciplines (e.g. natural, physical, health, and social sciences) are often obscured. The environment, as described in this course, includes the natural (biological) and built (social, political) settings. Case studies will be used to illustrate significant issues.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS375','Special Readings/Seminar on Select Topics','Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering that topic is available.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS382','Environmental Monitoring','This block field course provides students with theoretical and practical knowledge of ecological monitoring through active participation in programs applying protocols developed by the Smithsonian Institute and the UN Man and the Biosphere Program. This course is a collaborative effort with professional staff from the Niagara Escarpment Commission as well as guests from other organizations and interest groups along the Niagara Escarpment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS383','Tropical Ecosystems','This course examines the fundamental concepts of terrestrial ecosystems in tropical climates. The course has three sections: (1) biophysical aspects (climate, location, landforms, soil, vegetation), (2) tropical resource systems (forest- and agroecosystems) within the framework of conventional and sustainable resource extraction, and (3) current conservation issues. Case studies are presented.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS404','Global Environmental Governance','Examination of the ways in which world society is striving to address environmental challenges by means of ''''global governance'''' - that is, international organizations and institutions intended to deal with these challenges. The history of international environmental politics will be reviewed, specific organizations and other actors involved in global environmental governance will be examined and the management of particular global environmental challenges investigated.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS409','Activism! Community Action for Environmental and Social Change','Focus on analyzing social and environmental problems and creating strategies for change. Theories and concepts of community development, critical analysis and praxis - integration of action and reflection - will be introduced. The role and importance of social movements, including environmentalism, feminism, and the peace movement will be discussed. Skills in developing and implementing change strategies in areas such as facilitation, consensus-backed decision-making and conflict resolution will be introduced.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS410','Environmental Analysis and Solutions VI: Ecosocial Systems','The final course in this theme will examine the cumulative lessons learned in ERS and focus on emerging issues and problems related to the environment and how transdisciplinary approaches may solve them. Advanced ideas drawn from a range of literatures (social, natural and physical sciences, literature, philosphy, history, economics) will be discussed and their influence on social movements and environmental policy and laws discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS411A','Environmental Research Project II','Founded on principles and efforts in ERS 311, students will begin research and actions needed to complete a project of sufficient scope to demonstrate mastery of problem-solving and communication skills on a selected problem or issue related to the environment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS411B','Environmental Research Project III','Continues ERS 411A; students will complete their project and present it during a conference-style and quality poster session near the end of term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS412A','Environmental Research Project II','Founded on principles and efforts in ERS 311, students will begin research and actions needed to complete a project of sufficient scope to demonstrate mastery of problem-solving and communication skills on a selected program or issue related to the environment. Greater credit weight relative to ERS 411A reflects a project more ambitious in scope.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS412B','Environmental Research Project III','Continues ERS 412A; students will complete their project and present it during a conference-style and quality poster session near the end of term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS413','Senior Honours Research Seminar','This course consists of intensive readings, seminar discussion and a major one-term research paper. It will be taken instead of ERS 411A/B or ERS 412A/B. INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS413A','Environmental Research Project II','Founded on principles and efforts in ERS 311, students will begin research and actions needed to complete a project of sufficient scope to demonstrate mastery of problem-solving and communication skills on a selected problem or issue related to the environment. Greater credit weight relative to ERS 412A reflects a project more ambitious in scope.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS413B','Environmental Research Project III','Continuation of ERS 413A; students will complete their project and present it during a conference-style and quality poster session near the end of term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS415','Environmental and Sustainability Assessment III','Continuing from concepts developed in ERS 215 and ERS 315, this course will focus on the latest concepts and applications of assessment principles and practices. The course focus will vary from year to year following development in the field. Topics may include assessment into land use planning and community design, policy and program assessment, and assessment of new technologies and alternative futures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS430','Environmental Journalism 2','Advanced work in environmental journalism including examination of ethical issues and practical problems. Special attention to complex stories, editing and design. Course focus depends on nature of individual projects selected by participants.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS461','Food Systems and Sustainability','This course examines dimensions of food systems sustainability and food security, from production to consumption, from local to global scales, in the Global North and South. Specific themes covered in the course include technological and genetic change in food production, resource depletion and wider environmental impacts of the industrial food system, and policy, market-oriented, and civil society initiatives to bring about change in the food system, including organic production and localizing food systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS462','Global Food and Agricultural Politics','This course examines the global food and agriculture system. Specific themes to be covered include political and governance issues related to the Green Revolution, global food corporations, agricultural trade liberalization, food aid, international agricultural assistance, the global agro-chemical industry, and agricultural bio-technology.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS464','Economics and Sustainability','Advanced introduction and examination of a range of approaches to the relationship among the economy, the environment and sustainability. Course material covers both theoretical and practical policy challenges.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS474','Special Topics in Environmental & Resource Studies','These courses allow for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS475','Special Readings/Seminar on Select Topics','Background reading and study in consultation with faculty. Typically utilized when a student must study a topic in connection with other work, but no course offering that topic is available.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS476','Environmental Education','This web-based course is designed to assist undergraduate teaching assistants to develop their own philosophy and strategies for environmental education, explore various methods in teaching, and become effective teachers themselves. TAs will learn how to document, prepare, and implement lesson plans for tutorials, evaluate assignments, communicate effectively, and develop productive approaches to effective class session management.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS484','Soil in the Environment','This course examines the role of soil in the environment, its importance as a natural resource in agricultural and forest productivity, and the effects on soil resources as a result of different management practices. It is divided into three sections: 1) introduction to soil composition, formation, and physical, chemical and biological characteristics of soil; 2) soil degradation and management approaches to rehabilitation; 3) soil pollution and the role of soil in maintaining environmental integrity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ERS489','Global Food Systems','Examines the global nature of food systems from production to consumption, including both industrial and alternative models. Specific themes covered in the course include technological change in agriculture, corporate concentration, international agricultural trade, food aid, fair trade, and organic production in the Global North and South.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG100','On Becoming a Geographer','An introduction to geographic themes and methods of inquiry. The emphasis will be placed on practical skills including literature searches, field observation, scholarly debate and professional writing.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG101','Geography and Human Habitat','An introduction to human geography through a survey of some of the concepts, methods, techniques and applications of geographic analysis to the human cultural environment. Directed towards people-land and location analysis themes.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG102','Geography and Our Planetary Environment','Emphasis on the natural environment as an integrated system. Selected aspects of weather -- climate, water, soils, biota, landforms along with flows of energy, water and matter and their effects on the subsystems of the natural environment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG165','Computer Cartography: Principles and Design','Focus is on the compilation and cartographic display of spatially referenced data. Topics covered include geographic coordinate systems, map projections, mapping quantitative data, terrain representation, compiling data from a variety of sources, and the production of effective maps based on established principles of cartographic design.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG181','Principles of GIScience','Focus is on the issues and foundations of Geographic Information Science. Topics covered include geographic coordinate systems, map projections, mapping quantitative data, compiling data from a variety of sources, and the production of effective maps based on established principles of cartographic design.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG187','Problem Solving in Geomatics','A specialized introduction to fundamental concepts and emerging trends in Geomatics and Geographic Information Science (GIS). Focus on methods and techniques of spatial data capture, management, and analysis. Introduction to programming as applied to GIS.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG201','Fluvial Geomorphology','Emphasis on concepts related to fluvial processes, river mechanics, the relationship between environmental change and river regime. Selected topics include fluvial processes and landscape formation, flow and sediment regimes, channel processes, form and behaviour, river response to natural and anthropogenic change, and river management.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG202','Geography of the Global Economy','An introduction to globalization of the world economy through an examination of its causes, patterns, and consequences in a variety of geographic contexts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG203','Environment and Development in a Global Perspective','Examines the interface between human development and the environment in a global context. Various perspectives are explored to link environmental issues to wealth, poverty, consumption, population, and economic globalization. Case studies, with an emphasis on developing countries, are used to illustrate linkages.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG206','Human Dimensions of Natural Hazards','This course will investigate the human dimensions of the global experience with natural hazards and associated disasters. The physical nature of a wide range of geophysical and biophysical hazards will be explored, paying particular attention to: the ways in which hazards become dangerous to humans, and the pathways by which humans can either increase or decrease their vulnerability in the face of natural hazards.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG208','Human Dimensions of Global Climate Change','Climate change is one of the most profound environmental issues affecting society. The course is an introduction to the human dimensions of global climate change, including its scientific history, potential impacts for natural systems and human societies around the world, and the two societal responses: adaptation and greenhouse gas mitigation. Canadian climate change science, impacts and policy responses will be highlighted.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG209','Hydroclimatology','An introduction to the fundamental processes governing climate and hydrological systems and the links between them. It starts with a discussion of basic atmospheric and hydrological processes and traces the flow of energy and water between the earth''s surface and the atmosphere. The water cycle is examined including evapotranspiration, precipitation, runoff and water storage in the natural reservoirs (including soil and groundwater, lakes and wetlands).');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG210','Image Interpretation and Photogrammetry','Focuses on principles of air photo interpretation and use of airphotos and high-resolution images for studies such as terrain analysis, vegetation and soil mapping, rural and urban planning, crop identification, forestry, wildlife ecology and hazard evaluation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG212','Japan and the Pacific Rim','Explore Japan. Learn about its culture, economy, regions, environment, trade patterns, investment and development assistance.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG215','China: Diverse and Dynamic','Changing geographies of China are examined and explained. Patterns and processes of change will be systematically analyzed for topics such as the physical environment, resources, development policy, globalization, industrialization, urbanization, and regional development. Diverse cities and regions are compared and the integration of China into the global economy is explored.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG221','The United States','Focuses on population shifts, urban developments, and regional economic development in the context of the nation and selected regions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG222','Geographical Study of Canada','An exploration of the geographical bases of Canada''s regional identity. How physical, demographic, and economic geographies have combined to create unique regions within Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG233','Geography of Tourism','Covers the nature, history and growth of different types of tourism and tourist; the positive and negative impacts of tourism and alternative forms to mass tourism. The problems of and prospects for tourism are examined through a consideration of a variety of countries and regions, both developed and developing.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG250','Urban and Economic Systems: Inter-City and Global Connections','Contemporary patterns of urban and economic growth and urban based development. Changing trends in urban organization at the regional, national and global scale. New systems of world cities.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG271','Earth from Space Using Remote Sensing','Remote sensing of the Earth''s systems (atmosphere, land, and oceans) is introduced. The course covers the principles, physics, sensor technology, processing and applications of remote sensing in the electromagnetic spectrum.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG281','Introduction to Geographic Information Systems (GIS)','Introduction to the fundamental concepts and use of Geographic Information Systems (GIS). Students learn about the nature of geographic information and how to store, manipulate and analyze spatial data in a range of application areas. Students will learn underlying theory in lectures and gain a working knowledge of GIS software in lab sessions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG293','Professional and Scholarly Practice in Geography','Explores the relationships between the academic content and methodologies of geography and the professional practice of the field. The course emphasizes issues involved in problem identification, research design, thesis proposal preparation, and the fundamentals of scholarly writing. The professional practice of the field examines such questions as ethics, and professional collaboration.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG294','Approaches to Research in Physical Geography','Introduces skills for conducting research in physical geography. Selected techniques used in climatology, hydrology, geomorphology and/or biogeography research will be demonstrated and the principles behind the techniques will be explained. Students get hands on experience in research design, field and laboratory techniques, data assembly and the interpretation of data.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG300','Geomorphology and the Southern Ontario Environment','Study of the origin and evolution of landforms with emphasis on southern Ontario. Analysis of geomorphic processes. Study of human impact on geomorphological landscapes. The lectures will be supplemented by field trips and field work required for term projects.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG303','Physical Hydrology','Fundamental processes in physical hydrology are addressed. Components of the water balance are examined to determine the nature of their variation in time and space. Precipitation, interception, infiltration, groundwater and soil water processes, evapotranspiration, runoff and storage will be examined from a theoretical and practical viewpoint, and their linkages demonstrated by lab and fieldwork.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG306','Human Dimensions of Natural Hazards','This course will investigate the human dimensions of the global experience with natural hazards and associated disasters. The physical nature of a wide range of geophysical and biophysical hazards will be explored, paying particular attention to: the ways in which hazards become dangerous to humans, and the pathways by which humans can either increase or decrease their vulnerability in the face of natural hazards.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG308','Human Dimensions of Global Climate Change','Climate change is one of the most profound environmental issues affecting society. The course is an introduction to the human dimensions of global climate change, including its scientific history, potential impacts for natural systems and human societies around the world, and the two societal responses: adaptation and greenhouse gas mitigation. Canadian climate change science, impacts and policy responses will be highlighted.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG309','Physical Climatology','Principles of physical climatology with emphasis on regional and global change and variability. Topics include radiation and energy balances, general circulation patterns, synoptic development and micro-climatology.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG310','Geodesy and Surveying','Concepts of geodesy and surveying, Earth''s gravity field and the geoid, and measurement techniques applied to geomatics are examined. Field studies include the use of the level, the total station, and GPS for doing distance and angle measurements, leveling, traversing and topographic surveying.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG311','Local Development in a Global Context','The course examines the ability of local communities to influence their development trajectory. The roles and potential for collaboration among public, private, and third sector partners are explored. Social, environmental, and economic goals are interconnected within a sustainable development framework.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG316','Multivariate Statistics','The theory and application of multivariate statistics, with particular emphasis upon the use of the computer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG318','Spatial Analysis','Advanced quantitative analysis in a spatial context. A selection of techniques from sampling, geostatistics, point pattern analysis and cluster detection, spatial classification, and spatial data mining.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG319','Economic Analyses for Regional Planning','Practical application and critical appraisal of regional analysis techniques used by planners, economic developers and consultants. Problem based approaches to understanding the strength and leverage of business and industrial sectors, projection and forecasting, employment and demographic trends, investment decision-making and cost benefit analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG323','Perspectives on International Tourism','The character, problems of, and prospects of tourism are examined through consideration of tourism in a variety of countries and regions, both developed and developing. Topics include the nature and significance of tourism; economic, environmental and social impacts of tourism; and costs and benefits of tourism to destination areas.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG333','Recreation Geography','Implications of existing and potential recreation supplies and demands. Topics include recreational travel, site capability, economic and ecological impact models and behavioural aspects of amenity resources.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG340','Settlements of Rural Canada','Examines the evolving form and function of Canada''s dispersed and nucleated settlements. Considers the role of local economic development in stimulating change.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG349','Urban Form and Internal Spatial Structure','An examination of the major factors giving rise to distinctive styles of urban spatial organization. Focus moves from city-wide scale to subareas/sectors - inner city, housing, retailing, etc. Emphasis on understanding and planning for the dynamics of complex environments. Applied issues or problems are dealt with throughout the course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG351','Geography of Transportation','Focuses on Canadian transportation systems and issues and is organized into three modules: links between transportation and regional economic development, urban land use - transportation interactions, and sustainable transportation. Approximately one-quarter of the course focuses on analytic techniques including network analysis, category analysis, and the gravity model. Particular attention is paid to trends in air travel and related issues.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG353','Retail Location','Examines retail location at both the inter and intra-urban scales. Emphasis is placed on the underlying processes that give rise to retail structure, techniques of site selection and public sector involvement in retail location.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG356','Resources Management','Reviews selected theories, methods, and terminology related to economic, behavioural, institutional and decision-making aspects of resources and environmental problems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG360','Environment and Behaviour','An introduction to the study of human behaviours related to natural and built environments. Variations in environmental cognition, thought, perceptions, knowledge, attitudes, decisions and behaviours are explored at multiple scales (from individual to societal). The importance of social and cultural contexts in environmental decision-making is traced, using examples drawn from fields such as environmental psychology, urban and regional geography, natural hazards, environmental policy, and behavioural geography.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG365','Study Abroad','Study abroad for academic transfer credit under an Environmental Studies Exchange Program during a fall term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG366','Study Abroad','Study abroad for academic transfer credit under an Environmental Studies Exchange Program during a winter term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG368','Conservation/Resource Management of the Built Environment','Consideration of the constraints and guidelines that an application of the principles of ecology places on the planning and management of resources within urban spaces and the implications for urban design. The theory and history of this subject will be discussed together with urban ecomanagement, the management of waste, urban open space and parks, rehabilitated sites, and environmentally sensitive areas.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG371','Advanced Remote Sensing Techniques','Advanced image processing techniques of digital remote sensing measurements (e.g. radar systems, optical and infrared systems) from ground, aircraft and satellite instrument systems. Techniques are applied to the study of physical and human environments.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG372','Waterloo in Switzerland -- Lausanne','Study abroad for academic transfer credit under an Environmental Studies Exchange Program during a fall term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG373','Waterloo in Switzerland -- Lausanne','Study abroad for academic transfer credit under an Environmental Studies Exchange Program during a winter term.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG381','Advanced Geographic Information Systems','Students learn theoretical and operational approaches to advanced spatial analysis using geographical information systems. Emphasis is placed on the use of automation procedures using models and programming to address a variety of topics that may include but are not limited to digital terrain modeling, suitability analysis, network analysis, and cell-based models. The domain of spatial problems explored may vary by instructor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG387','Spatial Databases','This course focuses on design and development of a GIS database. It addresses theoretical issues regarding data models used in GIS and data modeling techniques used in designing spatial databases. It considers the processing required to input data from a variety of sources and clean and edit a multi-theme database and introduces students to creation and use of internet map services.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG391','Field Research','Field research course in which a specific area will be analyzed from a geographic point of view. Individual or group analysis of specific field problems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG393','Approaches to Research in Human Geography','Introduces skills for conducting human geography research and the basic principles and methods of analysis. These skills include problem identification, research design, research ethics, and the assembly and interpretation of evidence.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG394','Approaches to Research in Physical Geography','Introduces skills for conducting research in physical geography. Selected techniques used in climatology, hydrology, geomorphology and/or biogeography research will be demonstrated and the principles behind the techniques will be explained. Students get hands on experience in research design, field and laboratory techniques, data assembly and the interpretation of data.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG404','Soil in the Environment','This course examines the role of soil in the environment, its importance as a natural resource in agricultural and forest productivity, and the effects on soil resources as a result of different management practices. It is divided into three sections: 1) introduction to soil composition, formation, and physical, chemical and biological characteristics of soil; 2) soil degradation and management approaches to rehabilitation; 3) soil pollution and the role of soil in maintaining environmental integrity.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG405','Wetlands','Basic concepts on the distribution, classification, development, hydrology, biogeochemistry, and ecology of wetlands with an emphasis on temperate and boreal/subarctic systems. Human impacts, restoration and reclamation of wetlands are considered with the view of wetlands as functional ecosystems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG407','Environmental Hydrology','An interdisciplinary course that explores ecological processes that are linked to physical hydrology. Particular focus is on the storage and movement of water, solutes and nutrients within selected ecosystems and the ecological impacts of human activities on the ecohydrological system.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG408','Modeling our Future Climate','This course provides students with experience using numerical models to understand changes in Earth''s climate. Building on their knowledge of the fundamental physical processes governing the climate system, students will construct, apply and evaluate a hierarchy of climate models (from simple energy balance models to full ocean-atmosphere General Circulation Models). The predictive ability of climate models, and their validation, will be considered. The different types of uncertainty inherent in climate projections will be explained and the implications assessed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG409','Energy Balance Climatology','A field and lecture course including the radiation and energy balances of various surfaces, the principles of turbulent energy exchange, and the biotic response to the energy environment. These concepts will be illustrated through the collection and examination of field data. The student will be responsible for presentation of a seminar on an assigned topic as well as presentation of the results of research incorporating data collected at the University of Waterloo weather station.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG410','Global Navigation Satellite Systems','The course covers the essence of Global Navigation Satellite Systems (GNSS), including reference systems, satellite orbits, satellite signals, observables, mathematical models for positioning, data processing, transformation, and applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG411','Global and Local Dimensions of Industrial Restructuring','Understanding the implications of globalization for the local and regional economy through examining technological change, multinational corporations, employment and institutions. Course work focuses on analysis of the restructuring of specific industries in the southern Ontario region.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG418','The Arctic Climate System','This course provides a comprehensive overview of the Arctic climate system. A variety of topics are considered including: key components and processes of the energy and water budgets; the current state of the cryosphere; the relationship between the cryosphere and past variability; the role of the Arctic in the global climate system; and the prediction of future changes using global climate models. Various observational perspectives on the climate system, including conventional measurements, atmospheric reanalysis, and remote sensing are presented.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG419','The Cryosphere','This course provides a comprehensive overview of the cryosphere and cryosphere-atmosphere interactions. Topics covered include the material and thermodynamic properties of snow and ice, the role of the cryosphere in weather and climate, and the response of the cryosphere to climate change (past, present and future). Students will be introduced to, and experiment with, snow and ice process models.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG423','Tourism Lecture Series','This course will introduce participants to a variety of topics and research methods through presentations made by active researchers from Canada and abroad.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG426','Geographies of Development','Examines international development theories and practice, emphasizing the interactions between social, economic, political and environmental dimensions at the micro- and macro-scales. Selected case studies illustrate divergent outcomes of development and the contested process that development represents.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG429','Global Food Systems','Examines the global nature of food systems from production to consumption, including both industrial and alternative models. Specific themes covered in the course include technological change in agriculture, corporate concentration, international agricultural trade, food aid, fair trade, and organic production in the Global North and South.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG430A','Field Research in Regional Geography','Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG430B','Field Research in Regional Geography','Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG430C','Field Research in Regional Geography','Detailed analysis of a selected region with major emphasis upon a field examination of the region (several weeks duration). Offering dependent upon faculty availability and student enrolment. For additional information on duration, itinerary and travel costs of course offerings, contact the Geography Undergraduate Advisor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG432','Health, Environment, and Planning','A seminar course on the environmental sources and causes of disease and illness, the concepts of health, e.g. medical, scientific, economic, political, etc., the health services and facilities and related technologies and the role and responsibilities of (urban and regional) planners in the creation of a more healthful environment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG450','Changing Form and Structure of Metropolitan Canada','Selected analysis of processes, problems and planning issues associated with the internal growth and spatial reorganization of Canadian metropolitan areas. Three or four topics are chosen for detailed investigation. These will vary from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG452','Resource Management Project','Concepts and techniques of resources management and institutional analysis are applied to the study of a current resource or environmental management issue.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG453','Urban Stormwater Management','Urban stormwater runoff affects the water quality, water quantity, habitat and biological resources, public health and aesthetic appearance of urban waterways. This course reviews the physical and chemical processes, environmental assessment techniques and best management practices related to stormwater management in the urban systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG454','Retail Landscapes','A critical examination of the processes that govern retail landscapes in the city and its countryside. Emphasis is placed on understanding the forces that drive the creation, maintenance, and evolution of these settings at a variety of scales. Small-group work provides an opportunity to apply this understanding in a real-world context. [Formerly: GEOG 353]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG459','Energy and Sustainability','Renewable and non-renewable energy supply systems are compared using economic and environmental measures. Consumption trends and conservation options are considered at the local and global level. Projects are used to demonstrate the economic and environmental challenges in the design of a sustainable energy system.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG461','Food Systems and Sustainability','This course examines dimensions of food systems sustainability and food security, from production to consumption, from local to global scales, in the Global North and South. Specific themes covered in the course include technological and genetic change in food production, resource depletion and wider environmental impacts of the industrial food system, and policy, market-oriented, and civil society initiatives to bring about change in the food system, including organic production and localizing food systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG462','Global Food and Agricultural Politics','This course examines the global food and agriculture system. Specific themes to be covered include political and governance issues related to the Green Revolution, global food corporations, agricultural trade liberalization, food aid, international agricultural assistance, the global agro-chemical industry, and agricultural bio-technology.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG471','Remote Sensing Project','Digital image analysis for resource mapping and evaluation using remote sensing data. Topics range from initial data selection to final map production and assessment. Using commercial image analysis software, students will analyse data for a selected area and produce a portfolio of results. In addition, they will undertake a literature review on a selected topic and present highlights of the review at an end-of-term mini-conference.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG474','Special Topics in Geography','These courses allow for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG475','Independent Study of Selected Topics','Individual study of specific topics not covered in other courses. Students must not register for this course until a faculty member has agreed to supervise the study and the student has developed a brief outline to be filed with the Associate Chair, Undergraduate Studies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG481','Geographic Information Systems Project','The development, implementation, and presentation of a response to a set of GIS related project requirements is the focus of this course. Students work in small teams to enhance and develop their abilities to work with GIS and related spatial technologies and analytical methods in an advanced project setting. The nature of the project requirements and themes varies with faculty and student strengths and interests. Projects may emphasize development of software applications, use of programming, or advanced GIS analysis methods, and draw from theme areas such as environment studies and management, human and physical geography, or planning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG483','Geoweb and Location-Based Services','A critical approach to the development, implementation, and evaluation of the Geospatial Web and location-based services. Topics covered include Volunteered Geographic Information, open data, locational privacy, and mobile data collection. Assignments are used to experiment with course concepts.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG487','Management Issues in Geographic Information Systems','Built around a set of key issues in the management of Geographic Information Systems. Focuses on middle management concerns and covers topics including GIS needs assessment, benchmarking, the law and spatial data, spatial data warehousing, multi-user GIS modelling and GIS application development. Uses of GIS in both public and private sector organizations are covered. [""""Formerly: GEOG/PLAN 555""""]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG490A','Honours Thesis Preparation','Preparatory work and first draft of thesis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','GEOG490B','Honours Thesis Completion','Completion of thesis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN100','The Evolution of Planning','Introduction to planning in its historical and contemporary contexts. Discussion of city types and origins. Consideration of local, national, and international design and management of environment and human habitations. Introduction to selected main themes in planning and architecture.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN102','Professional Communication','This is a practical introduction to written communications and oral presentations as they are used specifically in the Planning profession. Report writing, correspondence and interaction with the news media are among the forms of communication considered. Students complete both written and oral assignments. Considerable critical comment is provided on assignment content as well as on technical aspects such as style and grammar.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN103','Planning, Administration, and Finance','Important planning and financial instruments, administrative processes and planning practice are reviewed. Planning and Municipal Acts, official plans, plan amendments, zoning bylaws, site plans, easements, consents, variances, assessments, mill rates, capital works, and debentures. Municipal budgets and accounting concepts, and financing are studied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN104','Perspectives on Planning','This course introduces undergraduates to the discipline and practice of Planning. Students will gain a basic understanding of the many facets that make up Planning through interaction with a select complement of the School''s faculty members. Emphasis will be placed on exploring research case studies and examining the link between academia and professional planning practice.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN105','Introduction to Planning Analysis','The goal of this course is to introduce students to analytical approaches employed in planning. Students will gain a basic understanding of planning relevant data and approaches to analyzing and communicating planning data. Ideas and techniques drawn from demography are emphasized along with spatial analysis, visualization, and techniques for socio-economic data.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN110','Visual Approaches to Design and Communication','Practical project-based skill development involving sketching, digital and film photography, and 2 dimensional computer graphics used by planners to conceive, evaluate and communicate design ideas.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN203','Transportation Planning and Analysis','This course provides an in-depth exploration of Transportation Planning. Topics covered will include fundamentals of transportation systems, the historical evolution of transportation planning, modern approaches to urban and regional transportation planning, and relevant transportation planning methods. Applied analysis emphasizing Canadian urban examples will be undertaken.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN210','Urban Planning Design and the Environment','A problem-based exploration of urban and regional design in the physical-natural, built, social-cultural environment. Individual and group projects, studio consultation and critiques explore traditional and contemporary approaches using sketches, constructed and computer models, and verbal analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN233','People and Plans','This course looks at social planning as a way of tackling urban social problems. Will examine the different types of social planning and the relationship between physical and social planning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN261','Urban and Metropolitan Planning and Development','This course explores the relationship between planning instruments and the urban land use market. It relates urban outcomes to interactions between market processes and planning interventions. It is in this light that it interprets changing urban land use and transportation patterns since the late 1940s, as well as contemporary urban issues. These issues are chosen so as to underscore differences in the circumstances confronting different zones of urbanization (core, inner city, inner suburb and outer suburb).');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN281','Introduction to Geographic Information Systems (GIS)','Introduction to the fundamental concepts and use of Geographic Information Systems (GIS). Students learn about the nature of geographic information and how to store, manipulate and analyze spatial data in a range of application areas. Students will learn underlying theory in lectures and gain a working knowledge of GIS software in lab sessions.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN300','Planning Theory','An introduction to the theoretical principles that influence planning practice. The course will examine selected contributions to planning theory and practice. Related issues of professional practice and ethics will be considered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN309','Site Planning and Design Studio','A design studio that focuses on design planning for individual sites and small scale developments. Projects integrate critical features: terrain, natural and cultural context, microclimate, infrastructure, and adjacent land uses. Studio seminars, consultation and critiques explore theory and practice in observation, awareness, comprehension, and idea development.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN313','Community Design Studio','A design studio on planning design at the community scale that is sensitive to the natural, built, and cultural context. Field study projects of area development or redevelopment projects are used to examine traditional and contemporary theory and practice. Studio seminars, presentations and critiques explore the definition, management and mapping of physical structures, visual and environmental resources. Included is consideration of the special problems of planning development within a scenic landscape.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN320','Economic Analyses for Regional Planning','Practical application and critical appraisal of regional analysis techniques used by planners, economic developers and consultants. Problem based approaches to understanding the strength and leverage of business and industrial sectors, projection and forecasting, employment and demographic trends, investment decision-making and cost benefit analysis.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN333','Neighbourhood and Community Planning','This course examines concepts and issues related to social planning for neighbourhood and community environments. It considers planning for particular target populations in the contexts of suburbanization and core area revitalization. It will review models of neighbourhood change and community development and will address ways to involve community members in the planning process.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN340','Ecology-Based Policy-Making','Consideration of how selected, key ecological concepts shape the options for policy planning and managing landscapes. Development of critical skills for assessing, framing and conveying information essential to planning, managing and developing policy for landscape stewardship.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN341','Conservation/Resource Management of the Built Environment','Consideration of the constraints and guidelines that an application of the principles of ecology places on the planning and management of resources within urban spaces and the implications for urban design. The theory and history of this subject will be discussed together with urban ecomanagement, the management of waste, urban open space and parks, rehabilitated sites, and environmentally sensitive areas.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN346','Advanced Tools for Planning: Program Evaluation, Mediation and Public Participation','A number of approaches and techniques such as public participation, program evaluation, and alternative dispute resolution and mediation are used extensively in modern planning. This course addresses these techniques and critically explores their background, rationale, application, and use in contemporary community planning within a modern democratic society.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN349','Urban Form and Internal Spatial Structure','An examination of the major factors giving rise to distinctive styles of urban spatial organization. Focus moves from city-wide scale to subareas/sectors - inner city, housing, retailing, etc. Emphasis on understanding and planning for the dynamics of complex environments. Applied issues or problems are dealt with throughout the course.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN350','Research Methods for Planners','This course develops the capacity of students to apply research methods to planning-related issues. Examination of a variety of alternative approaches to designing and conducting research. Students learn how to become informed consumers and producers of planning-related research.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN351','Multivariate Statistics','The theory and application of multivariate statistics, with particular emphasis upon the use of the computer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN353','Spatial Analysis','Advanced quantitative analysis in a spatial context. A selection of techniques from sampling, geostatistics, point pattern analysis and cluster detection, spatial classification, and spatial data mining.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN362','Regional Planning and Economic Development','The relationship of economic planning to regional planning. Concepts of economic development and models of regional development planning. Case studies and examples are drawn from federal regional development efforts in Canada and/or from Third World nations. Workshops focus on regional planning and development at both a conceptual and empirical level.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN381','Advanced Geographic Information Systems','Students learn theoretical and operational approaches to advanced spatial analysis using geographical information systems. Emphasis is placed on the use of automation procedures using models and programming to address a variety of topics that may include but are not limited to digital terrain modeling, suitability analysis, network analysis, and cell-based models. The domain of spatial problems explored may vary by instructor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN387','Spatial Databases','This course focuses on design and development of a GIS database. It addresses theoretical issues regarding data models used in GIS and data modeling techniques used in designing spatial databases. It considers the processing required to input data from a variety of sources and clean and edit a multi-theme database and introduces students to creation and use of internet map services.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN401','Planners and Planning Tribunals','The course focuses on contemporary planning problems before the Ontario Municipal Board. Students analyze and synthesize the various planning perspectives involved in real cases. Students explore the issues of presenting a case, or appearing as a witness before the Board through observation and participation in a moot OMB hearing presided over by a member of the OMB.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN403','Professional Practice, Public and Private Administration','Professional practice responsibility and ethics, administrative methods and organization are considered in the context of the nature of organizations, politics and economics that provide opportunities and limitations. Perspectives are drawn from organizational theory, public administration, land economics, political and planning theory. Public sector decision making and policy development are discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN405','Integrated Planning Project','This senior level course provides an opportunity for students to integrate their planning skills and knowledge in an applied context. Working under the supervision of Planning faculty, student groups will tackle case studies based on local real world examples. The projects will require an integrative approach with required design, analytic, and theoretical elements. Students will develop, implement, and present their term projects in a professional setting.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN408','Urban Design Seminar','An exploration of traditional and contemporary theories of urban design and their application in a variety of contexts. This involves study of approaches that lead to improved quality of life within a robust and sustainable urban environment. Topic includes the spatial, environmental, social, cultural and economic aspects of urban design. Precedents and trends will be analyzed. International viewpoints and case studies are considered. May involve study field trips to major urban centres.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN409','Urban Design Studio','An intensive urban design studio that addresses both new and redevelopment planning design at a community and higher scale. A problem-based exploration involving studio seminars, consultation and critiques to integrate design elements that provide economically and socially viable communities with a sense of place and community, and improved urban quality.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN414','Heritage Planning Workshop','The role of cultural heritage in the community context is examined. Varying forms of heritage preservation, its function and organization are covered.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN416','Modelling the City','This course examines the use of computer modeling and simulation in the realm of urban analysis and forecasting, with the goal of understanding urban land-use change trajectories. Topics include an overview of the drivers and consequences in urban land-use change, the role of models, an overview of current methodological approaches, and an examination of urban simulation models as used in the development of urban policies and official plans. This course provides an applied learning environment in which students will gain experience in the use of spatial (GIS) modeling approaches.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN418','Spatial Demography','This course develops the capacity of students to apply methods of spatial demography. Spatial demography refers to the statistical study of human population using spatial methods for analyzing demographic data. It can provide insights into the understanding of geographic variations of population''s characteristics, which in turn can help to make better plans in building the environment. Through this course, students will learn the basic concepts, data sources, data issues, methodologies, and applications of spatial demography.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN431','Issues in Housing','The first part consists of an overview of housing in Canada considering federal, provincial and municipal policy as well as the housing industry. In the second part, special topics such as homelessness, affordability, environment and other issues are discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN432','Health, Environment, and Planning','A seminar course on the environmental sources and causes of disease and illness, the concepts of health, e.g. medical, scientific, economic, political, etc., the health services and facilities and related technologies and the role and responsibilities of (urban and regional) planners in the creation of a more healthful environment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN433','Social Concepts in Planning','An advanced examination of planners in their environment using a variety of theories including those related to systems, organization, social change and planning. Social concepts that will enhance a planner''s effectiveness are examined within a framework of social structures and case studies.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN440','Waste Planning','This course will explore, through an examination of various procedures and techniques, the development of strategies and policies which assist in planning for a comprehensive and integrative approach to waste management. The focus is on the applications of contemporary waste management concepts and principles.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN450','Changing Form and Structure of Metropolitan Canada','Selected analysis of processes, problems and planning issues associated with the internal growth and spatial reorganization of Canadian metropolitan areas. Three or four topics are chosen for detailed investigation. These will vary from year to year.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN451','Tools for Sustainable Communities','This seminar course will focus on conceptual frameworks and tools intended to guide communities to a more sustainable condition. Examples of possible concepts covered in the course are material and energy flows, resilience, adaptability, passive survivability, sufficiency, biophilia, and localization. Course focus will vary from year to year. Recent topics have included The Natural Step, transition towns, LEED-ND, microclimate design, biophilia, and community energy systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN452','Policy Analysis for Planners','This course considers policy analysis as a step-by-step process of prescriptive activities used to aid decision-making. For heuristic purposes, a simplified model of the policy process is presented so that basic analytical methods for each step in the process can be explored. The course highlights quick, yet theoretically defensible, methods useful for smaller-scale analyses or for taking a preliminary approach to larger-scale analyses. The instructor will present case materials from environmental planning to illustrate the analytical methods.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN453','Urban Stormwater Management','Urban stormwater runoff affects the water quality, water quantity, habitat and biological resources, public health and aesthetic appearance of urban waterways. This course reviews the physical and chemical processes, environmental assessment techniques and best management practices related to stormwater management in the urban systems.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN471','Planning Law','An analysis of the legal basis for planning in Ontario and the practice of planning law as it affects planners, municipalities, local councils, property owners and residents. The roles of planning boards, municipal councils, the Ontario Municipal Board, the Ministry of Housing, provincial Cabinet and the Niagara Escarpment Commission in the planning process will be discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN474','Special Topics in Planning','These courses allow for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN477','Freight Planning and Policy','This course critically explores the evolution of freight planning and policy in the urban and regional realms from the early importance of ports to modern global logistics. Emphasis is placed on understanding 1) the theory, fundamental characteristics, and methods of analyzing freight systems, and 2) planning/policy perspectives on freight transportation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN478','Transit Planning and Operations','The historical evolution of transit in cities; the technological innovations which made transit possible; and transit mode definitions. Models of transit vehicle motion are presented; transit travel times under different travel regimes are derived. Transit scheduling methods are shown. System operational characteristics are defined and quantitative measures of effectiveness are introduced. Transit network planning objectives are identified; actual geometries are qualitatively and quantitatively analyzed. Transit ownership structures and economics are discussed; contemporary ITS applications are presented. Methods for selecting appropriate transit modes are covered. [Offered: W]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN480','Theory and Practice of Planning in the U.K.','Familiarization with the contribution of U.K. theory and practice to Canadian planning. Study of development of U.K. planning from mid-eighteenth century to present with reference to new town and urban redevelopment. For additional information on duration, itinerary and travel costs, contact the Planning UG Officer.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN481','Geographic Information Systems Project','The development, implementation, and presentation of a response to a set of GIS related project requirements is the focus of this course. Students work in small teams to enhance and develop their abilities to work with GIS and related spatial technologies and analytical methods in an advanced project setting. The nature of the project requirements and themes varies with faculty and student strengths and interests. Projects may emphasize development of software applications, use of programming, or advanced GIS analysis methods, and draw from theme areas such as environment studies and management, human and physical geography, or planning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN483','Land Development Planning','An examination of planning issues related to the design, economics and financing of private land and building construction projects including residential high-rise condominium, low-rise residential subdivision, infill, intensification and brownfield redevelopment and industrial/commercial land development. The course focuses on developer decision-making, analysis of risk, sources of financing, planning, environmental and engineering aspects of land development.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN484','Physical Infrastructure and Planning','The influence of transportation, communications, and water and sewage systems on the form, function and development of cities and regions; the application of this knowledge in urban and regional planning.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN485','Projects, Problems, and Readings in Planning','Special planning projects and problems chosen in consultation with instructor.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN487','Management Issues in Geographic Information Systems','Built around a set of key issues in the management of Geographic Information Systems. Focuses on middle management concerns and covers topics including GIS needs assessment, benchmarking, the law and spatial data, spatial data warehousing, multi-user GIS modelling and GIS application development. Uses of GIS in both public and private sector organizations are covered. [""""Formerly: GEOG/PLAN 555""""]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','PLAN490','Senior Honours Essay','Practical experience in carrying out a research proposal under the direction of a faculty member. The results of this research will be presented in the form of an essay that meets both professional and academic standards.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS102','Introduction to Environment and Business','Introduction of ways in which business has and is responding to environmental and business issues; business and sustainable development; issues of corporate/business greening.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS202','Environmental Management Systems','The examination and evaluation of Environmental Management Systems such as ISO 14001. Alternate EMS systems will be compared and reviewed to identify their respective strengths and weaknesses. Case studies will be used to illustrate the ideas presented.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS203','Green Entrepreneurship','Introduction to entrepreneurship in the economy, with a focus on the environmental industry and green technologies. Addresses competition, innovation and investment; historical experience, theoretical framework, market dynamics, public policy and practical applications.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS204','Principles of Industrial Ecology','The course presents the history of industrial ecology, defines its key concepts, presents its main methods, and discusses future directions. Local, national and international case studies will be discussed.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS302','Strategies for Environment and Business','A review of the successes and failures of businesses which have attempted to adopt sustainability principles into their strategies and practices. Case studies will be reviewed to identify barriers to adopting environmental priorities and the processes which proved successful in overcoming these obstacles.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS306','Research Design','This course will outline the principles involved in producing written research. Students will examine different types of research design; identify a problem/issue on a contemporary topic within environment and business; produce an analytical review of this topic; develop a research proposal; identify and collect data; conduct preliminary data (or information) analysis on the topic chosen for the literature review; and reflect on the above to produce a research proposal and associated risk analysis that could form the basis for the fourth year project.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS307','Environmental Declarations','The course presents the development and history of environmental labeling, environmental declarations and related tools; covers the ISO standards for product eco-labeling and other schemes including different national and sectoral programs, considering declarations on products, packaging, services and organizations; and addresses best practices and legislation; information and consumer programs to address climate change actions (such as the carbon neutral concept and GHG footprinting approaches).');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS308','Advanced Environmental Auditing','This course covers approaches and applications of environmental auditing that build upon the contents of ENBUS 202 and ENBUS 204. The course will cover topics such as: comparison of ISO 9001/14001; comparison of environmental auditing to other types of auditing; the role of environmental auditing in Environmental Management Systems (EMS) and corporate governance; auditing of other assertions (e.g. product environmental claims and greenhouse gases); evaluation of specific case studies; and consideration of the different requirements from different countries both in the developed and developing world.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS309','Occupational Health and Safety','Students will focus on the recognition, assessment and control of occupational hazards, together with appropriate approaches to regulation, enforcement and policy development.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS310','Strategic Management for Sustainable Business','This course draws on strategy concepts and theories explored in ENBUS 302 by focusing on the integration of business strategy with sustainability strategy. The course presents the principles that are essential for the formulation and evaluation of strategy for business and explores frameworks for strategic analysis, synthesis and action as it relates to sustainable business.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS311','Green Marketing','This course explores the principles of green marketing; green consumerism; green marketing myopia; the incorporation of environmental considerations into successful products and services; the role of social marketing, government regulations and other incentives related to green marketing.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS312','Operationalizing Sustainable Development within Business','Discussion of company organization structures; factors that deter change together with means of overcoming these issues; and how the greening of industry has affected organizational structure in different business sectors. Includes review of business case studies where environmental management changes have resulted.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS402A','Environment and Business Project','The application of the principles learned in earlier courses will focus on a particular project. Applications may include group projects of sufficient scope to demonstrate mastery of problem-solving, integration and communication on a selected topic related to environment and business, or on selected environmental issues related to a specific business operation.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS402B','Environment and Business Project','A continuation of ENBUS 402A.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS407','Corporate Sustainability Reporting','The course focuses on the environmental reporting mechanisms required of businesses in North America and overseas. The course will also evaluate best practices with reporting performance in different business sectors.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS408','Best Practices in Regulations','Well-crafted environmental regulations, smart regulations, can not only protect the environment but also enhance business competitiveness. This course will discuss issues with regard to smart regulations from the viewpoint of various stakeholders: for example, governments, businesses, and customers.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS409','Special Topics in Environment and Business','This course will allow for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS410','Engaging Stakeholders','Business practices need to reflect responsibility not just to shareholders, customers and staff, but also towards the local community. This course discusses what this means for management, and the strategies and practices that are needed to express this responsibility successfully.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','ENBUS411','International Corporate Responsibility','The course will focus on corporate responsibility, including social and environmental obligations. The course will consider methods of creating a culture of integrity; examine and evaluate methods such as due-diligence processes, risk assessment, sustainability assessment to integrate responsibility into day to day business positions; and evaluate social and sustainability reports; evaluate methods to determine the link between responsibility and increased profitability and benchmark responsibility in different business sectors including non-profit organizations.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV10','International Development Seminar','INDEV 10 is a non-credit, no-fee seminar series on current topics in International Development. Each session may feature a guest speaker or a field placement information session. Except where otherwise noted INDEV 10 seminars are open to all INDEV students, faculty, staff, and alumni. Participation is not mandatory but encouraged.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV100','Introduction to International Development','Students obtain an overview of the multi-dimensional and transdisciplinary nature of development. Themes include theories and strategies of development; history of development; relevance of context (culture, economics, and politics); the impact of law and international regulatory bodies; environmental influences; urbanization; the factors of health, literacy and violence; development agencies. Reference is made to particular development problems in specific developing countries.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV101','Issues in International Development','This course examines key issues in international development. International Development involves the search for sustainable and equitable solutions to poverty primarily but not only in the Global South. The causes and consequences of poverty are many and varied: environmental degradation; insufficient food production; inequitable access to land and related resources; poor governance; civil war and other types of conflict. The impacts of poverty manifest differently depending on culture, technology, geo-political factors and globalization.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV200','The Political Economy of Development','This course develops students'' understanding of how the complex interplay of international economic and political economy factors influence development initiatives and outcomes. The relation of trade, aid, and international institutions (WTO, IMF, World Bank) on development activity is examined using case studies. Different economic views will be examined.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV202','Accounting for Development Organizations','Students will obtain a fundamental understanding of financial and management concepts, and an appreciation of the measurement issues and monetary impact of business decisions on communities, society and the environment. Topics include financial statement reporting and analysis, cost analysis, triple bottom-line reporting, corporate social responsibility including the Global Sullivan Principles, and green accounting.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV212','Problem-solving for Development','Students will obtain an understanding of the fundamental features of creative thinking, decision-making and problem-solving from both a descriptive and a normative point of view. Techniques for analyzing problem-solving and decision-making by individuals and groups with particular attention to the impact of cultural difference among individuals and within teams. Techniques for improving problem-solving and decision-making in a community development context will be considered. Effective negotiation in different cultural contexts will be studied.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV275','Special Topics in International Development','This course allows for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV300','Culture and Ethics','This course surveys a variety of competing ethical positions that have been, and continue to be, adopted by different cultures around the world. Can two competing ethical viewpoints both be right? If not, then what grounds should we adopt to decide between them, and under what circumstances? How can we negotiate the difficulties that such differences pose for our practical judgment and moral theorizing? Students will engage these questions by reading texts in philosophical ethics and meta-ethics, and by applying what they have learned in case studies of ethical differences among cultures.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV302','Development Agents','This course examines the nature, variety and inter-relationships of and among organizations, institutions, agencies and individuals involved in contemporary development activity. The many and various legal, political and financial factors that affect the organization and operation of development agents are studied with respect to implications for business model, management skills, governance oversight and public policy.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV303','Marketing and Communication for Development Agents','This course teaches the principles of marketing relevant to development organizations. Fundamentals include dealing with the media, raising funds, reporting to investors/funding agencies, and crisis management.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV304','Language Conversation for Development Field Work','This is a special intensive language course designed to develop a working verbal fluency in the language of the student''s field assignment.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV308','Introduction to Social Entrepreneurship','This course uses the case study method to examine the challenges of starting, funding and operating an early-stage social change venture in a developing country. The focus is on ventures that address urbanization and poverty. Students explore the complexities of managing and sustaining growth, the role of governing boards, and the role of private sector partnerships and resources. Innovative public/private partnerships are examined. The challenges and opportunities associated with engaging diverse partners with differing agendas are considered from the perspective of the entrepreneur, investor/donor, local community leaders and legal counsel.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV375','Special Topics in International Development','This course allows for additions to the program on a short-term basis, and for the development of future permanent courses.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV387','Global Cities in Global Development','More than half of the world''''''''''''''''s population resides in urban areas, with the bulk of this growth taking place in the mega-cities of the global South. Many cities in the world are now truly ''''''''''''''''global'''''''''''''''' in their reach and orientation: New York, Shanghai, Mexico City, Johannesburg. Thus, urbanization presents key developmental challenges that face the world''''''''''''''''s urban population, and highlights the search for sustainable solutions in select cities.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV388','Key Issues in Urban Development','This course focuses on key developmental issues facing Third World cities such as housing, water supply and sanitation, energy, food security, migration and demographic change, and so on. The course also explores the links between North and South as well as comparative local issues facing new immigrants to Canada.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV401','International Development Placement 1','Students are placed for one term in a community development project or organization that has successfully managed an innovative solution to a development problem. Students are required to document the process of the project or organization leading to the development activity, including the nature and extent of problem, idea for addressing the problem, the challenges faced, resources used, critical decisions taken, results achieved, etc. An outline of the final report is required.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV402','International Development Placement 2','This course has the same content as INDEV 401. INDEV 402 is normally taken immediately following INDEV 401 to form an eight month internship in the field. Students will complete the final report outlined in INDEV 401.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV403','Advanced Marketing and Communication for Development Agents','This course allows students to put into practice the principles of marketing relevant to development organizations. Students will engage in fund raising activities relevant to their field placements.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','INDEV474','Special Topics in International Development','This course consists of directed readings leading to a research essay that focuses on an agreed aspect of the country of the student''''s field work placement. This course enhances the effectiveness of the field work and the field based courses by developing the student''''s knowledge of the development context and issues of the field placement.');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('ENV','Environment','SOCIN63','Team Process and Research Skills: Communication, Facilitation, Collaboration, Problem Solving','This is a multi-modal, skill-based course designed to help students act effectively in complex social systems and institutions. Students will learn and practice strategies related to the management of diversity, conflict negotiation, and decision-makiing. Self-diagnostic exercises and structured reflection assist students in identifying and understanding their own preferences and the roles they might most naturally and successfully take in group processes.');
