INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('REN','Renison','SDS499A','Senior Honours Essay/Thesis','Under the supervision of a faculty member, students focus on an area of Social Development Studies and either complete an essay involving in-depth literature research and analysis or complete a scholarly report on their own study involving literature review, research design, data collection, and analysis.');
