INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('VPA','Interdisciplinary','SE212','Logic and Computation','Formal logic. Proof systems and styles. Rudimentary model theory. Formal models of computation. Logic-based specification. Correctness proofs. Applications in software engineering. [Offered: F]');
INSERT INTO master(category_id, category_name, course_id, course_name, course_description) VALUES ('VPA','Interdisciplinary','SE240','Algorithms and Data Structures','Design and analysis of data structures and algorithms. Comparison of algorithms on data structures, including operations on sets (balanced search trees, hashing), sorting and priority queues, and graphs. Algorithmic paradigms including greedy algorithms, divide and conquer, dynamic programming, graph exploration, backtracking, and branch and bound. Mathematical analysis of space and time complexity. Overview of NP-completeness. [Offered: S]');
